import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import authService from "../../services/authService";
import Swal from "sweetalert2";

const AdminLogin = () => {
  const navigate = useNavigate();
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      /* 1. G·ªåI API LOGIN */
      const res = await authService.login({ email, password });
      const data = res.data;

      /* 2. L·∫§Y TOKEN */
      const token = data.token || data.jwt || data["jwt-token"];
      if (!token) throw new Error("Kh√¥ng nh·∫≠n ƒë∆∞·ª£c token t·ª´ Server");

      /* 3. GI·∫¢I M√É V√Ä KI·ªÇM TRA QUY·ªÄN ADMIN */
      try {
        const base64Url = token.split('.')[1];
        const base64 = base64Url.replace(/-/g, '+').replace(/_/g, '/');
        const payload = JSON.parse(window.atob(base64));

        console.log("Payload Check:", payload);

        // Ki·ªÉm tra t·∫•t c·∫£ c√°c tr∆∞·ªùng c√≥ th·ªÉ ch·ª©a Role
        const rawRole = payload.roles || payload.authorities || payload.role || payload.sub;
        
        // Chuy·ªÉn v·ªÅ chu·ªói ƒë·ªÉ ki·ªÉm tra (ph√≤ng tr∆∞·ªùng h·ª£p backend tr·∫£ v·ªÅ Array)
        const roleStr = JSON.stringify(rawRole);

        if (!roleStr.includes("ADMIN")) {
          Swal.fire({
            icon: "error",
            title: "‚õî Truy c·∫≠p b·ªã t·ª´ ch·ªëi",
            text: "T√†i kho·∫£n n√†y kh√¥ng c√≥ quy·ªÅn Qu·∫£n tr·ªã vi√™n!",
          });
          setLoading(false);
          return;
        }
      } catch (decodeErr) {
        throw new Error("X√°c th·ª±c Token th·∫•t b·∫°i");
      }

      /* 4. L∆ØU LOCALSTORAGE (D√πng key ri√™ng ƒë·ªÉ kh√¥ng ƒë·ª•ng User) */
      localStorage.setItem("admin-token", token);
      localStorage.setItem("admin-role", "ADMIN");

      /* 5. TH√îNG B√ÅO & ƒêI·ªÄU H∆Ø·ªöNG */
      window.dispatchEvent(new Event("adminAuthUpdated")); // Event ri√™ng cho Admin

      Swal.fire({
        icon: "success",
        title: "ƒêƒÉng nh·∫≠p th√†nh c√¥ng",
        text: "H·ªá th·ªëng ƒë√£ s·∫µn s√†ng, th∆∞a s·∫øp! üëë",
        timer: 1500,
        showConfirmButton: false,
      });

      navigate("/admin"); // V√†o Dashboard Admin
    } catch (err) {
      Swal.fire({
        icon: "error",
        title: "L·ªói ƒëƒÉng nh·∫≠p",
        text: err.response?.data?.message || err.message || "Sai t√†i kho·∫£n ho·∫∑c m·∫≠t kh·∫©u",
      });
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ minHeight: "100vh", display: "flex", alignItems: "center", justifyContent: "center", backgroundColor: "#1a1a2e" }}>
      <form
        onSubmit={handleSubmit}
        style={{
          width: 350,
          padding: "40px 30px",
          borderRadius: 15,
          backgroundColor: "#fff",
          boxShadow: "0 10px 40px rgba(0,0,0,0.3)",
        }}
      >
        <div style={{ textAlign: "center", marginBottom: 30 }}>
          <h2 style={{ margin: 0, color: "#1a1a2e", fontWeight: "bold" }}>ADMIN</h2>
          <small style={{ color: "#888" }}>QU·∫¢N TR·ªä H·ªÜ TH·ªêNG</small>
        </div>

        <div style={{ marginBottom: 20 }}>
          <label style={{ display: "block", marginBottom: 5, fontSize: 13, fontWeight: "bold" }}>EMAIL</label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
            disabled={loading}
            style={{ width: "100%", padding: "12px", borderRadius: 8, border: "1px solid #ddd", boxSizing: "border-box" }}
          />
        </div>

        <div style={{ marginBottom: 30 }}>
          <label style={{ display: "block", marginBottom: 5, fontSize: 13, fontWeight: "bold" }}>M·∫¨T KH·∫®U</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
            disabled={loading}
            style={{ width: "100%", padding: "12px", borderRadius: 8, border: "1px solid #ddd", boxSizing: "border-box" }}
          />
        </div>

        <button
          type="submit"
          disabled={loading}
          style={{
            width: "100%",
            padding: "12px",
            borderRadius: 8,
            border: "none",
            backgroundColor: "#e94560",
            color: "#fff",
            fontWeight: "bold",
            fontSize: 16,
            cursor: "pointer",
            transition: "0.3s"
          }}
        >
          {loading ? "ƒêANG X√ÅC TH·ª∞C..." : "ƒêƒÇNG NH·∫¨P NGAY"}
        </button>
      </form>
    </div>
  );
};

export default AdminLogin;
