  getProductImage(fileName) {
    if (!fileName) return "https://cdn-icons-png.flaticon.com/512/679/679922.png";
    return `/public/products/image/${fileName}`;
  },

  // üîê Admin: T·∫°o s·∫£n ph·∫©m m·ªõi
  async createProduct(categoryId, data) {
    try {
      const { data: result } = await api.post(`/admin/categories/${categoryId}/products`, data);
      return result;
    } catch (error) {
      console.error("‚ùå L·ªói khi t·∫°o s·∫£n ph·∫©m:", error);
      throw error;
    }
  },

  // üîê Admin: C·∫≠p nh·∫≠t s·∫£n ph·∫©m
