        email: form.email,
        password: form.password,
        address: {
          street: form.street,
          buildingName: form.buildingName,
          city: form.city,
          state: form.state,
          country: form.country,
          pincode: form.pincode,
        },
        // ƒê·∫£m b·∫£o c·∫•u tr√∫c Role gi·ªëng Backend mong ƒë·ª£i
        role: [
          {
            roleId: 102, 
            roleName: "USER",
          },
        ],
      };

      console.log("üöÄ Web Register Payload:", payload);

      const res = await authService.register(payload);

      console.log("‚úÖ Register Success:", res.data);
      alert("üéâ ƒêƒÉng k√Ω th√†nh c√¥ng! H√£y ƒëƒÉng nh·∫≠p ƒë·ªÉ ti·∫øp t·ª•c.");
      navigate("/login");

    } catch (error) {
      console.error("‚ùå Register Error:", error.response?.data || error.message);

      // Hi·ªÉn th·ªã l·ªói chi ti·∫øt t·ª´ backend n·∫øu c√≥
