              <label className="form-label small fw-bold">S·ªë ƒëi·ªán tho·∫°i</label>
              <input
                name="mobile_number"
                type="text"
                className="form-control"
                placeholder="Nh·∫≠p s·ªë ƒëi·ªán tho·∫°i"
                value={form.mobile_number}
                onChange={handleChange}
