import React, { useState, useEffect, useCallback } from "react";
import categoryService from "../../../services/categoryService";
import api from "../../../services/api";
import Swal from "sweetalert2";
import { useNavigate } from "react-router-dom";

const CategoryListAD = () => {
  const [categories, setCategories] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showModal, setShowModal] = useState(false);
  const [isEdit, setIsEdit] = useState(false);
  const [formData, setFormData] = useState({ categoryId: "", categoryName: "" });
  const navigate = useNavigate();

  // Qu·∫£n l√Ω ph√¢n trang (Gi·ªëng h·ªát ProductList)
  const [pageConfig, setPageConfig] = useState({ 
    pageNumber: 0, 
    pageSize: 10, 
    totalPages: 0,
    totalElements: 0
  });

  // ================= FETCH DATA (Check Role & Content) =================
  const fetchCategories = useCallback(async (page = 0) => {
    try {
      setLoading(true);
      
      // Ki·ªÉm tra quy·ªÅn ngay trong h√†m fetch
      const role = localStorage.getItem("role");
      if (role !== "ADMIN") {
        console.warn("üö´ [AUTH] Role kh√¥ng ph·∫£i ADMIN:", role);
        return; // D·ª´ng l·∫°i n·∫øu kh√¥ng ph·∫£i admin
      }

      // G·ªçi service (ƒë·∫£m b·∫£o service tr·∫£ v·ªÅ CategoryResponse)
      const res = await categoryService.getAllCategories(page, pageConfig.pageSize);
      
      console.log("üì• [DATA] Category Response:", res);

      // B√≥c t√°ch content (Gi·ªëng logic product)
      setCategories(res.content || []);
      setPageConfig(prev => ({ 
        ...prev,
        pageNumber: res.pageNumber || 0, 
        totalPages: res.totalPages || 0,
        totalElements: res.totalElements || 0
      }));
    } catch (err) {
      console.error("‚ùå L·ªói Fetch Category:", err.response || err);
    } finally {
      setLoading(false);
    }
  }, [pageConfig.pageSize]);

  useEffect(() => { 
  const token = localStorage.getItem("token") || localStorage.getItem("admin-token");
  const role = localStorage.getItem("role") || ""; // Tr√°nh null l·ªói string

  console.log("üõ°Ô∏è Check quy·ªÅn truy c·∫≠p:", { token: !!token, role });

  // Ki·ªÉm tra: C√≥ token V√Ä role c√≥ ch·ªØ ADMIN (kh√¥ng ph√¢n bi·ªát hoa th∆∞·ªùng)
  if (token && role.toUpperCase().includes("ADMIN")) {
      fetchCategories(); 
  } else {
      console.error("üö® S√∫t v·ªÅ login v√¨ thi·∫øu quy·ªÅn!");
      Swal.fire("L·ªói", "M√†y kh√¥ng c√≥ quy·ªÅn ADMIN!", "error");
      navigate("/admin/login");
  }
}, [fetchCategories, navigate]);

  // ================= LOGIC MODAL =================
  const handleOpenModal = (cat = null) => {
    if (cat) {
      setIsEdit(true);
      setFormData({ categoryId: cat.categoryId, categoryName: cat.categoryName });
    } else {
      setIsEdit(false);
      setFormData({ categoryId: "", categoryName: "" });
    }
    setShowModal(true);
  };

  // ================= SUBMIT (D√πng Token Admin) =================
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (formData.categoryName.trim().length < 5) {
        return Swal.fire("L·ªói", "T√™n danh m·ª•c √≠t nh·∫•t 5 k√Ω t·ª±", "warning");
    }

    const token = localStorage.getItem("token");
    const config = { headers: { Authorization: `Bearer ${token}` } };

    try {
      const payload = { categoryName: formData.categoryName };

      if (isEdit) {
        // Kh·ªõp endpoint @PutMapping({"/admin/categories/{categoryId}"})
        await api.put(`/admin/categories/${formData.categoryId}`, payload, config);
        Swal.fire("Th√†nh c√¥ng!", "ƒê√£ c·∫≠p nh·∫≠t danh m·ª•c.", "success");
      } else {
        // Kh·ªõp endpoint @PostMapping({"/admin/categories"})
        await api.post(`/admin/categories`, payload, config);
        Swal.fire("Th√†nh c√¥ng!", "ƒê√£ th√™m danh m·ª•c m·ªõi.", "success");
      }

      setShowModal(false);
      fetchCategories(pageConfig.pageNumber); 
    } catch (err) {
      console.error("‚ùå L·ªói Submit:", err.response?.data);
      Swal.fire("L·ªói", err.response?.data?.message || "Ki·ªÉm tra quy·ªÅn ADMIN/Token", "error");
    }
  };

  const handleDelete = async (id) => {
    const confirm = await Swal.fire({
      title: "X√≥a danh m·ª•c?",
      text: "H√†nh ƒë·ªông n√†y c√≥ th·ªÉ g√¢y l·ªói n·∫øu c√≥ s·∫£n ph·∫©m ƒëang thu·ªôc danh m·ª•c n√†y!",
      icon: "warning",
      showCancelButton: true,
      confirmButtonText: "X√≥a ngay"
    });

    if (confirm.isConfirmed) {
      const token = localStorage.getItem("token");
      try {
        await api.delete(`/admin/categories/${id}`, {
            headers: { Authorization: `Bearer ${token}` }
        });
        Swal.fire("ƒê√£ x√≥a!", "", "success");
        fetchCategories(0);
      } catch (err) {
        Swal.fire("L·ªói", "Kh√¥ng th·ªÉ x√≥a danh m·ª•c n√†y.", "error");
      }
    }
  };

  return (
    <div className="p-1">
      <div className="card shadow-sm border-0">
        <div className="card-header bg-white d-flex justify-content-between align-items-center py-3">
          <h5 className="m-0 fw-bold text-dark"><i className="bi bi-tags me-2"></i>QU·∫¢N L√ù DANH M·ª§C</h5>
          <button className="btn btn-primary btn-sm px-3" onClick={() => handleOpenModal()}>+ Th√™m m·ªõi</button>
        </div>

        <div className="table-responsive">
          <table className="table table-hover align-middle mb-0">
            <thead className="table-light">
              <tr>
                <th>ID</th>
                <th>T√™n danh m·ª•c</th>
                <th className="text-center">Thao t√°c</th>
              </tr>
            </thead>
            <tbody>
              {loading ? (
                <tr><td colSpan="3" className="text-center py-4">ƒêang t·∫£i d·ªØ li·ªáu...</td></tr>
              ) : categories.length > 0 ? (
                categories.map(c => (
                  <tr key={c.categoryId}>
                    <td className="text-muted">#{c.categoryId}</td>
                    <td className="fw-semibold">{c.categoryName}</td>
                    <td className="text-center">
                      <button className="btn btn-sm btn-outline-info me-2 shadow-sm" onClick={() => handleOpenModal(c)}>S·ª≠a</button>
                      <button className="btn btn-sm btn-outline-danger shadow-sm" onClick={() => handleDelete(c.categoryId)}>X√≥a</button>
                    </td>
                  </tr>
                ))
              ) : (
                <tr><td colSpan="3" className="text-center py-4 text-danger">‚ö†Ô∏è Kh√¥ng c√≥ d·ªØ li·ªáu. Check Console!</td></tr>
              )}
            </tbody>
          </table>
        </div>

        {/* PH√ÇN TRANG */}
        <div className="card-footer bg-white d-flex justify-content-between align-items-center py-3">
          <p className="text-muted small mb-0">T·ªïng: {pageConfig.totalElements} danh m·ª•c</p>
          <nav>
            <ul className="pagination pagination-sm mb-0">
              <li className={`page-item ${pageConfig.pageNumber === 0 ? 'disabled' : ''}`}>
                <button className="page-link" onClick={() => fetchCategories(pageConfig.pageNumber - 1)}>Tr∆∞·ªõc</button>
              </li>
              {[...Array(pageConfig.totalPages)].map((_, i) => (
                <li key={i} className={`page-item ${pageConfig.pageNumber === i ? 'active' : ''}`}>
                  <button className="page-link" onClick={() => fetchCategories(i)}>{i + 1}</button>
                </li>
              ))}
              <li className={`page-item ${pageConfig.pageNumber >= pageConfig.totalPages - 1 ? 'disabled' : ''}`}>
                <button className="page-link" onClick={() => fetchCategories(pageConfig.pageNumber + 1)}>Sau</button>
              </li>
            </ul>
          </nav>
        </div>
      </div>

      {/* MODAL */}
      {showModal && (
        <div className="modal d-block" style={{backgroundColor: "rgba(0,0,0,0.5)"}}>
          <div className="modal-dialog modal-dialog-centered">
            <div className="modal-content border-0">
              <form onSubmit={handleSubmit}>
                <div className="modal-header border-0 pb-0">
                  <h6 className="fw-bold m-0">{isEdit ? "C·∫¨P NH·∫¨T DANH M·ª§C" : "TH√äM DANH M·ª§C M·ªöI"}</h6>
                  <button type="button" className="btn-close" onClick={() => setShowModal(false)}></button>
                </div>
                <div className="modal-body py-4">
                  <label className="form-label small fw-bold text-muted text-uppercase">T√™n danh m·ª•c</label>
                  <input 
                    type="text" className="form-control" required 
                    value={formData.categoryName} 
                    onChange={e => setFormData({...formData, categoryName: e.target.value})}
                  />
                </div>
                <div className="modal-footer border-0">
                  <button type="submit" className="btn btn-primary btn-sm w-100 py-2 fw-bold">X√ÅC NH·∫¨N</button>
                </div>
              </form>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default CategoryListAD;
