import React, { useEffect, useState } from "react";
import { Link, useNavigate, useLocation } from "react-router-dom";
import userService from "../services/userService";
import categoryService from "../services/categoryService";
import bannerService from "../services/bannerService";

// âœ… Import CSS, Font, Bootstrap
import "bootstrap/dist/css/bootstrap.min.css";
import "bootstrap/dist/js/bootstrap.bundle.min.js";
import "../assets/fonts/fontawesome/css/all.min.css";

// âœ… Import hÃ¬nh áº£nh
import flagVN from "../assets/images/icons/flags/VN.webp";

const IMAGE_BASE_URL = "http://localhost:8080/api/public/banners/image";

const Header = () => {
  const [user, setUser] = useState(null);
  const [userInfo, setUserInfo] = useState(null);
  const [loading, setLoading] = useState(false);
  const [categories, setCategories] = useState([]);
  const [loadingCategories, setLoadingCategories] = useState(false);
  const [logo, setLogo] = useState(null);
  const navigate = useNavigate();
  const location = useLocation(); // âœ… ThÃªm useLocation Ä‘á»ƒ theo dÃµi trang hiá»‡n táº¡i

  // âœ… HÃ m kiá»ƒm tra active menu
  const isActive = (path) => {
    return location.pathname === path;
  };

  // âœ… HÃ m kiá»ƒm tra active cho category (trang product vá»›i state)
  const isCategoryActive = () => {
    return location.pathname === '/product' && location.state?.selectedCategory;
  };

  // âœ… Láº¥y user tá»« localStorage
  useEffect(() => {
    const storedUser = localStorage.getItem("user");
    if (storedUser) {
      try {
        const parsedUser = JSON.parse(storedUser);
        setUser(parsedUser);

        if (parsedUser.userId && typeof parsedUser.userId === 'number') {
          setLoading(true);

          userService
            .getUserById(parsedUser.userId)
            .then((userData) => {
              console.log("âœ… User data received:", userData);
              setUserInfo(userData);

              const updatedUser = {
                ...parsedUser,
                first_name: userData.first_name || userData.firstName,
                last_name: userData.last_name || userData.lastName,
                email: userData.email,
                mobile_number: userData.mobile_number
              };
              localStorage.setItem("user", JSON.stringify(updatedUser));
            })
            .catch((err) => {
              console.error("âŒ Lá»—i láº¥y thÃ´ng tin user:", err);
              if (parsedUser.first_name || parsedUser.last_name) {
                setUserInfo({
                  first_name: parsedUser.first_name,
                  last_name: parsedUser.last_name
                });
              }
            })
            .finally(() => {
              setLoading(false);
            });
        } else {
          if (parsedUser.first_name || parsedUser.last_name) {
            setUserInfo({
              first_name: parsedUser.first_name,
              last_name: parsedUser.last_name
            });
          }
        }
      } catch (error) {
        console.error("âŒ Parse user lá»—i:", error);
        setLoading(false);
      }
    }
  }, []);

  // âœ… Láº¥y danh sÃ¡ch categories tá»« API
  useEffect(() => {
    fetchCategories();
  }, []);

  // âœ… Láº¥y logo tá»« bannerService (id 4)
  useEffect(() => {
    fetchLogo();
  }, []);

  const fetchCategories = async () => {
    try {
      setLoadingCategories(true);
      const categoriesData = await categoryService.getAllCategories();
      setCategories(categoriesData || []);
    } catch (error) {
      console.error('âŒ Lá»—i khi láº¥y danh má»¥c:', error);
      setCategories([]);
    } finally {
      setLoadingCategories(false);
    }
  };

  // âœ… HÃ m láº¥y logo tá»« bannerService
  const fetchLogo = async () => {
    try {
      const banners = await bannerService.getAllBanners();
      const logoBanner = banners.find(banner => 
        banner.bannerId === 4 || banner.id === 4
      );

      if (logoBanner) {
        setLogo(logoBanner);
      } else if (banners.length > 0) {
        setLogo(banners[0]);
      }
    } catch (error) {
      console.error('âŒ Lá»—i khi láº¥y logo:', error);
    }
  };

  // âœ… HÃ m láº¥y URL áº£nh logo
  const getLogoUrl = () => {
    if (!logo) return "https://via.placeholder.com/120x120/667eea/ffffff?text=LOGO";

    const imageName = logo.imageUrl || logo.image_url;

    if (!imageName) {
      return "https://via.placeholder.com/120x120/667eea/ffffff?text=LOGO";
    }

    if (imageName.startsWith('http')) {
      return imageName;
    }

    return `${IMAGE_BASE_URL}/${imageName}`;
  };

  // âœ… Äiá»u hÆ°á»›ng Ä‘áº¿n trang sáº£n pháº©m theo category
  const handleCategoryClick = (category) => {
    const categoryId = category.categoryId || category.id;
    const categoryName = category.categoryName || category.name || category.title || 'Danh má»¥c';

    if (categoryId) {
      navigate(`/product`, { 
        state: { 
          selectedCategory: categoryId,
          categoryName: categoryName
        }
      });
    }
  };

  // âœ… ÄÄƒng xuáº¥t
  const handleLogout = () => {
    localStorage.removeItem("user");
    localStorage.removeItem("token");
    setUser(null);
    setUserInfo(null);
    navigate("/login");
  };

  // âœ… Chuyá»ƒn Ä‘áº¿n trang chá»‰nh sá»­a profile
  const handleProfileClick = () => {
    if (user) {
      navigate("/profile");
    } else {
      navigate("/login");
    }
  };

  // âœ… HÃ m hiá»ƒn thá»‹ tÃªn ngÆ°á»i dÃ¹ng
  const renderUserName = () => {
    if (!user) return "My profile";

    if (loading) {
      return "Loading...";
    }

    if (userInfo) {
      const firstName = userInfo.first_name || "";
      const lastName = userInfo.last_name || "";
      const fullName = `${firstName} ${lastName}`.trim();
      if (fullName) return fullName;
    }

    if (user.email) return user.email;

    return "User";
  };

  // âœ… HÃ m hiá»ƒn thá»‹ lá»i chÃ o
  const renderGreeting = () => {
    if (!user) return null;

    if (userInfo && (userInfo.first_name || userInfo.last_name)) {
      const firstName = userInfo.first_name || "";
      return `ðŸ‘‹ Hi, ${firstName}`;
    }

    if (user.email) {
      return `ðŸ‘‹ Hi, ${user.email.split('@')[0]}`;
    }

    return "ðŸ‘‹ Hi, User";
  };

  // âœ… HÃ m hiá»ƒn thá»‹ tÃªn category
  const getCategoryName = (category) => {
    return category.categoryName || category.name || category.title || 'Danh má»¥c';
  };

  // âœ… HÃ m get menu style vá»›i active state
  const getMenuStyle = (isActive) => {
    const baseStyle = {
      padding: '0.5rem 1.5rem',
      borderRadius: '25px',
      transition: 'all 0.3s ease',
      fontWeight: '600'
    };

    if (isActive) {
      return {
        ...baseStyle,
        backgroundColor: '#667eea',
        color: 'white',
        transform: 'scale(1.05)'
      };
    }

    return {
      ...baseStyle,
      backgroundColor: 'transparent',
      color: '#212529'
    };
  };

  // âœ… HÃ m get dropdown style vá»›i active state
  const getDropdownStyle = (isActive) => {
    const baseStyle = {
      padding: '0.5rem 1.5rem',
      borderRadius: '25px',
      transition: 'all 0.3s ease',
      fontWeight: '600',
      cursor: 'pointer'
    };

    if (isActive) {
      return {
        ...baseStyle,
        backgroundColor: '#667eea',
        color: 'white',
        transform: 'scale(1.05)'
      };
    }

    return {
      ...baseStyle,
      backgroundColor: 'transparent',
      color: '#212529'
    };
  };

  return (
    <header className="section-header">
      {/* ======= THANH TRÃŠN ======= */}
      <nav className="navbar d-none d-md-flex p-md-0 navbar-expand-sm navbar-light border-bottom">
        <div className="container">
          <div className="collapse navbar-collapse" id="navbarTop4">
            <ul className="navbar-nav mr-auto">
              <li className="nav-item">
                {user ? (
                  <span className="nav-link">
                    {renderGreeting()} |{" "}
                    <button
                      onClick={handleLogout}
                      className="btn btn-link p-0 text-danger"
                      style={{ textDecoration: "none" }}
                    >
                      Logout
                    </button>
                  </span>
                ) : (
                  <span className="nav-link">
                    Hi, <Link to="/login">Log In</Link> or{" "}
                    <Link to="/register">Register</Link>
                  </span>
                )}
              </li>
              <li className="nav-item">
                <a href="#" className="nav-link">
                  Deals
                </a>
              </li>
              <li className="nav-item">
                <a href="#" className="nav-link">
                  Sell
                </a>
              </li>
              <li className="nav-item">
                <a href="#" className="nav-link">
                  Help
                </a>
              </li>
            </ul>

            <ul className="navbar-nav">
              <li className="nav-item">
                <a href="#" className="nav-link">
                  <img src={flagVN} height="24" alt="flag" /> Ship to
                </a>
              </li>
              <li className="nav-item dropdown">
                <a
                  href="#"
                  className="nav-link dropdown-toggle"
                  data-bs-toggle="dropdown" 
                  aria-expanded="false"
                >
                  Watchlist
                </a>
                <ul className="dropdown-menu">
                  <li>
                    <a className="dropdown-item" href="#">
                      First item
                    </a>
                  </li>
                  <li>
                    <a className="dropdown-item" href="#">
                      Second item
                    </a>
                  </li>
                  <li>
                    <a className="dropdown-item" href="#">
                      Third item
                    </a>
                  </li>
                </ul>
              </li>
              <li className="nav-item">
                <a href="#" className="nav-link">
                  My shop
                </a>
              </li>
              <li className="nav-item">
                <a href="#" className="nav-link">
                  <i className="fa fa-bell"></i>
                </a>
              </li>
              <li className="nav-item">
                <a href="#" className="nav-link">
                  <i className="fa fa-shopping-cart"></i>
                </a>
              </li>
            </ul>
          </div>
        </div>
      </nav>

      {/* ======= THANH CHÃNH - LOGO + MENU CÃ™NG HÃ€NG ======= */}
      <div className="container">
        <section className="header-main border-bottom py-3">
          <div className="d-flex align-items-center justify-content-between">
            {/* âœ… Logo to bÃªn trÃ¡i */}
            <div className="flex-shrink-0">
              <Link to="/" className="brand-wrap text-decoration-none">
                <div className="d-flex align-items-center">
                  <div 
                    className="rounded-circle overflow-hidden border shadow-lg"
                    style={{
                      width: '100px',
                      height: '100px',
                      display: 'flex',
                      alignItems: 'center',
                      justifyContent: 'center',
                      backgroundColor: '#f8f9fa',
                      border: '3px solid #667eea'
                    }}
                  >
                    <img
                      src={getLogoUrl()}
                      alt="Logo"
                      className="img-fluid"
                      style={{
                        width: '100%',
                        height: '100%',
                        objectFit: 'cover'
                      }}
                      onError={(e) => {
                        e.target.src = "https://via.placeholder.com/100x100/667eea/ffffff?text=LOGO";
                      }}
                    />
                  </div>
                  <div className="ms-3">
                    <h3 className="fw-bold text-primary mb-1">SHOPMATE</h3>
                    <small className="text-muted fw-semibold">Premium Shopping</small>
                  </div>
                </div>
              </Link>
            </div>

            {/* âœ… Menu chÃ­nh - Vá»šI ACTIVE STATE */}
            <div className="flex-grow-1 mx-4">
              <nav className="navbar navbar-main p-0">
                <ul className="navbar-nav flex-row justify-content-center w-100">
                  {/* Home Menu - ACTIVE STATE */}
                  <li className="nav-item mx-3">
                    <Link 
                      to="/" 
                      className="nav-link fs-5 position-relative text-decoration-none"
                      style={getMenuStyle(isActive('/'))}
                      onMouseEnter={(e) => {
                        if (!isActive('/')) {
                          e.target.style.backgroundColor = '#FFD8A8';
                          e.target.style.color = 'white';
                          e.target.style.transform = 'scale(1.05)';
                        }
                      }}
                      onMouseLeave={(e) => {
                        if (!isActive('/')) {
                          e.target.style.backgroundColor = 'transparent';
                          e.target.style.color = '#212529';
                          e.target.style.transform = 'scale(1)';
                        }
                      }}
                    >
                      Home
                    </Link>
                  </li>

                  {/* Products Menu - ACTIVE STATE */}
                  <li className="nav-item mx-3">
                    <Link 
                      to="/product" 
                      className="nav-link fs-5 position-relative text-decoration-none"
                      style={getMenuStyle(isActive('/product') && !isCategoryActive())}
                      onMouseEnter={(e) => {
                        if (!isActive('/product') || isCategoryActive()) {
                          e.target.style.backgroundColor = '#667eea';
                          e.target.style.color = 'white';
                          e.target.style.transform = 'scale(1.05)';
                        }
                      }}
                      onMouseLeave={(e) => {
                        if (!isActive('/product') || isCategoryActive()) {
                          e.target.style.backgroundColor = 'transparent';
                          e.target.style.color = '#212529';
                          e.target.style.transform = 'scale(1)';
                        }
                      }}
                    >
                      Products
                    </Link>
                  </li>

                  {/* âœ… Categories Dropdown - ACTIVE STATE */}
                  <li className="nav-item dropdown mx-3">
                    <a
                      className="nav-link dropdown-toggle fs-5 position-relative text-decoration-none"
                      href="#"
                      id="categoriesDropdown"
                      role="button"
                      data-bs-toggle="dropdown"
                      aria-expanded="false"
                      style={getDropdownStyle(isCategoryActive())}
                      onMouseEnter={(e) => {
                        if (!isCategoryActive()) {
                          e.target.style.backgroundColor = '#667eea';
                          e.target.style.color = 'white';
                          e.target.style.transform = 'scale(1.05)';
                        }
                      }}
                      onMouseLeave={(e) => {
                        if (!isCategoryActive()) {
                          e.target.style.backgroundColor = 'transparent';
                          e.target.style.color = '#212529';
                          e.target.style.transform = 'scale(1)';
                        }
                      }}
                    >
                      Categories
                      {loadingCategories && (
                        <span className="ms-1">
                          <div className="spinner-border spinner-border-sm" role="status">
                            <span className="visually-hidden">Loading...</span>
                          </div>
                        </span>
                      )}
                    </a>

                    <ul 
                      className="dropdown-menu shadow-lg" 
                      aria-labelledby="categoriesDropdown"
                      style={{ 
                        maxHeight: '400px', 
                        overflowY: 'auto',
                        border: 'none',
                        borderRadius: '12px'
                      }}
                    >
                      {loadingCategories ? (
                        <li>
                          <div className="dropdown-item text-muted">
                            <div className="d-flex align-items-center">
                              <div className="spinner-border spinner-border-sm me-2" role="status">
                                <span className="visually-hidden">Loading...</span>
                              </div>
                              Loading categories...
                            </div>
                          </div>
                        </li>
                      ) : categories.length > 0 ? (
                        categories.map((category, index) => (
                          <li key={category.categoryId || category.id || index}>
