import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';

const GeminiChat = () => {
    const [isOpen, setIsOpen] = useState(false);
    const [messages, setMessages] = useState([
        { role: 'ai', text: 'Ch√†o n√†ng! Em l√† Ng·ªçc Anh Beauty AI, n√†ng c·∫ßn t∆∞ v·∫•n m·ªπ ph·∫©m g√¨ c·ª© b·∫£o em nh√©! ‚ù§Ô∏è' }
    ]);
    const [input, setInput] = useState('');
    const [loading, setLoading] = useState(false);
    const scrollRef = useRef(null);

    useEffect(() => {
        if (scrollRef.current) {
            scrollRef.current.scrollIntoView({ behavior: 'smooth' });
        }
    }, [messages, loading]);

    const handleSend = async () => {
        if (!input.trim() || loading) return;

        const userMsg = input.trim();
        setMessages(prev => [...prev, { role: 'user', text: userMsg }]);
        setInput('');
        setLoading(true);

        try {
            const res = await axios.post('http://localhost:8080/api/ai/ask', {
                message: userMsg
            });

            const { reply, suggested_products } = res.data;

            setMessages(prev => [...prev, { 
                role: 'ai', 
                text: reply || 'Em nghe ƒë√¢y, n√†ng n√≥i ti·∫øp ƒëi...',
                suggestions: suggested_products || [] 
            }]);

        } catch (error) {
            setMessages(prev => [...prev, { role: 'ai', text: 'Hic, k·∫øt n·ªëi b·∫≠n r·ªìi, n√†ng th·ª≠ l·∫°i nh√©! üòÖ' }]);
        } finally {
            setLoading(false);
        }
    };

    return (
        <div style={{ position: 'fixed', bottom: '30px', right: '30px', zIndex: 2000 }}>
            {/* N√∫t b·∫•m tr√≤n x·ªãn x√≤ */}
            <button 
                onClick={() => setIsOpen(!isOpen)} 
                className="btn btn-warning rounded-circle shadow-lg border-0 d-flex align-items-center justify-content-center floating-btn" 
                style={{ width: '65px', height: '65px', transition: 'all 0.3s' }}
            >
                <i className={`bi ${isOpen ? 'bi-x-lg' : 'bi-stars'} fs-2 text-white`}></i>
            </button>

            {/* C·ª≠a s·ªï Chat */}
            {isOpen && (
                <div className="card shadow-2xl border-0 rounded-4 animate-chat" 
                     style={{ width: '400px', height: '600px', display: 'flex', flexDirection: 'column', 
                              backgroundColor: 'rgba(255, 255, 255, 0.95)', backdropFilter: 'blur(10px)',
                              bottom: '80px', position: 'absolute', right: 0 }}>
                    
                    {/* Header: Gradient & Avatar */}
                    <div className="card-header border-0 py-3 px-3 shadow-sm" 
                         style={{ background: 'linear-gradient(135deg, #ffc107 0%, #ff9800 100%)', borderRadius: '15px 15px 0 0' }}>
                        <div className="d-flex align-items-center justify-content-between">
                            <div className="d-flex align-items-center text-white">
                                <div className="bg-white rounded-circle p-1 me-2 d-flex align-items-center justify-content-center shadow-sm" style={{width: '40px', height: '40px'}}>
                                    <i className="bi bi-robot text-warning fs-4"></i>
                                </div>
                                <div>
                                    <h6 className="mb-0 fw-bold">Ng·ªçc Anh AI Assistant</h6>
                                    <div className="d-flex align-items-center">
                                        <span className="online-dot me-1"></span>
                                        <small className="opacity-75">S·∫µn s√†ng t∆∞ v·∫•n</small>
                                    </div>
                                </div>
                            </div>
                            <button onClick={() => setIsOpen(false)} className="btn btn-link text-white p-0 shadow-none">
                                <i className="bi bi-dash-lg fs-4"></i>
                            </button>
                        </div>
                    </div>

                    {/* N·ªôi dung Chat */}
                    <div className="card-body p-3 overflow-auto chat-container" style={{ flex: 1 }}>
                        {messages.map((msg, idx) => (
                            <div key={idx} className={`d-flex flex-column mb-4 ${msg.role === 'user' ? 'align-items-end' : 'align-items-start'}`}>
                                <div className={`message-bubble px-3 py-2 ${msg.role === 'user' ? 'user-bubble' : 'ai-bubble shadow-sm'}`}>
                                    {msg.text}
                                </div>
                                
                                {/* Suggested Products: Carousel Design */}
                                {msg.suggestions && msg.suggestions.length > 0 && (
                                    <div className="w-100 mt-2 d-flex overflow-auto pb-2 gap-2 hide-scrollbar">
                                        {msg.suggestions.map((p, i) => (
                                            <div key={i} className="product-card-mini bg-white border shadow-sm rounded-4 flex-shrink-0">
                                                <img src={`http://localhost:8080/api/public/products/image/${p.image}`} 
                                                     alt={p.productName} className="product-img-mini"
                                                     onError={(e) => { e.target.src = 'https://via.placeholder.com/150?text=Beauty' }} />
                                                <div className="p-2">
                                                    <p className="product-name-mini mb-1">{p.productName}</p>
                                                    <div className="d-flex justify-content-between align-items-center">
                                                        <span className="price-mini">{(p.specialPrice || p.price).toLocaleString()}‚Ç´</span>
                                                        <a href={`/product/${p.productId}`} className="btn-go rounded-circle">
                                                            <i className="bi bi-chevron-right"></i>
                                                        </a>
                                                    </div>
                                                </div>
                                            </div>
                                        ))}
                                    </div>
                                )}
                            </div>
                        ))}

                        {loading && (
                            <div className="ai-bubble mb-3 d-flex align-items-center gap-2">
                                <span className="dot-flashing"></span>
                                <span className="dot-flashing"></span>
                                <span className="dot-flashing"></span>
                            </div>
                        )}
                        <div ref={scrollRef} />
                    </div>

                    {/* Footer: Input Cao C·∫•p */}
                    <div className="p-3 bg-white border-top">
                        <div className="input-group rounded-pill bg-light p-1 border overflow-hidden shadow-sm">
                            <input 
                                type="text" 
                                className="form-control border-0 bg-transparent shadow-none px-3" 
                                placeholder="N√†ng mu·ªën h·ªèi g√¨..." 
                                value={input} 
                                onChange={(e) => setInput(e.target.value)} 
                                onKeyDown={(e) => e.key === 'Enter' && handleSend()} 
                            />
                            <button onClick={handleSend} className="btn btn-warning rounded-circle d-flex align-items-center justify-content-center p-0 ms-1" style={{ width: '40px', height: '40px' }} disabled={loading}>
                                <i className="bi bi-send-fill text-white"></i>
                            </button>
                        </div>
                    </div>
                </div>
            )}
            
            <style>{`
                /* Hi·ªáu ·ª©ng Floating N√∫t Chat */
                .floating-btn:hover { transform: scale(1.1) rotate(5deg); box-shadow: 0 10px 20px rgba(255,193,7,0.4); }
                
                /* Animation C·ª≠a s·ªï Chat */
                .animate-chat { animation: slideIn 0.4s ease-out; }
                @keyframes slideIn { from { opacity: 0; transform: translateY(20px); } to { opacity: 1; transform: translateY(0); } }

                /* Bong b√≥ng chat */
                .message-bubble { max-width: 85%; font-size: 14px; line-height: 1.5; border-radius: 20px; position: relative; }
                .user-bubble { background: #ffc107; color: white; border-bottom-right-radius: 4px; }
                .ai-bubble { background: white; color: #333; border: 1px solid #eee; border-bottom-left-radius: 4px; }

                /* Ch·∫•m online */
                .online-dot { width: 8px; height: 8px; background: #2ecc71; border-radius: 50%; display: inline-block; box-shadow: 0 0 5px #2ecc71; }

                /* Card s·∫£n ph·∫©m mini */
                .product-card-mini { width: 150px; transition: transform 0.2s; cursor: pointer; }
                .product-card-mini:hover { transform: translateY(-3px); border-color: #ffc107 !important; }
                .product-img-mini { width: 100%; height: 110px; object-fit: cover; border-top-left-radius: 15px; border-top-right-radius: 15px; }
                .product-name-mini { font-size: 12px; font-weight: 600; color: #333; height: 32px; overflow: hidden; display: -webkit-box; -webkit-line-clamp: 2; -webkit-box-orient: vertical; }
                .price-mini { font-size: 13px; font-weight: 700; color: #e74c3c; }
