import React, { useState, useEffect, useCallback } from "react";
import axios from "axios";
import Swal from "sweetalert2";

const ProductListAD = () => {
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showModal, setShowModal] = useState(false);
  const [isEdit, setIsEdit] = useState(false);
  
  // State qu·∫£n l√Ω Form
  const [formData, setFormData] = useState({
    productId: "",
    productName: "",
    price: "",
    discount: 0,
    quantity: 1,
    description: "",
    categoryId: ""
  });
  const [selectedFile, setSelectedFile] = useState(null);
  const [pageConfig, setPageConfig] = useState({ pageNumber: 0, totalPages: 0 });

  // 1. H√†m l·∫•y d·ªØ li·ªáu (S·∫£n ph·∫©m & Danh m·ª•c)
  const fetchData = useCallback(async (page = 0) => {
    const token = localStorage.getItem("jwt-token"); // L·∫•y l·∫°i m·ªói l·∫ßn g·ªçi ƒë·ªÉ tr√°nh null
    
    if (!token) {
      console.error("L·ªói: Kh√¥ng t√¨m th·∫•y jwt-token trong LocalStorage!");
      setLoading(false);
      return;
    }

    try {
      const config = { headers: { Authorization: `Bearer ${token}` } };
      
      const [prodRes, catRes] = await Promise.all([
        axios.get(`http://localhost:8080/api/admin/products?pageNumber=${page}&pageSize=10`, config),
        axios.get("http://localhost:8080/api/public/categories", config)
      ]);

      setProducts(prodRes.data.content || []);
      setPageConfig({ 
        pageNumber: prodRes.data.pageNumber || 0, 
        totalPages: prodRes.data.totalPages || 0 
      });
      setCategories(catRes.data.content || catRes.data || []);
      setLoading(false);
    } catch (err) {
      console.error("L·ªói fetch data:", err.response?.status);
      if(err.response?.status === 403) {
        Swal.fire("L·ªói 403", "Token h·∫øt h·∫°n ho·∫∑c b·∫°n kh√¥ng c√≥ quy·ªÅn Admin!", "error");
      }
      setLoading(false);
    }
  }, []);

  useEffect(() => { fetchData(); }, [fetchData]);

  // 2. X·ª≠ l√Ω M·ªü Modal
  const handleOpenModal = (product = null) => {
    if (product) {
      setIsEdit(true);
      setFormData({
        ...product,
        categoryId: product.category?.categoryId || ""
      });
    } else {
      setIsEdit(false);
      setFormData({ productName: "", price: "", discount: 0, quantity: 1, description: "", categoryId: "" });
    }
    setSelectedFile(null);
    setShowModal(true);
  };

  // 3. X·ª≠ l√Ω Th√™m/S·ª≠a
  const handleSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem("jwt-token");
    const config = { headers: { Authorization: `Bearer ${token}` } };

    try {
      let savedProduct;
      if (isEdit) {
        // C·∫≠p nh·∫≠t th√¥ng tin ch·ªØ
        const res = await axios.put(`http://localhost:8080/api/admin/products/${formData.productId}`, formData, config);
        savedProduct = res.data;
      } else {
        // Th√™m m·ªõi
        const res = await axios.post(`http://localhost:8080/api/admin/categories/${formData.categoryId}/product`, formData, config);
        savedProduct = res.data;
      }

      // N·∫øu c√≥ ch·ªçn file ·∫£nh m·ªõi -> Upload ·∫£nh
      if (selectedFile) {
        const imageForm = new FormData();
        imageForm.append("image", selectedFile);
        await axios.put(`http://localhost:8080/api/admin/products/${savedProduct.productId}/image`, imageForm, {
          headers: { ...config.headers, "Content-Type": "multipart/form-data" }
        });
      }

      Swal.fire("Th√†nh c√¥ng!", "D·ªØ li·ªáu ƒë√£ ƒë∆∞·ª£c l∆∞u.", "success");
      setShowModal(false);
      fetchData(pageConfig.pageNumber);
    } catch (err) {
      Swal.fire("L·ªói!", "Kh√¥ng th·ªÉ l∆∞u s·∫£n ph·∫©m. Check console!", "error");
    }
  };

  // 4. X·ª≠ l√Ω X√≥a
  const handleDelete = async (id) => {
    const confirm = await Swal.fire({
      title: "X√≥a s·∫£n ph·∫©m n√†y?",
      text: "H√†nh ƒë·ªông n√†y kh√¥ng th·ªÉ ho√†n t√°c!",
      icon: "warning",
      showCancelButton: true,
      confirmButtonColor: "#d33",
      confirmButtonText: "X√≥a ngay!"
    });

    if (confirm.isConfirmed) {
      try {
        const token = localStorage.getItem("jwt-token");
        await axios.delete(`http://localhost:8080/api/admin/products/${id}`, {
          headers: { Authorization: `Bearer ${token}` }
        });
        Swal.fire("ƒê√£ x√≥a!", "S·∫£n ph·∫©m ƒë√£ bi·∫øn m·∫•t.", "success");
        fetchData();
      } catch (err) {
        Swal.fire("L·ªói!", "X√≥a th·∫•t b·∫°i (c√≥ th·ªÉ do li√™n k·∫øt kh√≥a ngo·∫°i).", "error");
      }
    }
  };

  if (loading) return <div className="text-center mt-5"><h3>üöÄ ƒêang t·∫£i d·ªØ li·ªáu...</h3></div>;

  return (
    <div className="container-fluid">
      <div className="card shadow border-0">
        <div className="card-header bg-white d-flex justify-content-between align-items-center py-3">
          <h5 className="m-0 fw-bold text-primary">üì¶ QU·∫¢N L√ù KHO M·ª∏ PH·∫®M</h5>
          <button className="btn btn-success shadow-sm" onClick={() => handleOpenModal()}>
            <i className="bi bi-plus-lg"></i> Th√™m S·∫£n Ph·∫©m
          </button>
        </div>

        <div className="table-responsive">
          <table className="table table-hover align-middle mb-0">
            <thead className="table-dark">
              <tr>
                <th>ID</th>
                <th>·∫¢nh</th>
                <th>T√™n s·∫£n ph·∫©m</th>
                <th>Gi√°</th>
                <th>S·ªë l∆∞·ª£ng</th>
                <th className="text-center">Thao t√°c</th>
              </tr>
            </thead>
            <tbody>
              {products.map(p => (
                <tr key={p.productId}>
                  <td>{p.productId}</td>
                  <td>
                    <img 
                      src={p.image ? `http://localhost:8080/api/public/products/image/${p.image}` : "https://via.placeholder.com/50"} 
                      style={{width: "50px", height: "50px", objectFit: "cover", borderRadius: "5px"}}
                      alt="img"
                      onError={(e) => e.target.src="https://via.placeholder.com/50"}
                    />
                  </td>
                  <td className="fw-bold">{p.productName}</td>
                  <td className="text-primary">{p.specialPrice?.toLocaleString()}‚Ç´</td>
                  <td>{p.quantity}</td>
                  <td className="text-center">
                    <button className="btn btn-sm btn-outline-info me-2" onClick={() => handleOpenModal(p)}>S·ª≠a</button>
                    <button className="btn btn-sm btn-outline-danger" onClick={() => handleDelete(p.productId)}>X√≥a</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>

      {/* --- MODAL FORM --- */}
      {showModal && (
        <div className="modal d-block" style={{backgroundColor: "rgba(0,0,0,0.5)"}}>
          <div className="modal-dialog modal-lg modal-dialog-centered">
            <div className="modal-content border-0 shadow">
              <form onSubmit={handleSubmit}>
                <div className="modal-header bg-primary text-white">
                  <h5 className="modal-title">{isEdit ? "C·∫¨P NH·∫¨T S·∫¢N PH·∫®M" : "TH√äM M·ª∏ PH·∫®M M·ªöI"}</h5>
                  <button type="button" className="btn-close btn-close-white" onClick={() => setShowModal(false)}></button>
                </div>
                <div className="modal-body row g-3">
                  <div className="col-md-8">
                    <label className="form-label fw-bold">T√™n s·∫£n ph·∫©m</label>
                    <input type="text" className="form-control" required value={formData.productName}
                           onChange={e => setFormData({...formData, productName: e.target.value})}/>
                  </div>
                  <div className="col-md-4">
                    <label className="form-label fw-bold">Danh m·ª•c</label>
                    <select className="form-select" required value={formData.categoryId}
                            onChange={e => setFormData({...formData, categoryId: e.target.value})}>
                      <option value="">-- Ch·ªçn --</option>
                      {categories.map(c => <option key={c.categoryId} value={c.categoryId}>{c.categoryName}</option>)}
                    </select>
                  </div>
                  <div className="col-md-4">
                    <label className="form-label fw-bold">Gi√° g·ªëc</label>
                    <input type="number" className="form-control" value={formData.price}
                           onChange={e => setFormData({...formData, price: e.target.value})}/>
                  </div>
                  <div className="col-md-4">
                    <label className="form-label fw-bold">Gi·∫£m gi√° (%)</label>
                    <input type="number" className="form-control" value={formData.discount}
                           onChange={e => setFormData({...formData, discount: e.target.value})}/>
                  </div>
                  <div className="col-md-4">
                    <label className="form-label fw-bold">S·ªë l∆∞·ª£ng</label>
                    <input type="number" className="form-control" value={formData.quantity}
                           onChange={e => setFormData({...formData, quantity: e.target.value})}/>
                  </div>
                  <div className="col-12">
                    <label className="form-label fw-bold">M√¥ t·∫£</label>
                    <textarea className="form-control" rows="2" value={formData.description}
                              onChange={e => setFormData({...formData, description: e.target.value})}></textarea>
                  </div>
                  <div className="col-12">
                    <label className="form-label fw-bold">·∫¢nh s·∫£n ph·∫©m</label>
                    <input type="file" className="form-control" onChange={e => setSelectedFile(e.target.files[0])}/>
                  </div>
                </div>
                <div className="modal-footer">
                  <button type="button" className="btn btn-light" onClick={() => setShowModal(false)}>ƒê√≥ng</button>
                  <button type="submit" className="btn btn-primary px-4">L∆∞u d·ªØ li·ªáu</button>
                </div>
              </form>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default ProductListAD;
