import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import authService from "../../services/authService";

const Register = () => {
  const navigate = useNavigate();
  const [form, setForm] = useState({
    first_name: "",
    last_name: "",
    email: "",
    mobile_number: "",
    password: "",
    confirmPassword: "",
  });

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (form.password !== form.confirmPassword) {
      alert("‚ùå M·∫≠t kh·∫©u kh√¥ng kh·ªõp!");
      return;
    }

    try {
      // ‚úÖ CHU·∫®N H√ìA D·ªÆ LI·ªÜU - S·ª≠ d·ª•ng first_name v√† last_name
      const payload = {
        first_name: form.first_name,    // ‚úÖ S·ª≠a th√†nh first_name
        last_name: form.last_name,      // ‚úÖ S·ª≠a th√†nh last_name
        email: form.email,
        password: form.password,
        mobile_number: form.mobile_number, // ‚úÖ S·ª≠a th√†nh mobile_number
        role: {
          role_name: "USER",            // ‚úÖ C√≥ th·ªÉ c·∫ßn role_name
        },
        address: {
          street: "Default Street",
          city: "Hanoi",
          state: "HN", 
          country: "VN",
          pincode: "100000",
          building_name: "Building A",  // ‚úÖ S·ª≠a th√†nh building_name
        },
        cart: {
          total_price: 0,               // ‚úÖ S·ª≠a th√†nh total_price
          products: [],
        },
      };

      console.log("üì§ Register payload:", payload);

      const res = await authService.register(payload);
      console.log("‚úÖ REGISTER RESULT:", res.data);

      // ‚úÖ SAU KHI ƒêƒÇNG K√ù TH√ÄNH C√îNG, T·ª∞ ƒê·ªòNG LOGIN
      try {
        console.log("üîÑ Auto-login after registration...");
        const loginRes = await authService.login({
          email: form.email,
          password: form.password
        });

        console.log("‚úÖ AUTO-LOGIN RESULT:", loginRes.data);

        // ‚úÖ L∆∞u th√¥ng tin user v√†o localStorage
        const loginData = loginRes.data;
        let userId;

        // T√¨m userId t·ª´ response
        if (loginData.user_id) userId = loginData.user_id;
        else if (loginData.userId) userId = loginData.userId;
        else if (loginData.user && loginData.user.user_id) userId = loginData.user.user_id;
        else if (loginData.user && loginData.user.userId) userId = loginData.user.userId;

        const userToStore = {
          userId: userId || `temp_${Date.now()}`,
          email: form.email,
          first_name: form.first_name,  // ‚úÖ L∆∞u first_name t·ª´ form
          last_name: form.last_name     // ‚úÖ L∆∞u last_name t·ª´ form
        };

        console.log("üíæ Saving user to localStorage:", userToStore);
        localStorage.setItem("user", JSON.stringify(userToStore));

        if (loginData.token) {
          localStorage.setItem("token", loginData.token);
        }

        alert("‚úÖ ƒêƒÉng k√Ω & ƒëƒÉng nh·∫≠p th√†nh c√¥ng!");
        window.location.href = "/"; // ‚úÖ Reload ƒë·ªÉ c·∫≠p nh·∫≠t Header

      } catch (loginError) {
        console.error("‚ùå Auto-login failed:", loginError);
        alert("‚úÖ ƒêƒÉng k√Ω th√†nh c√¥ng! Vui l√≤ng ƒëƒÉng nh·∫≠p.");
        navigate("/login");
      }

    } catch (error) {
      console.error("‚ùå REGISTER ERROR:", error.response?.data || error.message);
      alert("‚ùå ƒêƒÉng k√Ω th·∫•t b·∫°i: " + (error.response?.data?.message || "Vui l√≤ng ki·ªÉm tra l·∫°i!"));
    }
  };

  return (
    <section className="section-content padding-y">
      <div
        className="card mx-auto shadow"
        style={{ maxWidth: "520px", marginTop: "40px" }}
      >
        <article className="card-body">
          <header className="mb-4 text-center">
            <h4 className="card-title">Sign up</h4>
          </header>

          <form onSubmit={handleSubmit}>
            {/* T√™n */}
            <div className="form-row">
              <div className="col form-group">
                <label>First name</label>
                <input
                  name="first_name"
                  type="text"
                  className="form-control"
                  value={form.first_name}
                  onChange={handleChange}
                  placeholder="Nh·∫≠p first name"
                  required
                />
              </div>
              <div className="col form-group">
                <label>Last name</label>
                <input
                  name="last_name"
                  type="text"
                  className="form-control"
                  value={form.last_name}
                  onChange={handleChange}
                  placeholder="Nh·∫≠p last name" 
                  required
                />
              </div>
            </div>

            {/* Email */}
            <div className="form-group">
              <label>Email</label>
              <input
                name="email"
                type="email"
                className="form-control"
                value={form.email}
                onChange={handleChange}
                required
              />
            </div>

            {/* S·ªë ƒëi·ªán tho·∫°i */}
            <div className="form-group">
              <label>Mobile number</label>
              <input
                name="mobile_number"
                type="text"
                className="form-control"
                value={form.mobile_number}
                onChange={handleChange}
                placeholder="Nh·∫≠p s·ªë ƒëi·ªán tho·∫°i"
              />
            </div>

            {/* M·∫≠t kh·∫©u */}
            <div className="form-row">
              <div className="form-group col-md-6">
                <label>Password</label>
                <input
                  name="password"
                  type="password"
                  className="form-control"
                  value={form.password}
                  onChange={handleChange}
                  required
                />
              </div>
              <div className="form-group col-md-6">
                <label>Repeat password</label>
                <input
                  name="confirmPassword"
                  type="password"
                  className="form-control"
                  value={form.confirmPassword}
                  onChange={handleChange}
                  required
                />
              </div>
            </div>

            <button type="submit" className="btn btn-primary btn-block">
              Register
            </button>
          </form>
        </article>
      </div>

      <p className="text-center mt-4">
        Have an account? <Link to="/login">Log In</Link>
      </p>
    </section>
  );
};

export default Register;
