import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import userService from "../../services/userService";

const EditProfile = () => {
  const navigate = useNavigate();
  const [form, setForm] = useState({
    firstName: "",
    lastName: "",
    email: "",
    mobileNumber: "",
  });
  const [loading, setLoading] = useState(false);
  const [saving, setSaving] = useState(false);
  const [userId, setUserId] = useState(null);
  const [userInfo, setUserInfo] = useState(null);

  useEffect(() => {
    const loadUserData = async () => {
      try {
        const token = localStorage.getItem("token");
        const userData = localStorage.getItem("user");

        if (!token || !userData) {
          alert("‚ùå Vui l√≤ng ƒëƒÉng nh·∫≠p ƒë·ªÉ ti·∫øp t·ª•c!");
          navigate("/login");
          return;
        }

        const user = JSON.parse(userData);
        const currentUserId = user.userId;

        console.log("üë§ Current User ID:", currentUserId);
        setUserId(currentUserId);
        setUserInfo(user);

        if (typeof currentUserId === 'number' && currentUserId > 0) {
          setLoading(true);
          const userProfile = await userService.getUserById(currentUserId);

          console.log("‚úÖ User data loaded:", userProfile);

          setForm({
            firstName: userProfile.firstName || user.firstName || "",
            lastName: userProfile.lastName || user.lastName || "",
            email: userProfile.email || user.email || "",
            mobileNumber: userProfile.mobileNumber || user.mobileNumber || ""
          });
        } else {
          // D√πng data t·ª´ localStorage
          setForm({
            firstName: user.firstName || "",
            lastName: user.lastName || "",
            email: user.email || "",
            mobileNumber: user.mobileNumber || ""
          });
        }

      } catch (error) {
        console.error("‚ùå Failed to load user data:", error);
        alert("‚ùå Kh√¥ng th·ªÉ t·∫£i th√¥ng tin ng∆∞·ªùi d√πng!");
      } finally {
        setLoading(false);
      }
    };

    loadUserData();
  }, [navigate]);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!form.firstName.trim()) {
      alert("‚ùå Vui l√≤ng nh·∫≠p h·ªç!");
      return;
    }

    if (!form.lastName.trim()) {
      alert("‚ùå Vui l√≤ng nh·∫≠p t√™n!");
      return;
    }

    if (!form.email.trim()) {
      alert("‚ùå Vui l√≤ng nh·∫≠p email!");
      return;
    }

    if (!/\S+@\S+\.\S+/.test(form.email)) {
      alert("‚ùå Email kh√¥ng h·ª£p l·ªá!");
      return;
    }

    setSaving(true);

    try {
      const userData = JSON.parse(localStorage.getItem("user"));
      const currentUserId = userData.userId;

      console.log("üíæ Saving to database with user ID:", currentUserId);

      // ‚úÖ D√πng camelCase
      const updateData = {
        firstName: form.firstName,
        lastName: form.lastName,
        email: form.email,
        mobileNumber: form.mobileNumber
      };

      console.log("üì§ Sending camelCase data:", updateData);

      const response = await userService.updateUser(currentUserId, updateData);

      console.log("‚úÖ UPDATE SUCCESS:", response);

      const updatedUser = { 
        ...userData,
        firstName: form.firstName,
        lastName: form.lastName,
        email: form.email,
        mobileNumber: form.mobileNumber
      };
      localStorage.setItem("user", JSON.stringify(updatedUser));
      setUserInfo(updatedUser);

      alert("‚úÖ C·∫≠p nh·∫≠t h·ªì s∆° th√†nh c√¥ng!");
      navigate("/profile");

    } catch (error) {
      console.error("‚ùå UPDATE FAILED:", error);

      if (error.response) {
        console.log("üîç Error details:", error.response.data);
        alert("‚ùå L·ªói server: " + (error.response.data?.message || "Vui l√≤ng th·ª≠ l·∫°i sau"));
      } else {
        alert("‚ùå L·ªói: " + error.message);
      }
    } finally {
      setSaving(false);
    }
  };

  if (loading) {
    return (
      <section className="section-content padding-y">
        <div className="card mx-auto shadow" style={{ maxWidth: "520px", marginTop: "40px" }}>
          <article className="card-body text-center py-5">
            <div className="spinner-border text-primary mb-3" role="status">
              <span className="visually-hidden">Loading...</span>
            </div>
            <p className="mb-0">ƒêang t·∫£i th√¥ng tin h·ªì s∆°...</p>
          </article>
        </div>
      </section>
    );
