import React, { useEffect } from 'react';
import { useSearchParams, useNavigate } from 'react-router-dom';
import cartService from '../../services/cartService';
import Swal from 'sweetalert2';

const PaymentSuccess = () => {
    const [searchParams] = useSearchParams();
    const navigate = useNavigate();

    useEffect(() => {
        const finalize = async () => {
            const vnpCode = searchParams.get('vnp_ResponseCode');
            const pending = JSON.parse(localStorage.getItem('pendingOrder'));

            if (vnpCode === "00" && pending) {
                try {
                    // Thanh toán thật sự thành công -> Lưu vào Database Java
                    await cartService.createOrder(
                        pending.email, pending.cartId, 'BANKING', pending.address, pending.phone
                    );
                    await cartService.clearCart(pending.email, pending.cartId);
                    
                    localStorage.removeItem('pendingOrder'); // Xóa đơn tạm
                    Swal.fire('Thành công', 'Thanh toán online thành công!', 'success');
                    navigate('/orders');
                } catch (e) {
                    Swal.fire('Lỗi lưu đơn', 'Thanh toán thành công nhưng lưu đơn thất bại. Liên hệ Admin!', 'error');
                }
            } else {
                Swal.fire('Thất bại', 'Giao dịch bị hủy hoặc lỗi hệ thống.', 'error');
                navigate('/cart');
            }
        };
        finalize();
    }, []);

    return (
        <div className="container text-center py-5">
            <div className="spinner-border text-warning" role="status"></div>
            <h4 className="mt-3">Đang xác thực giao dịch với VNPay...</h4>
        </div>
    );
};

export default PaymentSuccess;
