import React, { useEffect, useState } from "react";
import { ShoppingCart, Users, Package, DollarSign, TrendingUp, ArrowUpRight } from "lucide-react";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  Filler,
} from "chart.js";
import { Line } from "react-chartjs-2";

// ƒêƒÉng k√Ω c√°c th√†nh ph·∫ßn c·ªßa Chart.js
ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend, Filler);

const Dashboard = () => {
  const [stats, setStats] = useState({
    totalRevenue: 15500000,
    orders: 120,
    products: 45,
    customers: 85,
  });

  // D·ªØ li·ªáu bi·ªÉu ƒë·ªì m·∫´u (Sau n√†y fetch t·ª´ Backend)
  const chartData = {
    labels: ["Th√°ng 1", "Th√°ng 2", "Th√°ng 3", "Th√°ng 4", "Th√°ng 5", "Th√°ng 6"],
    datasets: [
      {
        fill: true,
        label: "Doanh thu (VNƒê)",
        data: [12000000, 19000000, 15000000, 22000000, 18000000, 25000000],
        borderColor: "#4e73df",
        backgroundColor: "rgba(78, 115, 223, 0.05)",
        tension: 0.4, // ƒê·ªô cong c·ªßa ƒë∆∞·ªùng
      },
    ],
  };

  const chartOptions = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      legend: { display: false },
    },
    scales: {
      y: { beginAtZero: true, grid: { borderDash: [2] } },
      x: { grid: { display: false } },
    },
  };

  return (
    <div className="container-fluid p-4" style={{ backgroundColor: "#f4f6f9", minHeight: "100vh" }}>
      {/* Header */}
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h3 className="fw-bold text-dark m-0">üìä Dashboard Qu·∫£n Tr·ªã</h3>
        <button className="btn btn-primary btn-sm shadow-sm">
          <TrendingUp size={16} className="me-2" /> Xu·∫•t b√°o c√°o
        </button>
      </div>

      {/* 4 Th·∫ª th·ªëng k√™ nhanh */}
      <div className="row g-3 mb-4">
        <div className="col-md-3">
          <StatCard title="DOANH THU" value={stats.totalRevenue.toLocaleString() + "ƒë"} icon={<DollarSign color="#fff" />} bgColor="bg-primary" trend="+12%" />
        </div>
        <div className="col-md-3">
          <StatCard title="ƒê∆†N H√ÄNG" value={stats.orders} icon={<ShoppingCart color="#fff" />} bgColor="bg-success" trend="+5%" />
        </div>
        <div className="col-md-3">
          <StatCard title="S·∫¢N PH·∫®M" value={stats.products} icon={<Package color="#fff" />} bgColor="bg-warning" trend="0%" />
        </div>
        <div className="col-md-3">
          <StatCard title="KH√ÅCH H√ÄNG" value={stats.customers} icon={<Users color="#fff" />} bgColor="bg-info" trend="+18%" />
        </div>
      </div>

      <div className="row g-4">
        {/* Bi·ªÉu ƒë·ªì doanh s·ªë */}
        <div className="col-lg-8">
          <div className="card border-0 shadow-sm p-4 h-100">
            <div className="d-flex justify-content-between align-items-center mb-3">
              <h6 className="fw-bold text-secondary">BI·ªÇU ƒê·ªí TƒÇNG TR∆Ø·ªûNG</h6>
              <select className="form-select form-select-sm w-auto">
                <option>6 th√°ng g·∫ßn nh·∫•t</option>
                <option>NƒÉm nay</option>
              </select>
            </div>
            <div style={{ height: "300px" }}>
              <Line data={chartData} options={chartOptions} />
            </div>
          </div>
        </div>

        {/* ƒê∆°n h√†ng m·ªõi nh·∫•t */}
        <div className="col-lg-4">
          <div className="card border-0 shadow-sm p-4 h-100">
            <h6 className="fw-bold text-secondary mb-3">ƒê∆†N H√ÄNG M·ªöI NH·∫§T</h6>
            <div className="list-group list-group-flush">
              <OrderLink id="1234" item="Son Dior" price="850k" time="5 ph√∫t tr∆∞·ªõc" status="bg-success" />
              <OrderLink id="1235" item="Serum Klairs" price="390k" time="15 ph√∫t tr∆∞·ªõc" status="bg-warning" />
              <OrderLink id="1236" item="Kem ch·ªëng n·∫Øng" price="420k" time="1 gi·ªù tr∆∞·ªõc" status="bg-success" />
              <OrderLink id="1237" item="N∆∞·ªõc hoa Chanel" price="3.200k" time="2 gi·ªù tr∆∞·ªõc" status="bg-danger" />
            </div>
            <button className="btn btn-light btn-sm mt-3 w-100 text-primary fw-bold">Xem t·∫•t c·∫£</button>
          </div>
        </div>
      </div>
    </div>
  );
};

// Component th·∫ª th·ªëng k√™ "Pro"
const StatCard = ({ title, value, icon, bgColor, trend }) => (
  <div className="card border-0 shadow-sm overflow-hidden h-100 transition-hover">
    <div className="card-body p-4">
      <div className="d-flex justify-content-between align-items-center mb-2">
        <div className={`p-2 rounded-circle ${bgColor} shadow-sm`}>{icon}</div>
        <span className="badge bg-light text-success border small">
          <ArrowUpRight size={12} /> {trend}
        </span>
      </div>
      <p className="text-muted small fw-bold mb-1">{title}</p>
      <h4 className="fw-bold m-0">{value}</h4>
    </div>
  </div>
);

// Component d√≤ng ƒë∆°n h√†ng
const OrderLink = ({ id, item, price, time, status }) => (
  <div className="list-group-item px-0 border-0 d-flex justify-content-between align-items-center">
    <div className="d-flex align-items-center">
      <div className={`p-1 rounded-circle ${status} me-3`} style={{ width: "10px", height: "10px" }}></div>
      <div>
        <div className="fw-bold small">#{id} - {item}</div>
        <div className="text-muted" style={{ fontSize: "11px" }}>{time}</div>
      </div>
    </div>
    <div className="fw-bold text-primary small">{price}</div>
  </div>
);

export default Dashboard;
