import { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import productService from "../../services/productService";

const IMAGE_BASE_URL = "http://localhost:8080/api/public/products/image";

const ProductList = () => {
  const [products, setProducts] = useState([]);
  const [pagination, setPagination] = useState({
    page: 0, // Page number (zero-based)
    perPage: 8, // Items per page
  });
  const [total, setTotal] = useState(0);
  const [loading, setLoading] = useState(false);

  // T√≠nh totalPages t·ª´ total v√† perPage
  const totalPages = Math.ceil(total / pagination.perPage);

  // üß© H√†m t·∫£i s·∫£n ph·∫©m c√≥ ph√¢n trang
  const fetchProducts = async (page = 0, perPage = 8) => {
    try {
      setLoading(true);

      console.log("üîÑ ƒêang g·ªçi API v·ªõi:", { 
        page, 
        perPage 
      });

      const response = await productService.getAllProducts(page, perPage);

      console.log("üì¶ Response t·ª´ API:", response);

      const data = response || {};
      const content = data.content || [];
      const totalElements = data.totalElements || 0;

      setProducts(content);
      setTotal(totalElements);

    } catch (err) {
      console.error("‚ùå L·ªói khi t·∫£i s·∫£n ph·∫©m:", err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchProducts(pagination.page, pagination.perPage);
  }, [pagination.page, pagination.perPage]);

  // üß≠ X·ª≠ l√Ω khi chuy·ªÉn trang
  const handlePageChange = (newPage) => {
    console.log("üéØ Chuy·ªÉn trang t·ª´", pagination.page, "sang", newPage);
    if (newPage >= 0 && newPage < totalPages) {
      setPagination(prev => ({
        ...prev,
        page: newPage
      }));
    }
  };

  // üßÆ X·ª≠ l√Ω thay ƒë·ªïi s·ªë l∆∞·ª£ng s·∫£n ph·∫©m/trang
  const handlePerPageChange = (newPerPage) => {
    setPagination({
      page: 0, // Reset v·ªÅ trang ƒë·∫ßu
      perPage: newPerPage
    });
  };

  // üìä T·∫°o danh s√°ch s·ªë trang ƒë·ªÉ hi·ªÉn th·ªã
  const renderPageNumbers = () => {
    if (totalPages <= 1) return null;

    const pages = [];
    const maxVisible = 3; // Hi·ªÉn th·ªã t·ªëi ƒëa 3 trang
    let startPage = 0;
    let endPage = totalPages - 1;

    // T√≠nh to√°n trang b·∫Øt ƒë·∫ßu v√† k·∫øt th√∫c ƒë·ªÉ hi·ªÉn th·ªã
    if (totalPages > maxVisible) {
      startPage = Math.max(0, pagination.page - Math.floor(maxVisible / 2));
      endPage = startPage + maxVisible - 1;

      if (endPage >= totalPages) {
        endPage = totalPages - 1;
        startPage = Math.max(0, endPage - maxVisible + 1);
      }
    }

    for (let i = startPage; i <= endPage; i++) {
      pages.push(
        <button
          key={i}
          className={`btn mx-1 ${
            i === pagination.page
              ? "btn-primary text-white"
              : "btn-outline-primary"
          }`}
          onClick={() => handlePageChange(i)}
        >
          {i + 1}
        </button>
      );
    }

    return pages;
  };

  return (
    <section className="padding-bottom container mt-5">
      <header className="section-heading mb-4 text-center">
        <h3 className="title-section text-uppercase fw-bold text-primary">
          üõçÔ∏è T·∫•t c·∫£ s·∫£n ph·∫©m
        </h3>
      </header>

      {/* Th√¥ng tin ph√¢n trang */}
      <div className="d-flex justify-content-between align-items-center mb-3">
        <div className="text-muted small">
          {total > 0 ? (
            <>üìÑ Trang {pagination.page + 1} / {totalPages} ‚Ä¢ {products.length} s·∫£n ph·∫©m / {total} t·ªïng c·ªông</>
          ) : (
            "üìÑ ƒêang t·∫£i..."
          )}
        </div>

        {/* B·ªô ch·ªçn s·ªë s·∫£n ph·∫©m/trang */}
        <div className="d-flex align-items-center">
          <label className="me-2 fw-semibold">Hi·ªÉn th·ªã:</label>
          <select
            value={pagination.perPage}
            onChange={(e) => handlePerPageChange(Number(e.target.value))}
            className="form-select w-auto"
          >
            <option value="4">4 / trang</option>
            <option value="8">8 / trang</option>
            <option value="12">12 / trang</option>
            <option value="16">16 / trang</option>
          </select>
        </div>
      </div>

      {/* üß© Hi·ªÉn th·ªã s·∫£n ph·∫©m */}
      {loading ? (
        <div className="text-center py-5">
          <div className="spinner-border text-primary" role="status"></div>
          <p className="mt-2 text-muted">ƒêang t·∫£i s·∫£n ph·∫©m...</p>
        </div>
      ) : (
        <div className="row">
          {products.map((p, index) => {
            const productId = p.productId || p.product_id;

            return (
              <div
                key={productId}
                className="col-xl-3 col-lg-4 col-md-6 col-12 mb-4"
              >
                <div className="card card-product-grid shadow-sm h-100">
                  <div className="img-wrap">
                    <Link
                      to={`/product/${productId}`}
                      className="text-decoration-none"
                    >
                      <img
                        src={`${IMAGE_BASE_URL}/${p.image}`}
                        alt={p.productName || p.product_name}
                        onError={(e) =>
                          (e.target.src =
                            "https://cdn-icons-png.flaticon.com/512/679/679922.png")
                        }
                        style={{
                          width: "100%",
                          height: "250px",
                          objectFit: "cover",
                          borderRadius: "10px",
                        }}
                      />
                    </Link>
                  </div>

                  <div className="info-wrap p-3 d-flex flex-column">
                    <Link
