import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import productService from '../../services/productService';

// DỮ LIỆU BÀI VIẾT LẤY TỪ CÁC NGUỒN UY TÍN TRÊN INTERNET ( Elle, Harper's Bazaar,...)
const INTERNET_POSTS = [
  {
    title: "Xu hướng Skincare 2026: Khi 'Skin-Minimalism' lên ngôi",
    desc: "Tối giản hóa quy trình chăm sóc da không chỉ giúp tiết kiệm thời gian mà còn giúp da 'thở' tốt hơn...",
    img: "https://images.pexels.com/photos/3762871/pexels-photo-3762871.jpeg?auto=compress&cs=tinysrgb&w=800",
    date: "12/01/2026",
    link: "https://www.elle.vn/cham-soc-da",
    source: "Elle Vietnam"
  },
  {
    title: "Cách chọn Serum Vitamin C cho từng loại da",
    desc: "Vitamin C là hoạt chất vàng trong việc làm sáng da và mờ thâm, nhưng chọn nồng độ bao nhiêu là đủ?",
    img: "https://images.pexels.com/photos/3618606/pexels-photo-3618606.jpeg?auto=compress&cs=tinysrgb&w=800",
    date: "10/01/2026",
    link: "https://vogue.com/beauty",
    source: "Vogue Beauty"
  },
  {
    title: "Top 10 thành phần mỹ phẩm 'thuần chay' được săn đón",
    desc: "Mỹ phẩm thuần chay (Vegan Beauty) đang trở thành tiêu chuẩn mới của người tiêu dùng hiện đại...",
    img: "https://images.pexels.com/photos/4041391/pexels-photo-4041391.jpeg?auto=compress&cs=tinysrgb&w=800",
    date: "08/01/2026",
    link: "https://bazaarvietnam.vn/",
    source: "Bazaar VN"
  }
];

const IMAGE_BASE_URL = "http://localhost:8080/api/public/products/image";

const ProductSold = () => {
  const [topProducts, setTopProducts] = useState([]);
  const [allProducts, setAllProducts] = useState([]);
  const [loading, setLoading] = useState(false);
  const [page, setPage] = useState(0);

  useEffect(() => {
    fetchInitialData();
  }, []);

  const fetchInitialData = async () => {
    setLoading(true);
    try {
      const [topRes, allRes] = await Promise.all([
        productService.getTopSellingProducts(0, 8),
        productService.getAllProducts(0, 8)
      ]);
      setTopProducts(topRes.content || []);
      setAllProducts(allRes.content || []);
    } catch (err) {
      console.error('Lỗi tải dữ liệu:', err);
    } finally {
      setLoading(false);
    }
  };

  const loadMoreProducts = async () => {
    const nextPage = page + 1;
    setLoading(true);
    try {
      const res = await productService.getAllProducts(nextPage, 8);
      setAllProducts([...allProducts, ...(res.content || [])]);
      setPage(nextPage);
    } catch (err) {
      console.error('Lỗi tải thêm:', err);
    } finally {
      setLoading(false);
    }
  };

  const ProductItem = ({ p, index, isTop }) => {
    const productId = p.productId || p.product_id;
    return (
      <div className="col-xl-3 col-lg-4 col-md-6 col-6 mb-4">
        <div className="card h-100 border-0 shadow-sm hover-up overflow-hidden">
          {isTop && index < 3 && (
            <div className="position-absolute top-0 start-0 m-2 badge bg-primary text-white shadow" style={{zIndex: 5}}>
              #{index + 1} Best Seller
            </div>
          )}
          <div className="p-3 text-center bg-white">
            <Link to={`/product/${productId}`}>
              <img
                src={`${IMAGE_BASE_URL}/${p.image}`}
                alt={p.productName}
                onError={(e) => (e.target.src = "https://cdn-icons-png.flaticon.com/512/679/679922.png")}
                className="img-fluid"
                style={{ height: "220px", objectFit: "contain" }}
              />
            </Link>
          </div>
          <div className="card-body p-3">
            <Link to={`/product/${productId}`} className="text-dark fw-bold text-decoration-none d-block mb-1 text-truncate">
              {p.productName}
            </Link>
            <div className="text-danger fw-bold h5 mb-2">
              {(p.specialPrice || p.price).toLocaleString()} ₫
            </div>
            <div className="d-flex justify-content-between align-items-center small text-muted">
              <span>Đã bán: {p.sold || 0}</span>
              <span className="badge bg-light text-dark"><i className="fa fa-star text-warning"></i> 4.9</span>
            </div>
          </div>
        </div>
      </div>
    );
  };

  return (
    <div className="main-content-wrapper bg-light pb-5">

      {/* SECTION 1: TOP TRENDING COSMETICS */}
      <section className="container pt-5">
        <div className="text-center mb-5">
          <h2 className="fw-bold text-uppercase" style={{letterSpacing: '2px'}}>Mỹ phẩm bán chạy</h2>
          <div className="mx-auto bg-primary" style={{height: '3px', width: '60px'}}></div>
        </div>
        <div className="row">
          {topProducts.map((p, i) => <ProductItem key={`top-${i}`} p={p} index={i} isTop={true} />)}
        </div>
      </section>

      {/* SECTION 2: DISCOVER COSMETICS */}
      <section className="container mt-5">
        <h4 className="fw-bold mb-4"><i className="fa fa-th-large me-2"></i>DÀNH CHO BẠN</h4>
        <div className="row">
          {allProducts.map((p, i) => <ProductItem key={`all-${i}`} p={p} isTop={false} />)}
        </div>
        <div className="text-center mt-5">
          <button className="btn btn-outline-primary px-5 rounded-pill shadow-sm fw-bold" onClick={loadMoreProducts} disabled={loading}>
            {loading ? "ĐANG TẢI..." : "KHÁM PHÁ THÊM"}
          </button>
        </div>
      </section>

    </div>
  );
};

export default ProductSold;
