import React from "react";
import { Routes, Route } from "react-router-dom";
import 'bootstrap/dist/css/bootstrap.min.css';

// Layouts
import Header from "./layouts/Header";
import Footer from "./layouts/Footer";
import Main from "./layouts/Main";

// Pages User
import Login from "./pages/auth/Login";
import Register from "./pages/auth/Register";
import ForgotPassword from "./pages/auth/ForgotPassword";
import ProductList from "./pages/product/ProductList";
import ProductDetail from "./pages/product/ProductDetail";
import ProductCart from "./pages/cart/ProductCart";
import Checkout from "./pages/order/CheckOut";
import OrderList from "./pages/order/OrderList";

// Admin
import AdminRoute from "./routes/AdminRoute";
import AdminLayout from "./pages/admin/AdminLayout";
import AdminDashboard from "./pages/admin/Dashboard";
import AdminLogin from "./pages/admin/AdminLogin";

function App() {
  return (
    <Routes>
      {/* ================= GIAO DIỆN USER (Có Header/Footer) ================= */}
      <Route
        path="/*"
        element={
          <>
            <Header />
            <div style={{ minHeight: "80vh" }}> {/* Đảm bảo nội dung không bị dính Footer */}
              <Routes>
                <Route path="/" element={<Main />} />
                <Route path="/login" element={<Login />} />
                <Route path="/register" element={<Register />} />
                <Route path="/forgot-password" element={<ForgotPassword />} />
                <Route path="/product" element={<ProductList />} />
                <Route path="/product/:productId" element={<ProductDetail />} />
                <Route path="/cart" element={<ProductCart />} />
                <Route path="/checkout" element={<Checkout />} />
                <Route path="/orders" element={<OrderList />} />
                <Route path="/admin/login" element={<AdminLogin />} />
                <Route path="*" element={<h2 className="text-center mt-4">404</h2>} />
              </Routes>
            </div>
            <Footer />
          </>
        }
      />

      {/* ================= GIAO DIỆN ADMIN (Tách biệt hoàn toàn) ================= */}
      <Route
        path="/admin"
        element={
          <AdminRoute>
            <AdminLayout />
          </AdminRoute>
        }
      >
        <Route index element={<AdminDashboard />} />
        <Route path="admin/products" element={<ProductList />} />
        {/* <Route path="orders" element={<AdminOrders />} /> */}
      </Route>
    </Routes>
  );
}

export default App;
