// src/services/categoryService.js
import api from "./api";

const categoryService = {
  // üü¢ Public: L·∫•y t·∫•t c·∫£ danh m·ª•c
  async getAllCategories() {
    try {
      const { data } = await api.get("/public/categories");

      // ‚úÖ Ki·ªÉm tra n·∫øu API c√≥ tr·∫£ v·ªÅ content
      if (Array.isArray(data)) return data;
      if (data.content && Array.isArray(data.content)) return data.content;

      console.warn("‚ö†Ô∏è D·ªØ li·ªáu danh m·ª•c kh√¥ng ph·∫£i m·∫£ng:", data);
      return [];
    } catch (error) {
      console.error("‚ùå L·ªói khi l·∫•y danh m·ª•c:", error);
      return [];
    }
  },

  async addProductToCategory(categoryId, productDTO) {
    return await api.post(`/admin/categories/${categoryId}/product`, productDTO);
  },

  async removeProductFromCategory(categoryId, productId) {
    return await api.delete(`/admin/categories/${categoryId}/product/${productId}`);
  },

  async createCategory(categoryDTO) {
    return await api.post("/admin/categories", categoryDTO);
  },

  async updateCategory(categoryId, categoryDTO) {
    return await api.put(`/admin/categories/${categoryId}`, categoryDTO);
  }
};

export default categoryService;
