import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import userService from "../../services/userService";

const EditProfile = () => {
   const navigate = useNavigate();
  const [form, setForm] = useState({
    firstName: "",
    lastName: "",
    email: "",
    mobileNumber: "",
  });
  const [loading, setLoading] = useState(false);
  const [saving, setSaving] = useState(false);

  useEffect(() => {
    const loadUserData = async () => {
      try {
        const token = localStorage.getItem("token");
        const userData = localStorage.getItem("user");

        if (!token || !userData) {
          alert("‚ùå Vui l√≤ng ƒëƒÉng nh·∫≠p ƒë·ªÉ ti·∫øp t·ª•c!");
          navigate("/login");
          return;
        }

        const user = JSON.parse(userData);
        const userId = user.userId;

        console.log("üë§ Current User ID:", userId);

        if (typeof userId === 'number' && userId > 0) {
          setLoading(true);
          const userProfile = await userService.getUserById(userId);

          console.log("‚úÖ User data loaded:", userProfile);

          setForm({
            firstName: userProfile.firstName || "",
            lastName: userProfile.lastName || "",
            email: userProfile.email || "",
            mobileNumber: userProfile.mobileNumber || ""
          });
        }

      } catch (error) {
        console.error("‚ùå Failed to load user data:", error);
        alert("‚ùå Kh√¥ng th·ªÉ t·∫£i th√¥ng tin ng∆∞·ªùi d√πng!");
      } finally {
        setLoading(false);
      }
    };

    loadUserData();
  }, [navigate]);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!form.firstName.trim()) {
      alert("‚ùå Vui l√≤ng nh·∫≠p h·ªç!");
      return;
    }

    if (!form.lastName.trim()) {
      alert("‚ùå Vui l√≤ng nh·∫≠p t√™n!");
      return;
    }

    if (!form.email.trim()) {
      alert("‚ùå Vui l√≤ng nh·∫≠p email!");
      return;
    }

    if (!/\S+@\S+\.\S+/.test(form.email)) {
      alert("‚ùå Email kh√¥ng h·ª£p l·ªá!");
      return;
    }

    setSaving(true);

    try {
      const userData = JSON.parse(localStorage.getItem("user"));
      const userId = userData.userId;

      console.log("üíæ Saving to database with user ID:", userId);

      // ‚úÖ D√πng camelCase
      const updateData = {
        firstName: form.firstName,
        lastName: form.lastName,
        email: form.email,
        mobileNumber: form.mobileNumber
      };

      console.log("üì§ Sending camelCase data:", updateData);

      const response = await userService.updateUser(userId, updateData);

      console.log("‚úÖ UPDATE SUCCESS:", response);

      const updatedUser = { 
        ...userData,
        firstName: form.firstName,
        lastName: form.lastName,
        email: form.email,
        mobileNumber: form.mobileNumber
      };
      localStorage.setItem("user", JSON.stringify(updatedUser));

      alert("‚úÖ C·∫≠p nh·∫≠t h·ªì s∆° th√†nh c√¥ng!");
      navigate("/profile");

    } catch (error) {
      console.error("‚ùå UPDATE FAILED:", error);

      if (error.response) {
        console.log("üîç Error details:", error.response.data);
        alert("‚ùå L·ªói server: " + (error.response.data?.message || "Vui l√≤ng th·ª≠ l·∫°i sau"));
      } else {
        alert("‚ùå L·ªói: " + error.message);
      }
    } finally {
      setSaving(false);
    }
  };

  if (loading) {
    return (
      <div className="p-4">
        <div className="card mx-auto" style={{ maxWidth: "400px", marginTop: "50px" }}>
          <div className="card-body text-center py-5">
            <div className="spinner-border text-primary mb-3" role="status">
              <span className="visually-hidden">Loading...</span>
            </div>
            <p className="mb-0">ƒêang t·∫£i th√¥ng tin h·ªì s∆°...</p>
          </div>
        </div>
      </div>
    );
  }

  return (
    <section className="section-content padding-y">
      <div className="card mx-auto shadow" style={{ maxWidth: "520px", marginTop: "40px" }}>
        <article className="card-body">
          <header className="mb-4 text-center">
            <h4 className="card-title">Ch·ªânh s·ª≠a h·ªì s∆°</h4>
            {userId !== null && (
              <small className="text-muted">
                User ID: {userId} | {typeof userId === 'number' && userId >= 0 ? 'T√†i kho·∫£n th·ª±c' : 'T√†i kho·∫£n t·∫°m'}
              </small>
            )}
          </header>

          <form onSubmit={handleSubmit}>
            <div className="form-row">
              <div className="col form-group">
                <label>First name</label>
                <input
                  name="first_name"
                  type="text"
                  className="form-control"
                  value={form.firstName}
                  onChange={handleChange}
                  required
                  disabled={saving}
                />
              </div>
              <div className="col form-group">
                <label>Last name</label>
                <input
                  name="last_name"
                  type="text"
                  className="form-control"
                  value={form.firstName}
                  onChange={handleChange}
                  required
                  disabled={saving}
                />
              </div>
            </div>

            <div className="form-group">
              <label>Email</label>
              <input
                name="email"
                type="email"
                className="form-control"
                value={form.email}
                onChange={handleChange}
                required
                disabled={saving}
              />
            </div>

            <div className="form-group">
              <label>Mobile number</label>
              <input
                name="mobile_number"
                type="text"
                className="form-control"
                value={form.mobile_number}
                onChange={handleChange}
                disabled={saving}
                placeholder="+84 123 456 789"
              />
            </div>

            <div className="form-group d-flex gap-2">
              <button 
                type="button" 
                className="btn btn-secondary flex-fill"
                onClick={() => navigate(-1)}
                disabled={saving}
              >
                H·ªßy
              </button>
              <button 
                type="submit" 
                className="btn btn-primary flex-fill"
                disabled={saving}
              >
                {saving ? (
                  <>
                    <span className="spinner-border spinner-border-sm me-2" role="status"></span>
                    ƒêang l∆∞u...
                  </>
                ) : (
                  'C·∫≠p nh·∫≠t'
                )}
              </button>
            </div>
          </form>
        </article>
      </div>

      <p className="text-center mt-4">
        <Link to="/">‚Üê Quay v·ªÅ trang ch·ªß</Link>
      </p>
    </section>
  );
};

export default EditProfile;
