import React, { useState, useEffect } from 'react';
import bannerService from '../../services/bannerService';

const BannerThree = () => {
  const [leftBanner, setLeftBanner] = useState(null);
  const [rightBanner, setRightBanner] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchBanners();
  }, []);

  const fetchBanners = async () => {
    try {
      setLoading(true);
      setError(null);

      console.log('üîÑ ƒêang t·∫£i banners t·ª´ database...');

      // üî¥ TH·ª¨ C√ÅCH 1: L·∫•y t·∫•t c·∫£ banners t·ª´ database
      const allBanners = await bannerService.getAllBanners();
      console.log('üì¶ T·∫•t c·∫£ banners t·ª´ DB:', allBanners);

      if (allBanners && allBanners.length > 0) {
        // ‚úÖ T√¨m banner c√≥ displayOrder = 1 (ho·∫∑c bannerId = 7)
        const banner7 = allBanners.find(banner => 
          banner.bannerId === 7 || 
          banner.id === 7 || 
          banner.displayOrder === 1
        );

        // ‚úÖ T√¨m banner c√≥ displayOrder = 2 (ho·∫∑c bannerId = 8)
        const banner8 = allBanners.find(banner => 
          banner.bannerId === 8 || 
          banner.id === 8 || 
          banner.displayOrder === 2
        );

        console.log('üîç K·∫øt qu·∫£ t√¨m banner:');
        console.log('- Banner 7:', banner7);
        console.log('- Banner 8:', banner8);

        // ‚úÖ N·∫øu kh√¥ng t√¨m th·∫•y theo ID, l·∫•y 2 banner ƒë·∫ßu ti√™n
        if (!banner7 && allBanners.length >= 1) {
          setLeftBanner(allBanners[0]);
          console.log('‚úÖ D√πng banner ƒë·∫ßu ti√™n cho v·ªã tr√≠ tr√°i');
        } else {
          setLeftBanner(banner7);
        }

        if (!banner8 && allBanners.length >= 2) {
          setRightBanner(allBanners[1]);
          console.log('‚úÖ D√πng banner th·ª© hai cho v·ªã tr√≠ ph·∫£i');
        } else {
          setRightBanner(banner8);
        }

      } else {
        console.warn('‚ö†Ô∏è Kh√¥ng c√≥ banner n√†o trong database');
        setError('Kh√¥ng c√≥ banner n√†o ƒë·ªÉ hi·ªÉn th·ªã');
      }

    } catch (err) {
      console.error('‚ùå L·ªói khi l·∫•y banners t·ª´ database:', err);
      setError('Kh√¥ng th·ªÉ t·∫£i d·ªØ li·ªáu banners');
    } finally {
      setLoading(false);
    }
  };

  const getImageUrl = (banner) => {
    if (!banner?.imageUrl) {
      console.log('‚ö†Ô∏è Banner kh√¥ng c√≥ imageUrl:', banner);
      return 'https://via.placeholder.com/600x400/2d3748/ffffff?text=No+Image';
    }

    const imageUrl = `http://localhost:8080/api/public/banners/image/${banner.imageUrl}`;
    console.log('üñºÔ∏è Image URL:', imageUrl);
    return imageUrl;
  };

  // Component cho m·ªói banner card
  const BannerCard = ({ banner, position }) => {
    if (!banner) return null;

    console.log(`üé® Rendering banner ${position}:`, banner);

    const defaultContent = position === 'left' 
      ? {
          title: "∆Øu ƒê√£i ƒê·∫∑c Bi·ªát",
          description: "Kh√°m ph√° nh·ªØng s·∫£n ph·∫©m c√¥ng ngh·ªá m·ªõi nh·∫•t v·ªõi gi√° ∆∞u ƒë√£i ƒë·∫∑c bi·ªát. Ch·∫•t l∆∞·ª£ng ƒë·∫£m b·∫£o, giao h√†ng nhanh ch√≥ng.",
          buttonText: "Kh√°m Ph√° Ngay"
        }
      : {
          title: "Combo Ti·∫øt Ki·ªám", 
          description: "Tr·∫£i nghi·ªám tr·ªçn b·ªô s·∫£n ph·∫©m cao c·∫•p v·ªõi m·ª©c gi√° h·∫•p d·∫´n. N√¢ng c·∫•p thi·∫øt b·ªã c·ªßa b·∫°n ngay h√¥m nay.",
          buttonText: "Mua Ngay"
        };

    return (
      <div 
        className="position-relative rounded-3 overflow-hidden h-100 shadow-lg"
        style={{
          height: '400px',
          backgroundImage: `url(${getImageUrl(banner)})`,
          backgroundSize: 'cover',
          backgroundPosition: 'center',
          backgroundRepeat: 'no-repeat'
        }}
      >
        {/* Gradient Overlay */}
        <div 
          className="position-absolute top-0 start-0 w-100 h-100"
          style={{
            background: 'linear-gradient(135deg, rgba(0,0,0,0.7) 0%, rgba(0,0,0,0.3) 100%)'
