import React, { useState, useEffect, useCallback } from "react";
import api from "../../../services/api";
import { Eye, Package, User, Truck, CheckCircle, XCircle, Calendar, CreditCard, MapPin, Search, ChevronRight } from "lucide-react";
import Swal from "sweetalert2";
import { useNavigate } from "react-router-dom";

const OrderListAD = () => {
    const [orders, setOrders] = useState([]);
    const [loading, setLoading] = useState(true);
    const [selectedOrder, setSelectedOrder] = useState(null);
    const [showModal, setShowModal] = useState(false);
    const navigate = useNavigate();

    // ================= HELPER: LẤY TOKEN GIỐNG CATE =================
    const getAdminToken = () => localStorage.getItem("admin-token") || localStorage.getItem("token");

    // ================= FETCH DATA (Logic Token của Cate) =================
    const fetchOrders = useCallback(async (page = 0) => {
        try {
            setLoading(true);
            const token = getAdminToken();
            
            // Check Token trước khi gọi API
            if (!token) {
                navigate("/login");
                return;
            }

            const res = await api.get(`/admin/orders?pageNumber=${page}`, {
                headers: { Authorization: `Bearer ${token}` }
            });
            
            setOrders(res.data.content || []);
        } catch (err) {
            console.error("❌ Lỗi Fetch Order:", err);
            // Nếu lỗi 401 (Hết hạn/Chưa login) hoặc 403 (Không phải Admin) -> Đá về login
            if (err.response?.status === 401 || err.response?.status === 403) {
                navigate("/login");
            }
        } finally {
            setLoading(false);
        }
    }, [navigate]);

    useEffect(() => { 
        fetchOrders(0); 
    }, [fetchOrders]);

    // ================= XỬ LÝ XEM CHI TIẾT =================
    const handleViewOrder = async (orderId) => {
        try {
            const token = getAdminToken();
            const res = await api.get(`/admin/debug/orders/${orderId}`, {
                headers: { Authorization: `Bearer ${token}` }
            });
            setSelectedOrder(res.data);
            setShowModal(true);
        } catch (err) {
            Swal.fire("Lỗi", "Không thể lấy chi tiết đơn hàng!", "error");
        }
    };

    // ================= CẬP NHẬT TRẠNG THÁI (Dùng Param orderStatus) =================
    const updateStatus = async (orderId, newStatus) => {
        try {
            const token = getAdminToken();
            // Truyền param orderStatus khớp với Backend đã sửa
            await api.put(`/admin/orders/${orderId}/status?orderStatus=${newStatus}`, {}, {
                headers: { Authorization: `Bearer ${token}` }
            });

            Swal.fire({
                title: "Thành công!",
                text: `Đơn hàng đã chuyển sang: ${newStatus}`,
                icon: "success",
                timer: 1500,
                showConfirmButton: false
            });

            fetchOrders(); // Load lại danh sách
            setShowModal(false);
        } catch (err) {
            Swal.fire("Lỗi", "Bạn không có quyền hoặc cập nhật thất bại!", "error");
        }
    };

    return (
        <div style={{ backgroundColor: "#F4F7FA", minHeight: "100vh", padding: "40px", fontFamily: "'Inter', sans-serif" }}>
            
            {/* Header Section */}
            <div className="d-flex justify-content-between align-items-center mb-5">
                <div>
                    <h1 style={{ fontWeight: 850, fontSize: "36px", letterSpacing: "-1.5px", color: "#0F172A" }}>Orders Management</h1>
                    <span className="badge bg-white text-dark shadow-sm border-0 px-3 py-2 rounded-pill small fw-bold">
                        Hệ thống quản lý đơn hàng ADMIN
                    </span>
                </div>
                <button className="btn btn-dark shadow-lg border-0 rounded-pill px-4 py-2 fw-bold" onClick={() => fetchOrders(0)}>
                    Làm mới dữ liệu
                </button>
            </div>

            {/* List Orders */}
            <div className="row g-3">
                {loading ? (
                    <div className="col-12 text-center py-5 fw-bold text-muted">Đang tải dữ liệu đơn hàng...</div>
                ) : orders.map(o => (
                    <div className="col-12" key={o.orderId}>
                        <div className="order-item-card transition-all" onClick={() => handleViewOrder(o.orderId)}>
                            <div className="d-flex align-items-center justify-content-between p-4 bg-white shadow-sm" style={{ borderRadius: "20px" }}>
                                <div className="d-flex align-items-center gap-4 text-start">
                                    <div className="order-id-circle shadow-sm">
                                        <Package size={18} />
                                    </div>
                                    <div>
                                        <div className="fw-bold text-dark h6 mb-1">#{o.orderId}</div>
                                        <div className="text-muted small fw-medium">{o.email}</div>
                                    </div>
                                </div>

                                <div className="d-none d-md-block text-center">
                                    <div className="text-muted mb-1" style={{ fontSize: "10px", fontWeight: 800, textTransform: "uppercase" }}>Thời gian</div>
                                    <div className="small fw-bold text-dark">{o.orderDate}</div>
                                </div>

                                <div className="text-center">
                                    <div className="text-muted mb-1" style={{ fontSize: "10px", fontWeight: 800, textTransform: "uppercase" }}>Tổng tiền</div>
                                    <div className="fw-bold text-primary">{o.totalAmount?.toLocaleString()}đ</div>
                                </div>

                                <div className="d-flex align-items-center gap-4">
                                    <StatusPill status={o.orderStatus} />
                                    <ChevronRight size={20} className="text-muted opacity-50"/>
                                </div>
                            </div>
                        </div>
                    </div>
                ))}
            </div>

            {/* MODAL LUXURY (Giống mẫu Category) */}
            {showModal && selectedOrder && (
                <div className="modal d-block" style={{ background: "rgba(15, 23, 42, 0.4)", backdropFilter: "blur(20px)", zIndex: 1050 }}>
                    <div className="modal-dialog modal-dialog-centered modal-lg text-start">
                        <div className="modal-content border-0 shadow-none p-4" style={{ borderRadius: "40px", backgroundColor: "#FFFFFF" }}>
                            <div className="modal-body">
                                <div className="d-flex justify-content-between mb-5 align-items-center">
                                    <div className="d-flex align-items-center gap-3">
                                        <div style={{ padding: "18px", backgroundColor: "#0F172A", borderRadius: "22px" }} className="shadow-lg">
                                            <Package color="#FFF" size={26}/>
                                        </div>
                                        <div>
                                            <h2 className="fw-bold m-0 text-dark" style={{ letterSpacing: "-1.5px" }}>Đơn hàng #{selectedOrder.orderId}</h2>
                                            <span className="text-muted small fw-medium">Ngày đặt: {selectedOrder.orderDate}</span>
                                        </div>
                                    </div>
                                    <button onClick={() => setShowModal(false)} className="btn-close bg-light p-3 rounded-circle shadow-none"></button>
                                </div>

                                <div className="row g-4 mb-5 text-start">
                                    <DetailBox icon={<User size={18} className="text-primary"/>} label="Khách hàng" value={selectedOrder.email} />
                                    <DetailBox icon={<MapPin size={18} className="text-danger"/>} label="Địa chỉ" value={selectedOrder.shippingAddress} />
                                    <DetailBox icon={<CreditCard size={18} className="text-success"/>} label="Thanh toán" value={selectedOrder.paymentMethod || "COD"} />
                                </div>

                                <div className="p-4 bg-light rounded-5 mb-5 shadow-inner">
                                    <h6 className="fw-bold mb-4 text-muted small text-uppercase" style={{ letterSpacing: "1px" }}>Sản phẩm đã mua</h6>
                                    {selectedOrder.orderItems?.map((item, i) => (
                                        <div key={i} className="d-flex justify-content-between align-items-center py-3 border-bottom border-white">
                                            <div className="d-flex align-items-center gap-3">
                                                <img 
                                                    src={`http://localhost:8080/api/public/products/image/${item.image}`} 
                                                    alt="" 
                                                    style={{ width: "65px", height: "65px", borderRadius: "20px", objectFit: "cover" }} 
                                                    onError={(e) => e.target.src = 'https://placehold.co/100?text=No+Img'}
                                                />
                                                <div>
                                                    <div className="fw-bold text-dark small">{item.productName}</div>
                                                    <div className="text-muted fw-bold" style={{ fontSize: "11px" }}>x{item.quantity}</div>
                                                </div>
                                            </div>
                                            <div className="fw-bold text-dark">{(item.quantity * item.orderedProductPrice)?.toLocaleString()}đ</div>
                                        </div>
                                    ))}
                                    <div className="d-flex justify-content-between mt-4 align-items-end">
                                        <span className="fw-bold text-muted h6 mb-1">Tổng thanh toán</span>
                                        <span className="fw-bold h2 text-dark m-0">{selectedOrder.totalAmount?.toLocaleString()}đ</span>
                                    </div>
                                </div>

                                <div className="d-flex gap-3">
                                    <ActionBtn bg="#F0F9FF" color="#0369A1" icon={<Truck size={18}/>} label="Vận chuyển" onClick={() => updateStatus(selectedOrder.orderId, 'SHIPPED')} />
                                    <ActionBtn bg="#F0FDF4" color="#15803D" icon={<CheckCircle size={18}/>} label="Hoàn tất" onClick={() => updateStatus(selectedOrder.orderId, 'DELIVERED')} />
                                    <ActionBtn bg="#FEF2F2" color="#B91C1C" icon={<XCircle size={18}/>} label="Hủy đơn" onClick={() => updateStatus(selectedOrder.orderId, 'CANCELLED')} />
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            )}

            <style>{`
                .order-item-card { cursor: pointer; transition: all 0.3s ease; }
                .order-item-card:hover { transform: translateY(-5px); }
                .order-id-circle { 
                    width: 50px; height: 50px; background-color: #F8F9FA; 
                    border-radius: 15px; display: flex; align-items: center; 
                    justify-content: center;
                }
                .rounded-5 { border-radius: 30px !important; }
                .transition-all { transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1); }
            `}</style>
        </div>
    );
};

// Component con giống mẫu Cate
const StatusPill = ({ status }) => {
    const config = {
        'PENDING': { bg: "#FEF3C7", text: "#92400E" },
        'SHIPPED': { bg: "#DBEAFE", text: "#1E40AF" },
        'DELIVERED': { bg: "#D1FAE5", text: "#065F46" },
        'CANCELLED': { bg: "#FEE2E2", text: "#991B1B" }
    };
    const c = config[status] || { bg: "#F3F4F6", text: "#4B5563" };
    return (
        <span className="px-4 py-2 fw-bold rounded-pill shadow-sm" style={{ backgroundColor: c.bg, color: c.text, fontSize: "11px" }}>
            {status}
        </span>
    );
};

const DetailBox = ({ icon, label, value }) => (
    <div className="col-md-4">
        <div className="d-flex align-items-center gap-2 mb-2">
            <div className="p-2 bg-light rounded-3">{icon}</div> 
            <span className="small fw-bold text-muted text-uppercase" style={{ fontSize: "10px", letterSpacing: "1.2px" }}>{label}</span>
        </div>
        <div className="fw-bold text-dark ms-1" style={{ fontSize: "13px" }}>{value || "---"}</div>
    </div>
);

const ActionBtn = ({ bg, color, icon, label, onClick }) => (
    <button className="btn border-0 flex-fill d-flex align-items-center justify-content-center gap-2 py-3 transition-all rounded-4 shadow-sm" 
            style={{ backgroundColor: bg, color: color, fontWeight: 800, fontSize: "14px", borderRadius: "18px" }}
            onClick={onClick}>
        {icon} {label}
    </button>
);

export default OrderListAD;
