import React, { useState, useEffect } from 'react';
import bannerService from '../../services/bannerService';
import productService from "../../services/productService";


const Banner = () => {
  const [banners, setBanners] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchBanners = async () => {
      try {
        setLoading(true);
        // Sử dụng getActiveBanners thay vì getAllBanners
        const activeBanners = await bannerService.getActiveBanners();
        setBanners(activeBanners);
      } catch (err) {
        setError(err.message);
        console.error('Lỗi tải banners:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchBanners();
  }, []);

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;
  if (!banners.length) return <div>No banners found</div>;

  return (
    <div>
      {banners.map(banner => (
        <div key={banner.bannerId}>
          <img src={banner.imageUrl} alt={banner.title} />
          <h3>{banner.title}</h3>
          <p>{banner.description}</p>
        </div>
      ))}
    </div>
  );
};

export default Banner;
