import React, { useState, useEffect } from 'react';
import productService from '../../services/productService';
import ProductCard from '../product/ProductCard'; // Import component dùng chung

const ProductNew = () => {
  const [allProducts, setAllProducts] = useState([]);
  const [loading, setLoading] = useState(false);
  const [showAll, setShowAll] = useState(false);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    setLoading(true);
    try {
      // Lấy 20 sản phẩm mới nhất/bán chạy
      const res = await productService.getAllProducts(0, 20);
      setAllProducts(res.content || []);
    } catch (err) {
      console.error('Lỗi tải sản phẩm:', err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="main-content-wrapper bg-light pb-5">
      <section className="container pt-5">
        {/* Tiêu đề mục */}
        <div className="d-flex justify-content-between align-items-center mb-4 border-start border-4 border-primary ps-3">
          <div>
            <h3 className="fw-bold mb-0 text-uppercase">Sản phẩm cho bạn</h3>
            <p className="text-muted mb-0">Khám phá thế giới mỹ phẩm</p>
          </div>
          {!showAll && (
            <button 
              className="btn btn-primary btn-sm rounded-pill px-4 shadow-sm"
              onClick={() => setShowAll(true)}
            >
              Xem tất cả <i className="fa fa-arrow-right ms-1"></i>
            </button>
          )}
        </div>

        {/* Danh sách sản phẩm */}
        {showAll ? (
          <div className="row animate__animated animate__fadeIn">
            {allProducts.map((p) => (
              // Tận dụng ProductCard có sẵn hàm Add To Cart
              <ProductCard key={p.productId || p.id} product={p} />
            ))}
          </div>
        ) : (
          /* Chế độ trượt ngang (Horizontal Scroll) */
          <div className="d-flex overflow-auto pb-4 no-scrollbar gap-3">
            {allProducts.map((p) => (
              <div key={p.productId || p.id} style={{ minWidth: '280px' }}>
                <ProductCard product={p} />
              </div>
            ))}
          </div>
        )}

        {/* Nút thu gọn */}
        {showAll && (
          <div className="text-center mt-4">
            <button className="btn btn-outline-secondary btn-sm rounded-pill" onClick={() => setShowAll(false)}>
              Thu gọn <i className="fa fa-chevron-up ms-1"></i>
            </button>
          </div>
        )}
      </section>

      <style>{`
        /* ẨN THANH TRƯỢT MÀ VẪN CUỘN ĐƯỢC */
        .no-scrollbar::-webkit-scrollbar {
          display: none;
        }
        .no-scrollbar {
          -ms-overflow-style: none;  /* IE and Edge */
          scrollbar-width: none;  /* Firefox */
          -webkit-overflow-scrolling: touch;
        }
      `}</style>
    </div>
  );
};

export default ProductNew;
