import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import categoryService from '../services/categoryService';

const CategoryHome = () => {
  const [categories, setCategories] = useState([]);
  const [loading, setLoading] = useState(false);
  const [showAll, setShowAll] = useState(false);

  // Danh sách màu sắc nhẹ nhàng (Pastel) cho các danh mục
  const colors = ['#FFD1DC', '#E0BBE4', '#BEE7E8', '#D4F0F0', '#FFDFD3', '#F0E68C'];

  useEffect(() => {
    const fetchCats = async () => {
      setLoading(true);
      const data = await categoryService.getAllCategories();
      setCategories(data);
      setLoading(false);
    };
    fetchCats();
  }, []);

  if (loading && categories.length === 0) return null;

  return (
    <section className="container mt-4 mb-5">
      <div className="d-flex justify-content-between align-items-center mb-3">
        <h5 className="fw-bold text-uppercase mb-0" style={{ letterSpacing: '1px' }}>
          <i className="fa fa-tags me-2 text-primary"></i>Danh mục quan tâm
        </h5>
        <button 
          className="btn btn-link text-decoration-none fw-bold p-0 text-primary" 
          onClick={() => setShowAll(!showAll)}
        >
          {showAll ? 'Thu gọn' : 'Xem tất cả'} 
          <i className={`fa fa-chevron-${showAll ? 'up' : 'right'} ms-1`}></i>
        </button>
      </div>

      {/* HIỂN THỊ TRƯỢT NGANG HOẶC LƯỚI */}
      <div className={showAll ? "row g-3" : "d-flex overflow-auto pb-3 custom-scrollbar"}>
        {categories.map((cat, index) => (
          <div 
            key={cat.categoryId} 
            className={showAll ? "col-lg-2 col-md-3 col-4" : "flex-shrink-0 me-3"}
            style={!showAll ? { width: '110px' } : {}}
          >
            <Link to={`/category/${cat.categoryId}`} className="text-decoration-none">
              <div className="category-card text-center h-100">
                {/* VÒNG TRÒN CHỨA CHỮ CÁI ĐẦU TÊN DANH MỤC */}
                <div 
                  className="icon-circle mb-2 mx-auto d-flex align-items-center justify-content-center shadow-sm"
                  style={{ 
                    width: '70px', 
                    height: '70px', 
                    backgroundColor: colors[index % colors.length],
                    borderRadius: '20px', // Bo góc kiểu hiện đại (Squircle)
                    fontSize: '24px',
                    fontWeight: 'bold',
                    color: '#fff',
                    transition: '0.3s'
                  }}
                >
                  {cat.categoryName.charAt(0).toUpperCase()}
                </div>
                
                <div className="category-name text-dark fw-bold small text-truncate px-1">
                  {cat.categoryName}
                </div>
              </div>
            </Link>
          </div>
        ))}
      </div>

      <style>{`
        .category-card:hover .icon-circle {
          transform: translateY(-8px) rotate(5deg);
          box-shadow: 0 10px 15px rgba(0,0,0,0.1) !important;
        }
        .category-card:hover .category-name {
          color: #0d6efd !important;
        }
        
        /* Tùy chỉnh thanh cuộn ngang */
        .custom-scrollbar::-webkit-scrollbar {
          height: 4px;
        }
        .custom-scrollbar::-webkit-scrollbar-track {
          background: #f8f9fa;
        }
        .custom-scrollbar::-webkit-scrollbar-thumb {
          background: #dee2e6;
          border-radius: 10px;
        }
        .custom-scrollbar {
          scrollbar-width: thin;
          scrollbar-color: #dee2e6 #f8f9fa;
          -webkit-overflow-scrolling: touch;
          display: flex;
          flex-wrap: nowrap;
        }
      `}</style>
    </section>
  );
};

export default CategoryHome;
