import React, { useEffect, useState, useCallback, useMemo } from "react";
import { ShoppingCart, Package, DollarSign, TrendingUp, AlertTriangle, PieChart, RefreshCcw } from "lucide-react";
import {
  Chart as ChartJS, CategoryScale, LinearScale, PointElement, LineElement,
  Title, Tooltip, Legend, Filler, ArcElement
} from "chart.js";
import { Doughnut } from "react-chartjs-2";
import api from "../../services/api";
import { useNavigate } from "react-router-dom";
import { jwtDecode } from "jwt-decode";

ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, ArcElement, Title, Tooltip, Legend, Filler);

const Dashboard = () => {
  const [orders, setOrders] = useState([]);
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  const checkAuth = useCallback(() => {
    const token = localStorage.getItem("admin-token") || localStorage.getItem("token");
    if (!token) return null;
    try {
      const decoded = jwtDecode(token);
      return decoded.role === "ADMIN" ? token : null;
    } catch { return null; }
  }, []);

  const fetchData = useCallback(async () => {
    const token = checkAuth();
    if (!token) { navigate("/login"); return; }
    try {
      setLoading(true);
      const [ordersRes, productsRes] = await Promise.all([
        api.get("/admin/orders?pageSize=1000", { headers: { Authorization: `Bearer ${token}` } }),
        api.get("/admin/products?pageSize=1000", { headers: { Authorization: `Bearer ${token}` } })
      ]);
      setOrders(ordersRes.data.content || []);
      setProducts(productsRes.data.content || []);
    } catch (err) { console.error(err); } finally { setLoading(false); }
  }, [checkAuth, navigate]);

  useEffect(() => { fetchData(); }, [fetchData]);

  // Logic xử lý dữ liệu (useMemo để tránh lag)
  const stats = useMemo(() => ({
    totalRevenue: orders.reduce((sum, o) => o.orderStatus !== 'CANCELLED' ? sum + (o.totalAmount || 0) : sum, 0),
    ordersCount: orders.length,
    lowStockCount: products.filter(p => p.quantity < 10).length
  }), [orders, products]);

  const topSelling = useMemo(() => {
    const map = {};
    orders.forEach(o => {
      if (o.orderStatus !== 'CANCELLED') {
        o.orderItems?.forEach(i => {
          if (!map[i.productId]) map[i.productId] = { name: i.productName, sold: 0, img: i.image };
          map[i.productId].sold += i.quantity;
        });
      }
    });
    return Object.values(map).sort((a, b) => b.sold - a.sold).slice(0, 4);
  }, [orders]);

  const statusChartData = useMemo(() => {
    const counts = { PENDING: 0, SHIPPED: 0, DELIVERED: 0, CANCELLED: 0 };
    orders.forEach(o => counts[o.orderStatus]++);
    return {
      labels: ['Chờ duyệt', 'Đang giao', 'Thành công', 'Đã hủy'],
      datasets: [{
        data: Object.values(counts),
        backgroundColor: ['#f59e0b', '#3b82f6', '#10b981', '#ef4444'],
        hoverOffset: 15,
        borderRadius: 8,
        spacing: 5
      }]
    };
  }, [orders]);

  if (loading) return <div className="d-flex justify-content-center align-items-center vh-100 fw-bold">Đang tải dữ liệu...</div>;

  return (
    <div className="p-4" style={{ backgroundColor: "#F9FAFB", minHeight: "100vh", fontFamily: "'Inter', sans-serif" }}>
      {/* Header */}
      <div className="d-flex justify-content-between align-items-center mb-5">
        <div>
          <h2 className="fw-bold text-dark m-0" style={{ letterSpacing: "-1px" }}>Tổng quan hệ thống</h2>
          <p className="text-muted small m-0">Chào mừng trở lại, đây là những gì đang diễn ra hôm nay.</p>
        </div>
        <button className="btn btn-white border shadow-sm rounded-3 px-3 py-2" onClick={fetchData}>
          <RefreshCcw size={16} className="me-2" /> Làm mới
        </button>
      </div>

      {/* 3 Thẻ thống kê chính - Modern Style */}
      <div className="row g-4 mb-5">
        <ModernStatCard title="Tổng doanh thu" value={`${stats.totalRevenue.toLocaleString()}đ`} icon={<DollarSign size={24}/>} color="#3b82f6" trend="+12.5%" />
        <ModernStatCard title="Đơn hàng mới" value={stats.ordersCount} icon={<ShoppingCart size={24}/>} color="#10b981" trend="+3.2%" />
        <ModernStatCard title="Sản phẩm sắp hết" value={stats.lowStockCount} icon={<Package size={24}/>} color="#f59e0b" trend="Cảnh báo" isWarning={stats.lowStockCount > 0} />
      </div>

      <div className="row g-4">
        {/* Chart Section */}
        <div className="col-lg-4">
          <div className="card border-0 shadow-sm rounded-4 p-4 h-100">
            <h6 className="fw-bold mb-4 d-flex align-items-center"><PieChart size={18} className="me-2 text-primary"/> Phân tích đơn hàng</h6>
            <div style={{ height: "260px" }}>
              <Doughnut data={statusChartData} options={{ maintainAspectRatio: false, plugins: { legend: { position: 'bottom' } } }} />
            </div>
          </div>
        </div>

        {/* Top Selling Section */}
        <div className="col-lg-4">
          <div className="card border-0 shadow-sm rounded-4 p-4 h-100">
            <h6 className="fw-bold mb-4 d-flex align-items-center"><TrendingUp size={18} className="me-2 text-success"/> Bán chạy nhất</h6>
            {topSelling.map((p, i) => (
              <div key={i} className="d-flex align-items-center mb-4 p-2 rounded-3 transition-all hover-bg">
                <img src={`http://localhost:8080/api/public/products/image/${p.img}`} className="rounded-3 shadow-sm me-3" style={{width: "50px", height:"50px", objectFit:"cover"}} />
                <div className="flex-grow-1">
                  <div className="fw-bold small text-dark">{p.name}</div>
                  <div className="text-muted smaller">Đã bán {p.sold} sản phẩm</div>
                </div>
                <div className="fw-bold text-primary">#{i+1}</div>
              </div>
            ))}
          </div>
        </div>

        {/* Inventory Alert Section */}
        <div className="col-lg-4">
          <div className="card border-0 shadow-sm rounded-4 p-4 h-100">
            <h6 className="fw-bold mb-4 d-flex align-items-center text-danger"><AlertTriangle size={18} className="me-2"/> Cần nhập hàng ngay</h6>
            <div className="overflow-auto" style={{maxHeight: "350px"}}>
              {products.filter(p => p.quantity < 10).map(p => (
                <div key={p.productId} className="d-flex justify-content-between align-items-center mb-3 p-3 bg-danger-subtle rounded-3 border border-danger-subtle">
                  <div>
                    <div className="small fw-bold text-danger">{p.productName}</div>
                    <div className="smaller text-danger opacity-75">Kho hiện tại: {p.quantity}</div>
                  </div>
                  <button className="btn btn-danger btn-sm rounded-2 px-3" onClick={() => navigate('/admin/products')}>Nhập</button>
                </div>
              ))}
              {stats.lowStockCount === 0 && <div className="text-center py-5 text-muted small">Mọi thứ đều ổn!</div>}
            </div>
          </div>
        </div>
      </div>

      <style>{`
        .smaller { font-size: 11px; }
        .hover-bg:hover { background-color: #f3f4f6; cursor: pointer; }
        .transition-all { transition: all 0.2s ease-in-out; }
        .rounded-4 { border-radius: 1.25rem !important; }
        .bg-danger-subtle { background-color: #fef2f2 !important; }
      `}</style>
    </div>
  );
};

const ModernStatCard = ({ title, value, icon, color, trend, isWarning }) => (
  <div className="col-md-4">
    <div className="card border-0 shadow-sm rounded-4 p-4 h-100" style={{ borderLeft: `5px solid ${color}` }}>
      <div className="d-flex justify-content-between align-items-center mb-3">
        <div className="p-3 rounded-3" style={{ backgroundColor: `${color}15`, color: color }}>{icon}</div>
        <span className={`badge ${isWarning ? 'bg-danger' : 'bg-light text-success'} rounded-pill px-3`}>{trend}</span>
      </div>
      <p className="text-muted small fw-bold mb-1 text-uppercase" style={{ letterSpacing: "1px" }}>{title}</p>
      <h3 className="fw-bold text-dark m-0">{value}</h3>
    </div>
  </div>
);

export default Dashboard;
