// src/pages/EditProfile.js
import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import userService from "../../services/userService";
import "bootstrap/dist/css/bootstrap.min.css";

const EditProfile = () => {
  const [formData, setFormData] = useState({
    first_name: "",
    last_name: "",
    email: "",
    mobile_number: "",
  });
  const [loading, setLoading] = useState(false);
  const [saving, setSaving] = useState(false);
  const [message, setMessage] = useState({ type: "", text: "" });
  const navigate = useNavigate();

  useEffect(() => {
    const loadProfile = async () => {
      const storedUser = localStorage.getItem("user");
      const storedToken = localStorage.getItem("token");

      if (!storedUser || !storedToken) {
        setMessage({ type: "error", text: "Vui lòng đăng nhập để chỉnh sửa hồ sơ" });
        setTimeout(() => navigate("/login"), 1500);
        return;
      }

      try {
        const parsedUser = JSON.parse(storedUser);
        const userId = parsedUser.userId || parsedUser.user_id;

        setLoading(true);
        const userData = await userService.getUserProfile(userId);
        setFormData({
          first_name: userData.first_name || "",
          last_name: userData.last_name || "",
          email: userData.email || "",
          mobile_number: userData.mobile_number || "",
        });
      } catch (err) {
        console.error("❌ Lỗi khi tải thông tin người dùng:", err);
        setMessage({ type: "error", text: "Không thể tải thông tin hồ sơ." });
      } finally {
        setLoading(false);
      }
    };

    loadProfile();
  }, [navigate]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setSaving(true);
    const storedUser = localStorage.getItem("user");
    const parsedUser = JSON.parse(storedUser);
    const userId = parsedUser.userId || parsedUser.user_id;

    try {
      const updated = await userService.updateUser(userId, formData);
      setMessage({ type: "success", text: "Cập nhật hồ sơ thành công!" });

      // ✅ Cập nhật lại localStorage để Header hiển thị tên mới
      const updatedUser = { ...parsedUser, ...formData };
      localStorage.setItem("user", JSON.stringify(updatedUser));

      setTimeout(() => navigate(-1), 1500);
    } catch (error) {
      console.error("❌ Cập nhật thất bại:", error);
      setMessage({ type: "error", text: "Không thể cập nhật hồ sơ." });
    } finally {
      setSaving(false);
    }
  };

  if (loading) {
    return (
      <div className="container py-5 text-center">
        <div className="spinner-border text-primary mb-3"></div>
        <p>Đang tải thông tin người dùng...</p>
      </div>
    );
  }

  return (
    <div className="container mt-4">
      <h2 className="text-primary mb-4">
        <i className="fa fa-user-edit me-2"></i>Chỉnh sửa hồ sơ
      </h2>

      {message.text && (
        <div
          className={`alert ${
            message.type === "success"
              ? "alert-success"
              : message.type === "error"
              ? "alert-danger"
              : "alert-warning"
          }`}
        >
          {message.text}
        </div>
      )}

      <form onSubmit={handleSubmit}>
        <div className="mb-3">
          <label className="form-label fw-semibold">Họ</label>
          <input
            type="text"
            name="first_name"
            className="form-control"
            value={formData.first_name}
            onChange={handleChange}
            disabled={saving}
            required
          />
        </div>

        <div className="mb-3">
          <label className="form-label fw-semibold">Tên</label>
          <input
            type="text"
            name="last_name"
            className="form-control"
            value={formData.last_name}
            onChange={handleChange}
            disabled={saving}
            required
          />
        </div>

        <div className="mb-3">
          <label className="form-label fw-semibold">Email</label>
          <input
            type="email"
            name="email"
            className="form-control"
            value={formData.email}
            onChange={handleChange}
            disabled={saving}
            required
          />
        </div>

        <div className="mb-3">
          <label className="form-label fw-semibold">Số điện thoại</label>
          <input
            type="tel"
            name="mobile_number"
            className="form-control"
            value={formData.mobile_number}
            onChange={handleChange}
            disabled={saving}
          />
        </div>

        <div className="d-flex justify-content-end">
          <button
            type="button"
            className="btn btn-outline-secondary me-2"
            onClick={() => navigate(-1)}
            disabled={saving}
          >
            Hủy
          </button>
          <button type="submit" className="btn btn-primary" disabled={saving}>
            {saving ? (
              <>
                <span className="spinner-border spinner-border-sm me-2"></span>Đang lưu...
              </>
            ) : (
              <>
                <i className="fa fa-save me-2"></i>Lưu thay đổi
              </>
            )}
          </button>
        </div>
      </form>
    </div>
  );
};

export default EditProfile;
