import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import authService from "../../services/authService";

const Register = () => {
  const navigate = useNavigate();
  const [form, setForm] = useState({
    first_name: "",
    last_name: "",
    email: "",
    mobile_number: "",
    password: "",
    confirmPassword: "",
    street: "",
    city: "",
    state: "",
    country: "",
    pincode: "",
    buildingName: ""
  });

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (form.password !== form.confirmPassword) {
      alert("‚ùå M·∫≠t kh·∫©u kh√¥ng kh·ªõp!");
      return;
    }

    try {
      // ‚úÖ Chu·∫©n h√≥a d·ªØ li·ªáu theo API expected format
      const payload = {
        firstName: form.first_name,
        lastName: form.last_name,
        email: form.email,
        password: form.password,
        mobileNumber: form.mobile_number,
        roles: [
          {
            roleName: "USER"
          }
        ],
        address: {
          street: form.street || "Default Street",
          city: form.city || "Hanoi",
          state: form.state || "HN",
          country: form.country || "Vietnam",
          pincode: form.pincode || "100000",
          buildingName: form.buildingName || "Building A"
        },
        cart: {
          email: form.email, // Th√™m email v√†o cart
          products: [],
          totalPrice: 0
        }
      };

      console.log("üì§ Payload g·ª≠i l√™n:", payload);

      const res = await authService.register(payload);
      console.log("‚úÖ REGISTER RESULT:", res.data);

      alert("‚úÖ ƒêƒÉng k√Ω th√†nh c√¥ng!");
      navigate("/login");
    } catch (error) {
      console.error("‚ùå REGISTER ERROR:", error.response?.data || error.message);

      let errorMessage = "ƒêƒÉng k√Ω th·∫•t b·∫°i, vui l√≤ng ki·ªÉm tra l·∫°i!";

      if (error.response?.status === 400) {
        errorMessage = "D·ªØ li·ªáu kh√¥ng h·ª£p l·ªá: " + (error.response.data?.message || "Vui l√≤ng ki·ªÉm tra l·∫°i th√¥ng tin");
      } else if (error.response?.status === 409) {
        errorMessage = "Email ƒë√£ t·ªìn t·∫°i!";
      } else if (error.response?.data?.message) {
        errorMessage = error.response.data.message;
      }

      alert(`‚ùå ${errorMessage}`);
    }
  };

  return (
    <section className="section-content padding-y">
      <div
        className="card mx-auto shadow"
        style={{ maxWidth: "600px", marginTop: "40px" }}
      >
        <article className="card-body">
          <header className="mb-4 text-center">
            <h4 className="card-title">ƒêƒÉng k√Ω t√†i kho·∫£n</h4>
          </header>

          <form onSubmit={handleSubmit}>
            {/* Th√¥ng tin c√° nh√¢n */}
            <div className="form-row">
              <div className="col form-group">
                <label>H·ªç *</label>
                <input
                  name="first_name"
                  type="text"
                  className="form-control"
                  value={form.first_name}
                  onChange={handleChange}
                  required
                  placeholder="Nh·∫≠p h·ªç c·ªßa b·∫°n"
                />
              </div>
              <div className="col form-group">
                <label>T√™n *</label>
                <input
                  name="last_name"
                  type="text"
                  className="form-control"
                  value={form.last_name}
                  onChange={handleChange}
                  required
                  placeholder="Nh·∫≠p t√™n c·ªßa b·∫°n"
                />
              </div>
            </div>

            {/* Email */}
            <div className="form-group">
              <label>Email *</label>
              <input
                name="email"
                type="email"
                className="form-control"
                value={form.email}
                onChange={handleChange}
                required
                placeholder="example@gmail.com"
              />
            </div>

            {/* S·ªë ƒëi·ªán tho·∫°i */}
            <div className="form-group">
              <label>S·ªë ƒëi·ªán tho·∫°i</label>
              <input
                name="mobile_number"
                type="text"
                className="form-control"
                value={form.mobile_number}
                onChange={handleChange}
                placeholder="0987654321"
              />
            </div>

            {/* M·∫≠t kh·∫©u */}
            <div className="form-row">
              <div className="form-group col-md-6">
                <label>M·∫≠t kh·∫©u *</label>
                <input
                  name="password"
                  type="password"
                  className="form-control"
                  value={form.password}
                  onChange={handleChange}
