import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import categoryService from '../services/categoryService';

const CategoryHome = () => {
  const [categories, setCategories] = useState([]);
  const [loading, setLoading] = useState(false);
  const [showAll, setShowAll] = useState(false);

  useEffect(() => {
    const fetchCats = async () => {
      setLoading(true);
      const data = await categoryService.getAllCategories();
      setCategories(data);
      setLoading(false);
    };
    fetchCats();
  }, []);

  if (loading && categories.length === 0) return null;

  return (
    <section className="container mt-4 mb-5">
      <div className="d-flex justify-content-between align-items-center mb-3">
        <h5 className="fw-bold text-uppercase mb-0">Danh mục quan tâm</h5>
        <button 
          className="btn btn-link text-decoration-none fw-bold p-0" 
          onClick={() => setShowAll(!showAll)}
          style={{ color: '#fd7e14' }}
        >
          {showAll ? 'Thu gọn' : 'Xem tất cả'} <i className={`fa fa-chevron-${showAll ? 'up' : 'right'} ms-1`}></i>
        </button>
      </div>

      {/* HIỂN THỊ DẠNG TRƯỢT NGANG HOẶC LƯỚI */}
      <div className={showAll ? "row g-3" : "d-flex overflow-auto pb-2 custom-scrollbar"}>
        {categories.map((cat) => (
          <div 
            key={cat.categoryId} 
            className={showAll ? "col-lg-2 col-md-3 col-4" : "flex-shrink-0 me-3"}
            style={!showAll ? { width: '120px' } : {}}
          >
            <Link to={`/category/${cat.categoryId}`} className="text-decoration-none">
              <div className="category-item text-center p-3 shadow-sm bg-white rounded-4 border hover-up h-100">
                <div className="icon-circle mb-2 mx-auto d-flex align-items-center justify-content-center bg-light rounded-circle" style={{ width: '65px', height: '65px' }}>
                  <img 
                    src={cat.image || "https://cdn-icons-png.flaticon.com/512/3524/3524335.png"} 
                    alt={cat.categoryName}
                    className="img-fluid"
                    style={{ width: '40px', height: '40px', objectFit: 'contain' }}
                    onError={(e) => (e.target.src = "https://cdn-icons-png.flaticon.com/512/3524/3524335.png")}
                  />
                </div>
                <div className="category-name text-dark fw-bold small text-truncate">
                  {cat.categoryName}
                </div>
              </div>
            </Link>
          </div>
        ))}
      </div>

      <style>{`
        .hover-up { transition: all 0.3s ease; }
        .hover-up:hover { 
          transform: translateY(-5px); 
          border-color: #fd7e14 !important;
          box-shadow: 0 5px 15px rgba(253, 126, 20, 0.1) !important;
        }
        
        /* Tùy chỉnh thanh cuộn ngang */
        .custom-scrollbar::-webkit-scrollbar {
          height: 4px;
        }
        .custom-scrollbar::-webkit-scrollbar-track {
          background: #eee;
        }
        .custom-scrollbar::-webkit-scrollbar-thumb {
          background: #fd7e14;
          border-radius: 10px;
        }
        .custom-scrollbar {
          scrollbar-width: thin;
          scrollbar-color: #fd7e14 #eee;
          -webkit-overflow-scrolling: touch;
        }
      `}</style>
    </section>
  );
};

export default CategoryHome;
