import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';

const GeminiChat = () => {
    const [isOpen, setIsOpen] = useState(false);
    const [messages, setMessages] = useState([
        { role: 'ai', text: 'Há»‡ thá»‘ng Ä‘Ã£ sáºµn sÃ ng. NÃ ng cáº§n tá»‘i Æ°u váº» Ä‘áº¹p hÃ´m nay chá»©? âš¡' }
    ]);
    const [input, setInput] = useState('');
    const [loading, setLoading] = useState(false);
    const scrollRef = useRef(null);

    useEffect(() => {
        if (scrollRef.current) scrollRef.current.scrollIntoView({ behavior: 'smooth' });
    }, [messages, loading]);

    const handleSend = async () => {
        if (!input.trim() || loading) return;
        const userMsg = input.trim();
        setMessages(prev => [...prev, { role: 'user', text: userMsg }]);
        setInput('');
        setLoading(true);

        try {
            const res = await axios.post('http://localhost:8080/api/ai/ask', { message: userMsg });
            const { reply, suggested_products } = res.data;
            setMessages(prev => [...prev, { 
                role: 'ai', 
                text: reply || 'Data received. Äang xá»­ lÃ½...',
                suggestions: suggested_products || [] 
            }]);
        } catch (error) {
            setMessages(prev => [...prev, { role: 'ai', text: 'Error 404: Káº¿t ná»‘i bá»‹ ngáº¯t quÃ£ng rá»“i nÃ ng Æ¡i! ðŸ“¡' }]);
        } finally { setLoading(false); }
    };

    return (
        <div className="cyber-chat-wrapper">
            {/* NÃºt Toggle Floating Glow */}
            <button onClick={() => setIsOpen(!isOpen)} className={`cyber-toggle ${isOpen ? 'active' : ''}`}>
                <div className="glow-effect"></div>
                <i className={isOpen ? "bi bi-cpu-fill" : "bi bi-command"}></i>
            </button>

            {/* Cá»­a sá»• Chat Cyber-Glass */}
            {isOpen && (
                <div className="cyber-window shadow-lg">
                    <div className="cyber-header">
                        <div className="d-flex align-items-center gap-2">
                            <div className="neon-pulse"></div>
                            <span className="cyber-title text-uppercase">Beauty Core v1.0</span>
                        </div>
                        <div className="header-actions">
                            <span className="battery-icon mr-2">100%</span>
                            <i className="bi bi-x-lg" onClick={() => setIsOpen(false)} style={{cursor: 'pointer'}}></i>
                        </div>
                    </div>

                    <div className="cyber-body hide-scrollbar">
                        {messages.map((msg, idx) => (
                            <div key={idx} className={`cyber-msg ${msg.role}`}>
                                <div className="bubble-wrap">
                                    <div className="bubble-content">{msg.text}</div>
                                </div>
                                
                                {msg.suggestions?.length > 0 && (
                                    <div className="cyber-shelf hide-scrollbar">
                                        {msg.suggestions.map((p, i) => (
                                            <div key={i} className="cyber-card">
                                                <div className="card-scanner"></div>
                                                <img src={`http://localhost:8080/api/public/products/image/${p.image}`} alt="" />
                                                <div className="card-info">
                                                    <div className="name">{p.productName}</div>
                                                    <a href={`/product/${p.productId}`} className="cyber-btn-sm">
                                                        LINK <i className="bi bi-arrow-right"></i>
                                                    </a>
                                                </div>
                                            </div>
                                        ))}
                                    </div>
                                )}
                            </div>
                        ))}
                        {loading && <div className="cyber-loader"><span></span><span></span><span></span></div>}
                        <div ref={scrollRef} />
                    </div>

                    <div className="cyber-footer">
                        <div className="input-wrap">
                            <input 
                                type="text" 
                                placeholder="Command here..." 
                                value={input} 
                                onChange={(e) => setInput(e.target.value)}
                                onKeyDown={(e) => e.key === 'Enter' && handleSend()}
                            />
                            <button onClick={handleSend} disabled={loading} className="send-btn">
                                <i className="bi bi-lightning-charge-fill"></i>
                            </button>
                        </div>
                    </div>
                </div>
            )}

            <style>{`
                @import url('https://fonts.googleapis.com/css2?family=Orbitron:wght@400;700&family=Rajdhani:wght@400;600&display=swap');

                .cyber-chat-wrapper { position: fixed; bottom: 30px; right: 30px; z-index: 9999; font-family: 'Rajdhani', sans-serif; }

                /* Toggle Button Glow */
                .cyber-toggle {
                    width: 60px; height: 60px; border-radius: 18px; border: none;
                    background: #000; color: #00f2ff; position: relative;
                    font-size: 24px; transition: 0.3s; overflow: hidden;
                }
                .cyber-toggle.active { color: #ff00ff; box-shadow: 0 0 20px #ff00ff; }
                .glow-effect { position: absolute; inset: 0; background: linear-gradient(45deg, #00f2ff, #ff00ff); opacity: 0.2; }

                /* Cá»­a sá»• Chat TÆ°Æ¡ng Lai */
                .cyber-window {
                    position: absolute; bottom: 80px; right: 0; width: 350px; height: 550px;
                    background: rgba(10, 10, 15, 0.85); backdrop-filter: blur(25px);
                    border: 1px solid rgba(0, 242, 255, 0.3); border-radius: 20px;
                    display: flex; flex-direction: column; overflow: hidden;
                    animation: powerOn 0.5s ease-out;
                }
                @keyframes powerOn {
                    from { opacity: 0; height: 0; transform: skewX(10deg); }
                    to { opacity: 1; height: 550px; transform: skewX(0); }
                }

                .cyber-header { padding: 15px 20px; background: rgba(0, 242, 255, 0.1); display: flex; justify-content: space-between; align-items: center; border-bottom: 1px solid rgba(0, 242, 255, 0.2); }
                .cyber-title { font-family: 'Orbitron', sans-serif; font-size: 12px; color: #00f2ff; letter-spacing: 2px; }
                .neon-pulse { width: 8px; height: 8px; background: #00f2ff; border-radius: 50%; box-shadow: 0 0 10px #00f2ff; animation: pulse 1.5s infinite; }
                @keyframes pulse { 0% { transform: scale(1); opacity: 1; } 100% { transform: scale(2.5); opacity: 0; } }

                .cyber-body { flex: 1; padding: 20px; overflow-y: auto; }
                .cyber-msg { margin-bottom: 20px; display: flex; flex-direction: column; }
                .bubble-wrap { position: relative; max-width: 85%; }
                .ai .bubble-wrap::before { content: ''; position: absolute; left: -5px; top: 0; height: 100%; width: 2px; background: #00f2ff; }
                .bubble-content { padding: 10px 15px; font-size: 15px; color: #fff; background: rgba(255, 255, 255, 0.05); border-radius: 0 12px 12px 12px; }
                .user { align-items: flex-end; }
                .user .bubble-content { background: #00f2ff; color: #000; border-radius: 12px 12px 0 12px; font-weight: 600; }

                /* Product Card Hologram */
                .cyber-shelf { display: flex; gap: 12px; margin-top: 10px; overflow-x: auto; padding: 5px; }
                .cyber-card { 
                    min-width: 140px; background: rgba(255,255,255,0.05); border-radius: 12px; 
                    border: 1px solid rgba(255,255,255,0.1); position: relative; overflow: hidden;
                }
                .card-scanner { position: absolute; top: 0; left: 0; width: 100%; height: 2px; background: #00f2ff; animation: scan 2s infinite linear; }
                @keyframes scan { from { top: 0; } to { top: 100%; } }
                .cyber-card img { width: 100%; height: 100px; object-fit: cover; opacity: 0.7; }
                .card-info { padding: 8px; }
                .name { font-size: 11px; color: #aaa; white-space: nowrap; overflow: hidden; text-overflow: ellipsis; }
                .cyber-btn-sm { font-size: 10px; color: #00f2ff; text-decoration: none; font-weight: bold; }

                /* Input Bar */
                .cyber-footer { padding: 20px; }
                .input-wrap { display: flex; background: rgba(255,255,255,0.05); border-radius: 12px; border: 1px solid rgba(0, 242, 255, 0.2); padding: 5px; }
                .input-wrap input { flex: 1; background: none; border: none; color: #fff; padding: 10px; outline: none; font-size: 14px; }
                .send-btn { width: 40px; height: 40px; border-radius: 8px; border: none; background: #00f2ff; color: #000; transition: 0.3s; }
                .send-btn:hover { background: #ff00ff; color: #fff; transform: scale(1.1); }

                .cyber-loader span { display: inline-block; width: 6px; height: 6px; background: #00f2ff; margin: 0 3px; animation: bounce 0.6s infinite alternate; }
                @keyframes bounce { to { transform: translateY(-5px); opacity: 0.3; } }
                .hide-scrollbar::-webkit-scrollbar { display: none; }
            `}</style>
        </div>
    );
};

export default GeminiChat;
