import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import authService from "../../services/authService";
import userService from "../../services/userService";

const Login = () => {
  const navigate = useNavigate();
  const [form, setForm] = useState({
    email: "",
    password: "",
  });

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await authService.login(form);
      console.log("‚úÖ LOGIN RESULT:", res.data);

      const loginData = res.data;

      let userId;
      let userToStore = {};

      // ‚úÖ T√¨m userId t·ª´ response
      if (loginData.user_id) {
        userId = loginData.user_id;
      } else if (loginData.userId) {
        userId = loginData.userId;
      } else if (loginData.user && loginData.user.user_id) {
        userId = loginData.user.user_id;
      } else if (loginData.user && loginData.user.userId) {
        userId = loginData.user.userId;
      }

      if (userId) {
        // ‚úÖ G·ªåI API ƒê·ªÇ L·∫§Y TH√îNG TIN USER ƒê·∫¶Y ƒê·ª¶
        try {
          console.log("üîÑ Fetching user profile for userId:", userId);
          const userProfile = await userService.getUserProfile(userId);
          console.log("‚úÖ User profile received:", userProfile);

          userToStore = {
            userId: userId,
            email: userProfile.email || form.email,
            first_name: userProfile.firstName || userProfile.first_name || "",
            last_name: userProfile.lastName || userProfile.last_name || ""
          };

        } catch (profileError) {
          console.error("‚ùå Error fetching user profile:", profileError);
          // Fallback: ch·ªâ l∆∞u userId v√† email
          userToStore = {
            userId: userId,
            email: form.email,
            first_name: "",
            last_name: ""
          };
        }
      } else {
        // ‚úÖ N·∫øu kh√¥ng c√≥ userId, t·∫°o t·∫°m
        console.warn("‚ö†Ô∏è No userId in login response");
        userToStore = {
          userId: `temp_${Date.now()}`,
          email: form.email,
          first_name: "",
          last_name: ""
        };
      }

      // ‚úÖ L∆∞u v√†o localStorage
      console.log("üíæ Saving user to localStorage:", userToStore);
      localStorage.setItem("user", JSON.stringify(userToStore));

      // ‚úÖ L∆∞u token n·∫øu c√≥
      if (loginData.token) {
        localStorage.setItem("token", loginData.token);
      }

      alert("‚úÖ ƒêƒÉng nh·∫≠p th√†nh c√¥ng!");
      window.location.href = "/"; // ‚úÖ Reload ƒë·ªÉ c·∫≠p nh·∫≠t Header

    } catch (err) {
      console.error("‚ùå LOGIN ERROR:", err);
      alert("‚ùå ƒêƒÉng nh·∫≠p th·∫•t b·∫°i! Vui l√≤ng ki·ªÉm tra email v√† m·∫≠t kh·∫©u.");
    }
  };

  return (
    <div className="card mx-auto" style={{ maxWidth: "380px", marginTop: "100px" }}>
      <div className="card-body">
        <h4 className="card-title mb-4">Sign in</h4>

        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <input
              name="email"
              className="form-control"
              placeholder="Email"
              type="email"
              value={form.email}
              onChange={handleChange}
              required
            />
          </div>

          <div className="form-group">
            <input
              name="password"
              className="form-control"
              placeholder="Password"
              type="password"
              value={form.password}
              onChange={handleChange}
              required
            />
          </div>

          <button type="submit" className="btn btn-primary btn-block">
            Login
          </button>
        </form>
      </div>
    </div>
  );
};

export default Login;
