import React, { useState, useEffect } from 'react';
import axios from 'axios';

const BannerThree = () => {
  const [banner, setBanner] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchBanner();
  }, []);

  const fetchBanner = async () => {
    try {
      // Lấy banner ID 7 trực tiếp (hoặc ID bạn muốn cho BannerThree)
      const response = await axios.get('http://localhost:8080/api/public/banners/7');
      setBanner(response.data);
    } catch (err) {
      console.error('Lỗi lấy banner:', err);
      // Nếu ID 7 không tồn tại, lấy banner thứ 2
      try {
        const allResponse = await axios.get('http://localhost:8080/api/public/banners?size=2');
        if (allResponse.data?.content?.length > 1) {
          setBanner(allResponse.data.content[1]);
        } else if (allResponse.data?.content?.length > 0) {
          setBanner(allResponse.data.content[0]);
        }
      } catch (fallbackErr) {
        console.error('Lỗi lấy banner fallback:', fallbackErr);
      }
    } finally {
      setLoading(false);
    }
  };

  const getImageUrl = () => {
    if (!banner?.imageUrl) return 'https://via.placeholder.com/600x400/2d3748/ffffff?text=Electronics+Bundle';
    return `http://localhost:8080/api/public/banners/image/${banner.imageUrl}`;
  };

  if (loading) {
    return (
      <section className="banner-three-section mb-5">
        <div className="container">
          <div className="row">
            <div className="col-md-8">
              <div className="placeholder-glow">
                <div className="placeholder rounded-3" style={{ height: '400px' }}></div>
              </div>
            </div>
            <div className="col-md-4">
              <div className="placeholder-glow">
                <div className="placeholder rounded-3" style={{ height: '400px' }}></div>
              </div>
            </div>
          </div>
        </div>
      </section>
    );
  }

  if (!banner) {
    return (
      <section className="banner-three-section mb-5">
        <div className="container">
          <div className="row align-items-center">
            {/* Main Banner Content */}
            <div className="col-md-8 mb-4 mb-md-0">
              <div 
                className="rounded-3 p-5 text-white position-relative overflow-hidden"
                style={{
                  background: 'linear-gradient(135deg, #4A5568 0%, #2D3748 100%)',
                  height: '400px',
                  display: 'flex',
                  flexDirection: 'column',
                  justifyContent: 'center'
                }}
              >
                <h1 className="display-5 fw-bold mb-3">Combo Thiết Bị Thông Minh</h1>
                <p className="lead mb-4">
                  Trải nghiệm trọn bộ thiết bị công nghệ cao cấp. Từ smartphone flagship đến laptop gaming và phụ kiện chính hãng - tất cả trong một gói ưu đãi duy nhất.
                </p>
                <div className="mb-4">
                  <div className="bg-warning" style={{ width: '50px', height: '2px' }}></div>
                </div>
                <button className="btn btn-warning btn-lg px-4 align-self-start">
                  Khám Phá Combo
                </button>
              </div>
            </div>

            {/* Side Banner */}
            <div className="col-md-4">
              <div 
                className="rounded-3 p-4 text-white h-100"
                style={{
                  background: 'linear-gradient(135deg, #E53E3E 0%, #C53030 100%)',
                  height: '400px',
                  display: 'flex',
                  flexDirection: 'column',
                  justifyContent: 'center'
                }}
              >
                <h3 className="fw-bold mb-3">Ưu Đãi Đặc Biệt</h3>
                <p className="mb-4">
                  Giảm giá lên đến 40% cho các sản phẩm công nghệ mới nhất. Thời gian ưu đãi có hạn!
                </p>
                <button className="btn btn-light btn-sm px-3 align-self-start">
                  Mua Ngay
                </button>
              </div>
            </div>
          </div>
        </div>
      </section>
    );
  }

  return (
    <section className="banner-three-section mb-5">
      <div className="container">
        <div className="row align-items-center">
          {/* Main Banner Content */}
          <div className="col-md-8 mb-4 mb-md-0">
            <div 
              className="rounded-3 p-5 text-white position-relative overflow-hidden"
              style={{
                backgroundImage: `url(${getImageUrl()})`,
                backgroundSize: 'cover',
                backgroundPosition: 'center',
                height: '400px',
                display: 'flex',
                flexDirection: 'column',
                justifyContent: 'center'
              }}
            >
              {/* Overlay */}
              <div 
                className="position-absolute top-0 start-0 w-100 h-100"
                style={{
                  background: 'linear-gradient(90deg, rgba(0,0,0,0.7) 0%, rgba(0,0,0,0.4) 100%)'
                }}
              ></div>

              {/* Content */}
              <div className="position-relative">
                <h1 className="display-5 fw-bold mb-3">
                  {banner.title || "Combo Thiết Bị Thông Minh"}
                </h1>
                <p className="lead mb-4">
                  {banner.description || "Trải nghiệm trọn bộ thiết bị công nghệ cao cấp. Từ smartphone flagship đến laptop gaming và phụ kiện chính hãng - tất cả trong một gói ưu đãi duy nhất."}
                </p>
                <div className="mb-4">
                  <div className="bg-warning" style={{ width: '50px', height: '2px' }}></div>
                </div>
                <button className="btn btn-warning btn-lg px-4">
                  {banner.buttonText || "Khám Phá Combo"}
                </button>
              </div>
            </div>
          </div>

          {/* Side Banner */}
          <div className="col-md-4">
            <div 
              className="rounded-3 p-4 text-white h-100"
              style={{
                background: 'linear-gradient(135deg, #E53E3E 0%, #C53030 100%)',
                height: '400px',
                display: 'flex',
                flexDirection: 'column',
                justifyContent: 'center'
              }}
            >
              <h3 className="fw-bold mb-3">Bundle Công Nghệ</h3>
              <p className="mb-4">
                Tiết kiệm đến 1.5 triệu khi mua combo smartphone + tai nghe + sạc dự phòng. Chất lượng đảm bảo, giá trị vượt trội.
              </p>
              <div className="mb-3">
                <div className="bg-light" style={{ width: '40px', height: '2px' }}></div>
              </div>
              <button className="btn btn-light btn-sm px-3">
                Xem Bundle
              </button>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default BannerThree;
