import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import orderService from '../../services/orderService'; // Import service ƒë∆°n h√†ng

const ProductMostBuy = () => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const IMAGE_BASE_URL = "http://localhost:8080/api/public/products/image";

  useEffect(() => {
    calculateMostBought();
  }, []);

  const calculateMostBought = async () => {
    try {
      setLoading(true);
      setError(null);

      // 1. G·ªçi API l·∫•y danh s√°ch ƒë∆°n h√†ng (gi·∫£ s·ª≠ d√πng getAllOrders)
      const response = await orderService.getAllOrders(); 
      const orders = response.content || response || [];

      // 2. Thu·∫≠t to√°n gom nh√≥m v√† t√≠nh t·ªïng s·ªë l∆∞·ª£ng s·∫£n ph·∫©m ƒë√£ b√°n
      const productStats = {};

      orders.forEach(order => {
        // Duy·ªát qua t·ª´ng item trong ƒë∆°n h√†ng (orderItems)
        order.orderItems?.forEach(item => {
          const p = item.product;
          if (!p) return;

          const pId = p.productId;
          if (productStats[pId]) {
            productStats[pId].totalBought += item.quantity;
          } else {
            productStats[pId] = {
              ...p,
              totalBought: item.quantity
            };
          }
        });
      });

      // 3. Chuy·ªÉn object th√†nh array v√† s·∫Øp x·∫øp gi·∫£m d·∫ßn theo totalBought
      const sortedProducts = Object.values(productStats)
        .sort((a, b) => b.totalBought - a.totalBought)
        .slice(0, 8); // L·∫•y top 8 s·∫£n ph·∫©m b√°n ch·∫°y nh·∫•t

      setProducts(sortedProducts);
    } catch (err) {
      console.error('‚ùå L·ªói khi t√≠nh to√°n s·∫£n ph·∫©m b√°n ch·∫°y:', err);
      setError('Kh√¥ng th·ªÉ th·ªëng k√™ s·∫£n ph·∫©m b√°n ch·∫°y');
    } finally {
      setLoading(false);
    }
  };

  // --- Render ph·∫ßn Loading v√† Error t∆∞∆°ng t·ª± nh∆∞ c≈©, ch·ªâ ƒë·ªïi text ---
  if (loading) return <div className="container mt-5 text-center p-5"><div className="spinner-border text-warning"></div><p>ƒêang th·ªëng k√™ h√†ng b√°n ch·∫°y...</p></div>;

  return (
    <section className="padding-bottom container mt-5">
      <header className="section-heading mb-4 text-center">
        <h3 className="title-section text-uppercase fw-bold text-warning">
          üî• S·∫£n ph·∫©m b√°n ch·∫°y nh·∫•t
        </h3>
        <p className="text-muted">Nh·ªØng m·∫∑t h√†ng ƒë∆∞·ª£c kh√°ch h√†ng tin d√πng nh·∫•t</p>
      </header>

      <div className="row">
        {products.map((p, index) => {
          const productId = p.productId;

          return (
            <div key={productId} className="col-xl-3 col-lg-4 col-md-6 col-12 mb-4">
              <div className="card card-product-grid shadow-sm h-100 border-0 transition-all product-card">

                {/* Badge Th·ª© h·∫°ng */}
                <div className="position-absolute top-0 start-0 m-2 badge bg-danger shadow" style={{ zIndex: 10 }}>
                   Top {index + 1}
                </div>

                {/* Badge T·ªïng ƒë√£ b√°n */}
                <div className="position-absolute top-0 end-0 m-2 badge bg-dark opacity-75" style={{ zIndex: 10 }}>
                   ƒê√£ b√°n {p.totalBought}
                </div>

                <div className="img-wrap p-2">
                  <Link to={`/product/${productId}`}>
                    <img
                      src={`${IMAGE_BASE_URL}/${p.image}`}
                      alt={p.productName}
                      style={{ width: "100%", height: "250px", objectFit: "cover", borderRadius: "12px" }}
                      onError={(e) => (e.target.src = "https://via.placeholder.com/250")}
                    />
                  </Link>
                </div>

                <div className="info-wrap p-3">
                  <Link to={`/product/${productId}`} className="text-decoration-none">
                    <h6 className="text-truncate text-dark fw-bold mb-1">{p.productName}</h6>
                  </Link>

                  <div className="price-wrap mb-2">
                    <span className="price h5 text-danger">{p.specialPrice?.toLocaleString() || p.price?.toLocaleString()} ‚Ç´</span>
                  </div>

                  <div className="d-grid mt-2">
                    <Link to={`/product/${productId}`} className="btn btn-outline-warning btn-sm rounded-pill fw-bold">
                      Xem chi ti·∫øt
                    </Link>
                  </div>
                </div>
              </div>
            </div>
          );
        })}
      </div>

      {products.length === 0 && !loading && (
        <div className="text-center py-5 bg-light rounded-4">
           <h4>Ch∆∞a c√≥ d·ªØ li·ªáu b√°n h√†ng üòÖ</h4>
           <p className="text-muted">S·∫£n ph·∫©m s·∫Ω hi·ªán ra khi c√≥ ƒë∆°n h√†ng th√†nh c√¥ng.</p>
        </div>
      )}

      <style>{`
        .product-card:hover {
          transform: translateY(-5px);
          box-shadow: 0 10px 20px rgba(0,0,0,0.1) !important;
        }
      `}</style>
    </section>
  );
};

export default ProductMostBuy;
