import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import authService from "../../services/authService";
import userService from "../../services/userService";

const Login = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    email: "",
    password: "",
    remember: true,
  });
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState("");
  const [messageType, setMessageType] = useState(""); // "success" or "error"

  const showMessage = (text, type) => {
    setMessage(text);
    setMessageType(type);
    setTimeout(() => {
      setMessage("");
      setMessageType("");
    }, 3000);
  };

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: type === "checkbox" ? checked : value,
    }));
  };

  const validate = () => {
    const { email, password } = formData;
    const errors = [];

    if (!email || !password) {
      errors.push("Vui l√≤ng nh·∫≠p ƒë·∫ßy ƒë·ªß th√¥ng tin.");
    }

    if (email && !/\S+@\S+\.\S+/.test(email)) {
      errors.push("Email kh√¥ng h·ª£p l·ªá.");
    }

    return errors.length > 0 ? errors : null;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Validate form
    const errors = validate();
    if (errors) {
      showMessage(errors[0], "error");
      return;
    }

    setLoading(true);
    setMessage("");

    try {
      console.log("üîê Attempting login with:", { email: formData.email });

      const res = await authService.login({
        email: formData.email,
        password: formData.password
      });

      console.log("‚úÖ LOGIN SUCCESS - Full response:", res.data);
      const loginData = res.data;

      // ‚úÖ L·∫§Y TOKEN T·ª™ RESPONSE (theo API c·ªßa b·∫°n)
      const token = loginData['jwt-token'];

      if (!token) {
        throw new Error("Server kh√¥ng tr·∫£ v·ªÅ token");
      }

      console.log("üîë Token received:", token);

      // ‚úÖ L∆ØU TOKEN V√ÄO LOCALSTORAGE
      localStorage.setItem("token", token);
      localStorage.setItem("jwt-token", token); // L∆∞u c·∫£ 2 key ƒë·ªÉ ƒë·ªÅ ph√≤ng

      // ‚úÖ L·∫§Y TH√îNG TIN USER T·ª™ API USER SERVICE
      let userInfo = null;
      let realUserId = null;

      try {
        console.log("üë§ Fetching user info for email:", formData.email);
        userInfo = await userService.getUserByEmail(formData.email);
        console.log("‚úÖ User info from API:", userInfo);

        // ‚úÖ L·∫§Y USER ID ƒê√öNG C√ÅCH - TH·ª¨ NHI·ªÄU FIELD
        realUserId = userInfo.userId || userInfo.user_id || userInfo.id;
        console.log("üîç User ID candidates:", {
          userId: userInfo.userId,
          user_id: userInfo.user_id, 
          id: userInfo.id
        });

        // ‚úÖ ƒê·∫¢M B·∫¢O USER ID L√Ä S·ªê
        if (realUserId) {
          realUserId = Number(realUserId);
          console.log("‚úÖ Parsed user ID:", realUserId, `(type: ${typeof realUserId})`);
        }

        // ‚úÖ KI·ªÇM TRA USER ID H·ª¢P L·ªÜ
        if (!realUserId || isNaN(realUserId) || realUserId <= 0) {
          console.error("‚ùå INVALID USER ID:", realUserId);
          throw new Error("User ID kh√¥ng h·ª£p l·ªá t·ª´ server");
        }

      } catch (userError) {
        console.error("‚ùå Error fetching user info:", userError);
        throw new Error("Kh√¥ng th·ªÉ l·∫•y th√¥ng tin ng∆∞·ªùi d√πng: " + userError.message);
      }

      // ‚úÖ L∆ØU USER DATA V√ÄO LOCALSTORAGE
      const userToStore = {
        userId: realUserId,
        email: formData.email,
        firstName: userInfo.firstName || userInfo.first_name || "",
        lastName: userInfo.lastName || userInfo.last_name || "",
        mobileNumber: userInfo.mobileNumber || userInfo.mobile_number || "",
        // Th√™m c√°c field kh√°c n·∫øu c·∫ßn
        ...userInfo
      };

      localStorage.setItem("user", JSON.stringify(userToStore));
      console.log("üíæ User data saved to localStorage:", userToStore);

      // ‚úÖ KI·ªÇM TRA L·∫†I D·ªÆ LI·ªÜU ƒê√É L∆ØU
      const savedUser = JSON.parse(localStorage.getItem("user"));
      const savedToken = localStorage.getItem("token");

      console.log("üîç VERIFICATION - Saved data:", {
        token: !!savedToken,
        user: savedUser,
        userId: savedUser?.userId,
        userIdType: typeof savedUser?.userId
      });

      // ‚úÖ HI·ªÇN TH·ªä TH√îNG B√ÅO TH√ÄNH C√îNG
      showMessage(`ƒêƒÉng nh·∫≠p th√†nh c√¥ng! Ch√†o m·ª´ng ${userToStore.firstName} ${userToStore.lastName}`, "success");

      // ‚úÖ CHUY·ªÇN H∆Ø·ªöNG SAU 1.5 GI√ÇY
      setTimeout(() => {
        navigate("/", { replace: true });
      }, 1500);

    } catch (err) {
      console.error("‚ùå LOGIN ERROR:", err);

      // Clear any partial data
      localStorage.removeItem("token");
      localStorage.removeItem("jwt-token");
      localStorage.removeItem("user");

      // Hi·ªÉn th·ªã th√¥ng b√°o l·ªói
      const errorMessage = err.response?.data?.message || 
                          err.response?.data?.error || 
                          err.message || 
                          "ƒêƒÉng nh·∫≠p th·∫•t b·∫°i. Vui l√≤ng th·ª≠ l·∫°i.";

      showMessage(errorMessage, "error");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="p-4">
      <div className="card mx-auto" style={{ maxWidth: "400px", marginTop: "50px" }}>
        <div className="card-body">
          <h4 className="card-title mb-4 text-center text-primary">Sign in</h4>

          {/* Message Alert */}
          {message && (
            <div 
              className={`alert ${messageType === "success" ? "alert-success" : "alert-danger"} d-flex align-items-center`} 
              role="alert"
            >
              {messageType === "success" ? (
                <>
                  <i className="fas fa-check-circle me-2"></i>
                  {message}
                </>
              ) : (
                <>
                  <i className="fas fa-exclamation-triangle me-2"></i>
                  {message}
                </>
              )}
            </div>
          )}

          <form onSubmit={handleSubmit}>
            <div className="form-group mb-3">
              <label className="form-label">Email</label>
              <input
                name="email"
                className="form-control"
                placeholder="Enter your email"
                type="email"
                value={formData.email}
                onChange={handleChange}
                required
                disabled={loading}
              />
            </div>

            <div className="form-group mb-3">
              <label className="form-label">Password</label>
              <input
                name="password"
                className="form-control"
                placeholder="Enter your password"
                type="password"
                value={formData.password}
                onChange={handleChange}
                required
                disabled={loading}
              />
            </div>

            <div className="form-group mb-3">
              <div className="form-check">
                <input
                  name="remember"
                  className="form-check-input"
                  type="checkbox"
                  checked={formData.remember}
                  onChange={handleChange}
                  disabled={loading}
                />
                <label className="form-check-label">
                  Remember me
                </label>
              </div>
            </div>

            <button 
              type="submit" 
              className="btn btn-primary w-100 py-2"
              disabled={loading}
            >
              {loading ? (
                <>
                  <span className="spinner-border spinner-border-sm me-2" role="status"></span>
                  ƒêang ƒëƒÉng nh·∫≠p...
                </>
              ) : (
                <>
                  <i className="fas fa-sign-in-alt me-2"></i>
                  ƒêƒÉng nh·∫≠p
                </>
              )}
            </button>
          </form>

          <div className="text-center mt-3">
            <a href="/forgot-password" className="text-decoration-none">
              Qu√™n m·∫≠t kh·∫©u?
            </a>
          </div>

          <div className="text-center mt-2">
            <span className="text-muted">Ch∆∞a c√≥ t√†i kho·∫£n? </span>
            <a href="/register" className="text-decoration-none">
              ƒêƒÉng k√Ω ngay
            </a>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Login;
