import React from "react";
import { Routes, Route } from "react-router-dom";
import 'bootstrap/dist/css/bootstrap.min.css';

// Layouts & Components (Giữ nguyên các import của mày)
// ...

function App() {
  return (
    <Routes>
      {/* 1. TRANG LOGIN ADMIN: Phải đặt ngoài cùng và ưu tiên lên đầu */}
      <Route path="/admin/login" element={<AdminLogin />} />

      {/* 2. GIAO DIỆN ADMIN: Dùng AdminRoute để bảo vệ */}
      <Route
        path="/admin"
        element={
          <AdminRoute>
            <AdminLayout />
          </AdminRoute>
        }
      >
        <Route index element={<AdminDashboard />} />
        <Route path="products" element={<ProductListAD />} />
      </Route>

      {/* 3. GIAO DIỆN USER: Bọc tất cả trong một Layout chung */}
      <Route
        path="/"
        element={
          <>
            <Header />
            <div style={{ minHeight: "80vh" }}>
              <Main /> {/* Trang chủ mặc định */}
            </div>
            <Footer />
          </>
        }
      />

      {/* 4. CÁC ROUTE USER CÓ HEADER/FOOTER */}
      <Route path="/" element={<><Header /><div style={{ minHeight: "80vh" }}><Outlet /></div><Footer /></>}>
          <Route path="login" element={<Login />} />
          <Route path="register" element={<Register />} />
          <Route path="forgot-password" element={<ForgotPassword />} />
          <Route path="product" element={<ProductList />} />
          <Route path="product/:productId" element={<ProductDetail />} />
          <Route path="cart" element={<ProductCart />} />
          <Route path="checkout" element={<Checkout />} />
          <Route path="orders" element={<OrderList />} />
      </Route>

      {/* Báo lỗi 404 */}
      <Route path="*" element={<h2 className="text-center mt-4">404 - Không tìm thấy trang</h2>} />
    </Routes>
  );
}

// Lưu ý: Nhớ import { Outlet } from "react-router-dom" để dùng cho phần Layout User
