    } catch (error) {
      console.error('âŒ Lá»—i khi xÃ³a sáº£n pháº©m:', error);
      throw error;
    }
  },

  // ========== CART MANAGEMENT ==========

  // Láº¥y giá» hÃ ng active - ÄÃƒ Sá»¬A: ThÃªm fallback
  getActiveCart: async (emailId) => {
    try {
      const user = JSON.parse(localStorage.getItem('user') || '{}');
      let cartId = user.cart?.cartId;

      // Náº¿u khÃ´ng cÃ³ cartId, thá»­ tÃ¬m trong cart localStorage
      if (!cartId) {
        const localCart = cartService.getCartFromStorage();
        if (localCart && localCart.cartId) {
          cartId = localCart.cartId;
          console.log("ðŸ”„ Sá»­ dá»¥ng cartId tá»« localStorage:", cartId);
        }
      }

      if (!cartId) {
        throw new Error('KhÃ´ng tÃ¬m tháº¥y cartId. User cÃ³ thá»ƒ chÆ°a cÃ³ giá» hÃ ng.');
      }

      return await cartService.getCart(emailId, cartId);
    } catch (error) {
      console.error('âŒ Lá»—i khi láº¥y giá» hÃ ng active:', error);

      // Fallback: tráº£ vá» cart tá»« localStorage
      const localCart = cartService.getCartFromStorage();
      if (localCart) {
        console.log("ðŸ”„ Fallback: sá»­ dá»¥ng cart tá»« localStorage");
        return localCart;
      }

      throw error;
    }
  },

