import React, { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import userService from "../services/userService";
import categoryService from "../services/categoryService";

// ‚úÖ Import CSS, Font, Bootstrap
import "bootstrap/dist/css/bootstrap.min.css";
import "bootstrap/dist/js/bootstrap.bundle.min.js"; // ‚úÖ QUAN TR·ªåNG: Th√™m Bootstrap JS
import "../assets/fonts/fontawesome/css/all.min.css";

// ‚úÖ Import h√¨nh ·∫£nh
import logo from "../assets/images/logo.png";
import flagVN from "../assets/images/icons/flags/VN.webp";

const Header = () => {
  const [user, setUser] = useState(null);
  const [userInfo, setUserInfo] = useState(null);
  const [loading, setLoading] = useState(false);
  const [categories, setCategories] = useState([]);
  const [loadingCategories, setLoadingCategories] = useState(false);
  const navigate = useNavigate();

  // ‚úÖ L·∫•y user t·ª´ localStorage
  useEffect(() => {
    const storedUser = localStorage.getItem("user");
    if (storedUser) {
      try {
        const parsedUser = JSON.parse(storedUser);
        setUser(parsedUser);

        if (parsedUser.userId && typeof parsedUser.userId === 'number') {
          setLoading(true);

          userService
            .getUserById(parsedUser.userId)
            .then((userData) => {
              console.log("‚úÖ User data received:", userData);
              setUserInfo(userData);

              const updatedUser = {
                ...parsedUser,
                first_name: userData.first_name || userData.firstName,
                last_name: userData.last_name || userData.lastName,
                email: userData.email,
                mobile_number: userData.mobile_number
              };
              localStorage.setItem("user", JSON.stringify(updatedUser));
            })
            .catch((err) => {
              console.error("‚ùå L·ªói l·∫•y th√¥ng tin user:", err);
              if (parsedUser.first_name || parsedUser.last_name) {
                setUserInfo({
                  first_name: parsedUser.first_name,
                  last_name: parsedUser.last_name
                });
              }
            })
            .finally(() => {
              setLoading(false);
            });
        } else {
          if (parsedUser.first_name || parsedUser.last_name) {
            setUserInfo({
              first_name: parsedUser.first_name,
              last_name: parsedUser.last_name
            });
          }
        }
      } catch (error) {
        console.error("‚ùå Parse user l·ªói:", error);
        setLoading(false);
      }
    }
  }, []);

  // ‚úÖ L·∫•y danh s√°ch categories t·ª´ API
  useEffect(() => {
    fetchCategories();
  }, []);

  const fetchCategories = async () => {
    try {
      setLoadingCategories(true);
      console.log('üîÑ ƒêang t·∫£i danh m·ª•c...');

      const categoriesData = await categoryService.getAllCategories();
      console.log('‚úÖ Danh s√°ch categories:', categoriesData);

      setCategories(categoriesData || []);

    } catch (error) {
      console.error('‚ùå L·ªói khi l·∫•y danh m·ª•c:', error);
      setCategories([]);
    } finally {
      setLoadingCategories(false);
    }
  };

  // ‚úÖ ƒêi·ªÅu h∆∞·ªõng ƒë·∫øn trang s·∫£n ph·∫©m theo category
  const handleCategoryClick = (category) => {
    console.log('üéØ Ch·ªçn category:', category);

    const categoryId = category.categoryId || category.id;
    const categoryName = category.categoryName || category.name || category.title || 'Danh m·ª•c';

    if (categoryId) {
      // ƒêi·ªÅu h∆∞·ªõng ƒë·∫øn trang products v·ªõi state ho·∫∑c query params
      navigate(`/products`, { 
        state: { 
          selectedCategory: categoryId,
          categoryName: categoryName
        }
      });

      // Ho·∫∑c s·ª≠ d·ª•ng query params:
      // navigate(`/products?category=${categoryId}&name=${encodeURIComponent(categoryName)}`);
    } else {
      console.warn('‚ö†Ô∏è Category kh√¥ng c√≥ ID:', category);
    }
  };

  // ‚úÖ ƒêƒÉng xu·∫•t
  const handleLogout = () => {
    localStorage.removeItem("user");
    localStorage.removeItem("token");
    setUser(null);
    setUserInfo(null);
    navigate("/login");
  };

  // ‚úÖ Chuy·ªÉn ƒë·∫øn trang ch·ªânh s·ª≠a profile
  const handleProfileClick = () => {
    if (user) {
      navigate("/profile");
    } else {
      navigate("/login");
    }
  };

  // ‚úÖ H√†m hi·ªÉn th·ªã t√™n ng∆∞·ªùi d√πng
  const renderUserName = () => {
    if (!user) {
      return "My profile";
    }

    if (loading) {
      return (
        <div className="d-flex align-items-center">
          <div className="spinner-border spinner-border-sm text-primary me-2" role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
          <span>Loading...</span>
        </div>
      );
    }

    if (userInfo) {
      const firstName = userInfo.first_name || "";
      const lastName = userInfo.last_name || "";
      const fullName = `${firstName} ${lastName}`.trim();

      if (fullName) {
        return fullName;
      }
    }

    if (user.email) {
      return user.email;
    }

    return "User";
  };

  // ‚úÖ H√†m hi·ªÉn th·ªã l·ªùi ch√†o
  const renderGreeting = () => {
    if (!user) return null;

    if (userInfo && (userInfo.first_name || userInfo.last_name)) {
      const firstName = userInfo.first_name || "";
      return `üëã Hi, ${firstName}`;
    }

    if (user.email) {
      return `üëã Hi, ${user.email.split('@')[0]}`;
    }

    return "üëã Hi, User";
  };

  // ‚úÖ H√†m hi·ªÉn th·ªã t√™n category
  const getCategoryName = (category) => {
    return category.categoryName || category.name || category.title || 'Danh m·ª•c';
  };

  return (
    <header className="section-header">
      {/* ======= THANH TR√äN ======= */}
      <nav className="navbar d-none d-md-flex p-md-0 navbar-expand-sm navbar-light border-bottom">
        <div className="container">
          <div className="collapse navbar-collapse" id="navbarTop4">
            <ul className="navbar-nav mr-auto">
              <li className="nav-item">
                {user ? (
                  <span className="nav-link">
                    {renderGreeting()} |{" "}
                    <button
                      onClick={handleLogout}
                      className="btn btn-link p-0 text-danger"
                      style={{ textDecoration: "none" }}
                    >
                      Logout
                    </button>
                  </span>
                ) : (
                  <span className="nav-link">
                    Hi, <Link to="/login">Log In</Link> or{" "}
                    <Link to="/register">Register</Link>
                  </span>
                )}
              </li>
              <li className="nav-item">
                <a href="#" className="nav-link">
                  Deals
                </a>
              </li>
              <li className="nav-item">
                <a href="#" className="nav-link">
                  Sell
                </a>
              </li>
              <li className="nav-item">
                <a href="#" className="nav-link">
                  Help
                </a>
              </li>
            </ul>

            <ul className="navbar-nav">
              <li className="nav-item">
                <a href="#" className="nav-link">
                  <img src={flagVN} height="24" alt="flag" /> Ship to
                </a>
              </li>
              <li className="nav-item dropdown">
                <a
                  href="#"
                  className="nav-link dropdown-toggle"
                  data-bs-toggle="dropdown" 
                  aria-expanded="false"
                >
                  Watchlist
                </a>
                <ul className="dropdown-menu">
                  <li>
                    <a className="dropdown-item" href="#">
                      First item
                    </a>
                  </li>
                  <li>
                    <a className="dropdown-item" href="#">
                      Second item
                    </a>
                  </li>
                  <li>
                    <a className="dropdown-item" href="#">
                      Third item
                    </a>
                  </li>
                </ul>
              </li>
              <li className="nav-item">
                <a href="#" className="nav-link">
                  My shop
                </a>
              </li>
              <li className="nav-item">
                <a href="#" className="nav-link">
                  <i className="fa fa-bell"></i>
                </a>
              </li>
              <li className="nav-item">
                <a href="#" className="nav-link">
                  <i className="fa fa-shopping-cart"></i>
                </a>
              </li>
            </ul>
          </div>
        </div>
      </nav>

      {/* ======= THANH CH√çNH ======= */}
      <div className="container">
        <section className="header-main border-bottom">
          <div className="row row-sm align-items-center">
            {/* Logo */}
            <div className="col-6 col-sm col-md col-lg flex-grow-0">
              <Link to="/" className="brand-wrap">
                <img src={logo} alt="Logo" height={56} />
              </Link>
            </div>

            {/* Thanh t√¨m ki·∫øm */}
            <div className="col-lg-6 col-xl-5 col-md-5 col-sm-12 flex-grow-1">
              <form className="search-header">
                <div className="input-group">
                  <input
                    type="text"
                    className="form-control"
                    placeholder="Search for products..."
                  />
                  <button className="btn btn-primary" type="submit">
                    <i className="fa fa-search"></i>
                  </button>
                </div>
              </form>
            </div>

            {/* C√°c icon b√™n ph·∫£i */}
            <div className="col-xl-4 col-lg-5 col-md-6">
              <div className="widgets-wrap float-md-right d-flex justify-content-end">
                {/* Widget Profile */}
                <div className="widget-header mr-3">
                  <a 
                    href="#" 
                    className="widget-view"
                    onClick={(e) => {
                      e.preventDefault();
                      handleProfileClick();
                    }}
                  >
                    <div className="icon-area">
                      <i className="fa fa-user"></i>
                    </div>
                    <small className="text">
                      {renderUserName()}
                    </small>
                  </a>
                </div>

                <div className="widget-header mr-3">
                  <a href="#" className="widget-view">
                    <div className="icon-area">
                      <i className="fa fa-comment-dots"></i>
                      <span className="notify">1</span>
                    </div>
                    <small className="text">Message</small>
                  </a>
                </div>
                <div className="widget-header mr-3">
                  <a href="#" className="widget-view">
                    <div className="icon-area">
                      <i className="fa fa-store"></i>
                    </div>
                    <small className="text">Orders</small>
                  </a>
                </div>
                <div className="widget-header">
                  <a href="#" className="widget-view">
                    <div className="icon-area">
                      <i className="fa fa-shopping-cart"></i>
                    </div>
                    <small className="text">Cart</small>
                  </a>
                </div>
              </div>
            </div>
          </div>
        </section>

        {/* Menu ch√≠nh v·ªõi Categories */}
        <nav className="navbar navbar-main navbar-expand pl-0 py-2">
          <ul className="navbar-nav flex-wrap">
            <li className="nav-item">
              <Link to="/" className="nav-link">
                <i className="fa fa-home me-1"></i>
                Home
              </Link>
            </li>
            <li className="nav-item">
              <Link to="/products" className="nav-link">
                <i className="fa fa-box me-1"></i>
                All Products
              </Link>
            </li>

            {/* ‚úÖ Categories Dropdown - S·ª¨A L·∫†I ·ªû ƒê√ÇY */}
            <li className="nav-item dropdown">
              <a
                className="nav-link dropdown-toggle"
                href="#"
                id="categoriesDropdown"
                role="button"
                data-bs-toggle="dropdown"
                aria-expanded="false"
              >
                <i className="fa fa-list me-1"></i>
                Categories
                {loadingCategories && (
                  <span className="ms-1">
                    <div className="spinner-border spinner-border-sm text-primary" role="status">
                      <span className="visually-hidden">Loading...</span>
                    </div>
                  </span>
                )}
              </a>

              <ul 
                className="dropdown-menu" 
                aria-labelledby="categoriesDropdown"
                style={{ maxHeight: '400px', overflowY: 'auto' }}
              >
                {loadingCategories ? (
                  <li>
                    <div className="dropdown-item text-muted">
                      <div className="d-flex align-items-center">
                        <div className="spinner-border spinner-border-sm me-2" role="status">
                          <span className="visually-hidden">Loading...</span>
                        </div>
                        Loading categories...
                      </div>
                    </div>
                  </li>
                ) : categories.length > 0 ? (
                  categories.map((category, index) => (
                    <li key={category.categoryId || category.id || index}>
                      <a
                        className="dropdown-item"
                        href="#"
                        onClick={(e) => {
                          e.preventDefault();
                          handleCategoryClick(category);
                        }}
                      >
                        <i className="fa fa-folder me-2"></i>
                        {getCategoryName(category)}
                      </a>
                    </li>
                  ))
                ) : (
                  <li>
                    <div className="dropdown-item text-muted">
                      <i className="fa fa-exclamation-circle me-2"></i>
                      No categories found
                    </div>
                  </li>
                )}
              </ul>
            </li>
          </ul>
        </nav>
      </div>
    </header>
  );
};

export default Header;
