import axios from "axios";

const API_URL = "http://localhost:8080/api";

// Tạo axios instance
const api = axios.create({
  baseURL: API_URL,
});

// Đăng ký
const register = (data) => {
  return api.post("/register", {
    email: data.email,
    password: data.password,
    fullName: data.fullName,
  });
};

// Đăng nhập
const login = (data) => {
  return api.post("/login", {
    email: data.email,
    password: data.password,
  });
};

export default {
  register,
  login,
};
