import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import productService from '../../services/productService';

const ProductSold = () => {
  const [topSellingProducts, setTopSellingProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchTopSellingProducts = async () => {
      try {
        setLoading(true);
        const response = await productService.getTopSellingProducts(0, 8);
        setTopSellingProducts(response.content || []);
      } catch (err) {
        console.error('❌ Lỗi khi lấy sản phẩm bán chạy:', err);
        setError('Không thể tải sản phẩm bán chạy');
      } finally {
        setLoading(false);
      }
    };

    fetchTopSellingProducts();
  }, []);

  const formatPrice = (price) => {
    return new Intl.NumberFormat('vi-VN', {
      style: 'currency',
      currency: 'VND'
    }).format(price);
  };

  if (loading) {
    return (
      <section className="section-content padding-y">
        <div className="container">
          <div className="row">
            {[...Array(8)].map((_, index) => (
              <div key={index} className="col-md-3 col-6 mb-4">
                <div className="card product-card">
                  <div className="img-wrapper" style={{ height: '200px', backgroundColor: '#f8f9fa' }}>
                    <div className="d-flex justify-content-center align-items-center h-100">
                      <div className="spinner-border text-primary" role="status">
                        <span className="visually-hidden">Loading...</span>
                      </div>
                    </div>
                  </div>
                  <div className="card-body">
                    <div className="placeholder-glow">
                      <span className="placeholder col-8"></span>
                      <span className="placeholder col-6"></span>
                      <span className="placeholder col-4"></span>
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </section>
    );
  }

  if (error) {
    return (
      <section className="section-content padding-y">
        <div className="container">
          <div className="alert alert-danger text-center">
            <i className="fas fa-exclamation-triangle me-2"></i>
            {error}
          </div>
        </div>
      </section>
    );
  }

  return (
    <section className="section-content padding-y bg-light">
      <div className="container">
        {/* Header Section */}
        <div className="row mb-4">
          <div className="col-md-8">
            <h2 className="section-title">
              <i className="fas fa-fire text-danger me-2"></i>
              Sản Phẩm Bán Chạy
            </h2>
            <p className="text-muted">Những sản phẩm được yêu thích nhất</p>
          </div>
          <div className="col-md-4 text-end">
            <Link to="/products/top-selling" className="btn btn-outline-primary">
              Xem tất cả <i className="fas fa-arrow-right ms-1"></i>
            </Link>
          </div>
        </div>

        {/* Products Grid */}
        <div className="row">
          {topSellingProducts.length > 0 ? (
            topSellingProducts.map((product, index) => (
              <div key={product.productId} className="col-xl-3 col-lg-4 col-md-6 col-sm-6 mb-4">
                <div className="card product-card h-100 shadow-sm">
                  {/* Badge for top products */}
                  {index < 3 && (
                    <div className={`position-absolute top-0 start-0 m-2 badge ${index === 0 ? 'bg-danger' : index === 1 ? 'bg-warning' : 'bg-info'}`}>
                      <i className={`fas ${index === 0 ? 'fa-crown' : index === 1 ? 'fa-medal' : 'fa-award'} me-1`}></i>
                      Top {index + 1}
                    </div>
                  )}

                  {/* Sold Count Badge */}
                  <div className="position-absolute top-0 end-0 m-2">
                    <span className="badge bg-success">
                      <i className="fas fa-shopping-cart me-1"></i>
                      Đã bán: {product.sold || 0}
                    </span>
                  </div>

                  {/* Product Image */}
                  <div className="img-wrapper position-relative">
                    <Link to={`/products/${product.productId}`}>
                      <img
                        src={product.image ? `/api/public/products/image/${product.image}` : '/images/default-product.png'}
                        className="card-img-top"
                        alt={product.productName}
                        style={{ height: '200px', objectFit: 'cover' }}
                        onError={(e) => {
                          e.target.src = '/images/default-product.png';
                        }}
                      />
                    </Link>

                    {/* Discount Badge */}
                    {product.discount > 0 && (
                      <div className="position-absolute bottom-0 start-0 m-2 badge bg-danger">
                        -{product.discount}%
                      </div>
                    )}
                  </div>

                  {/* Card Body */}
                  <div className="card-body d-flex flex-column">
                    {/* Product Name */}
                    <h6 className="card-title">
                      <Link 
                        to={`/products/${product.productId}`} 
                        className="text-dark text-decoration-none"
                        style={{
                          display: '-webkit-box',
                          WebkitLineClamp: 2,
                          WebkitBoxOrient: 'vertical',
                          overflow: 'hidden'
                        }}
                      >
                        {product.productName}
                      </Link>
                    </h6>

                    {/* Views Count */}
                    <div className="mb-2">
                      <small className="text-muted">
                        <i className="fas fa-eye me-1"></i>
                        {product.views || 0} lượt xem
                      </small>
                    </div>

                    {/* Price Section */}
                    <div className="mt-auto">
                      {product.discount > 0 ? (
                        <>
                          <div className="d-flex align-items-center">
                            <strong className="text-danger me-2">
                              {formatPrice(product.specialPrice || product.price * (1 - product.discount / 100))}
                            </strong>
                            <small className="text-muted text-decoration-line-through">
                              {formatPrice(product.price)}
                            </small>
                          </div>
                        </>
                      ) : (
                        <strong className="text-primary">
                          {formatPrice(product.price)}
                        </strong>
                      )}
                    </div>

                    {/* Stock Status */}
                    <div className="mt-2">
                      {product.quantity > 0 ? (
                        <small className="text-success">
                          <i className="fas fa-check-circle me-1"></i>
                          Còn hàng
                        </small>
                      ) : (
                        <small className="text-danger">
                          <i className="fas fa-times-circle me-1"></i>
                          Hết hàng
                        </small>
                      )}
                    </div>

                    {/* Action Buttons */}
                    <div className="d-grid gap-2 mt-3">
                      <Link 
                        to={`/products/${product.productId}`} 
                        className="btn btn-primary btn-sm"
                      >
                        <i className="fas fa-shopping-cart me-1"></i>
                        Mua ngay
                      </Link>
                    </div>
                  </div>
                </div>
              </div>
            ))
          ) : (
            <div className="col-12 text-center py-5">
              <div className="text-muted">
                <i className="fas fa-box-open fa-3x mb-3"></i>
                <h5>Không có sản phẩm bán chạy</h5>
                <p>Hiện chưa có sản phẩm nào được bán</p>
              </div>
            </div>
          )}
        </div>

        {/* View More Section for Mobile */}
        {topSellingProducts.length > 0 && (
          <div className="row mt-4 d-md-none">
            <div className="col-12 text-center">
              <Link to="/products/top-selling" className="btn btn-primary">
                Xem thêm sản phẩm bán chạy
              </Link>
            </div>
          </div>
        )}
      </div>

      <style jsx>{`
        .product-card {
          transition: transform 0.3s ease, box-shadow 0.3s ease;
          border: 1px solid #e9ecef;
        }

        .product-card:hover {
          transform: translateY(-5px);
          box-shadow: 0 8px 25px rgba(0,0,0,0.15) !important;
        }

        .img-wrapper {
          overflow: hidden;
        }

        .product-card img {
          transition: transform 0.3s ease;
        }

        .product-card:hover img {
          transform: scale(1.05);
        }

        .section-title {
          font-weight: 700;
          color: #2c3e50;
          position: relative;
          padding-bottom: 10px;
        }

        .section-title::after {
          content: '';
          position: absolute;
          bottom: 0;
          left: 0;
          width: 60px;
          height: 3px;
          background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
          border-radius: 2px;
        }
      `}</style>
    </section>
  );
};

export default ProductSold;
