import { useEffect, useState } from "react";
import { useParams, Link, useNavigate } from "react-router-dom";
import productService from "../../services/productService";
import cartService from "../../services/cartService";
import Swal from 'sweetalert2'; // Th√™m th∆∞ vi·ªán n√†y

const IMAGE_BASE_URL = "http://localhost:8080/api/public/products/image";

const ProductDetail = () => {
  const { productId } = useParams();
  const navigate = useNavigate();
  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [addingToCart, setAddingToCart] = useState(false);

  const currentUser = JSON.parse(localStorage.getItem('user'));

  useEffect(() => {
    const fetchProductDetail = async () => {
      if (!productId || productId === 'undefined' || productId === 'null') {
        setError("Kh√¥ng t√¨m th·∫•y ID s·∫£n ph·∫©m t·ª´ URL");
        setLoading(false);
        return;
      }

      try {
        setLoading(true);
        const productData = await productService.getProductById(productId);
        setProduct(productData);
      } catch (err) {
        setError(`Kh√¥ng th·ªÉ t·∫£i th√¥ng tin s·∫£n ph·∫©m: ${err.response?.data?.message || err.message}`);
      } finally {
        setLoading(false);
      }
    };

    fetchProductDetail();
  }, [productId]);

  const handleAddToCart = async () => {
    // Th√¥ng b√°o y√™u c·∫ßu ƒëƒÉng nh·∫≠p
    if (!currentUser || !currentUser.email) {
      Swal.fire({
        title: 'Y√™u c·∫ßu ƒëƒÉng nh·∫≠p',
        text: 'Vui l√≤ng ƒëƒÉng nh·∫≠p ƒë·ªÉ th√™m s·∫£n ph·∫©m v√†o gi·ªè h√†ng!',
        icon: 'warning',
        showCancelButton: true,
        confirmButtonColor: '#3085d6',
        cancelButtonColor: '#d33',
        confirmButtonText: 'ƒêƒÉng nh·∫≠p ngay',
        cancelButtonText: 'ƒê·ªÉ sau'
      }).then((result) => {
        if (result.isConfirmed) {
          navigate('/login');
        }
      });
      return;
    }

    if (product?.quantity === 0) {
      Swal.fire('H·∫øt h√†ng', 'R·∫•t ti·∫øc, s·∫£n ph·∫©m n√†y hi·ªán ƒë√£ h·∫øt h√†ng!', 'error');
      return;
    }

    try {
      setAddingToCart(true);

      // 1. L·∫•y gi·ªè h√†ng active
      let cart = await cartService.getActiveCart(currentUser.email);

      // 2. Th√™m s·∫£n ph·∫©m v√†o gi·ªè h√†ng
      await cartService.addToCart(cart.cartId, productId, 1);

      // Th√¥ng b√°o th√†nh c√¥ng m∆∞·ª£t m√†
      Swal.fire({
        icon: 'success',
        title: 'ƒê√£ th√™m v√†o gi·ªè h√†ng!',
        text: `S·∫£n ph·∫©m ${product.productName || product.product_name} ƒë√£ n·∫±m trong gi·ªè h√†ng c·ªßa b·∫°n.`,
        showConfirmButton: false,
        timer: 1500
      });

      setTimeout(() => {
        navigate('/cart');
      }, 1600);

    } catch (error) {
      Swal.fire({
        icon: 'error',
        title: 'L·ªói th√™m gi·ªè h√†ng',
        text: error.response?.data?.message || error.message,
      });
    } finally {
      setAddingToCart(false);
    }
  };

  if (loading) {
    return (
      <div className="container mt-5 text-center">
        <div className="spinner-border text-primary" role="status"></div>
        <p className="mt-2 text-muted">ƒêang t·∫£i th√¥ng tin s·∫£n ph·∫©m...</p>
      </div>
    );
  }

  if (error || !product) {
    return (
      <div className="container mt-4">
        <div className="alert alert-danger text-center shadow-sm">
          <h4>‚ùå Kh√¥ng th·ªÉ t·∫£i s·∫£n ph·∫©m</h4>
          <p>{error || "S·∫£n ph·∫©m kh√¥ng t·ªìn t·∫°i"}</p>
          <button className="btn btn-primary mt-2" onClick={() => navigate(-1)}>‚Üê Quay l·∫°i</button>
        </div>
      </div>
    );
  }

  const productName = product.productName || product.product_name;
  const productPrice = product.price;
  const productSpecialPrice = product.special_price;
  const productQuantity = product.quantity;
  const productDiscount = product.discount;

  return (
    <div className="container mt-4">
      {/* Breadcrumb gi·ªØ nguy√™n nh∆∞ c≈© */}
      <nav aria-label="breadcrumb">
        <ol className="breadcrumb">
          <li className="breadcrumb-item"><Link to="/">Trang ch·ªß</Link></li>
          <li className="breadcrumb-item"><Link to="/product">S·∫£n ph·∫©m</Link></li>
          <li className="breadcrumb-item active">{productName}</li>
        </ol>
      </nav>

      <div className="row g-4">
        <div className="col-md-6">
          <div className="card border-0 shadow-sm overflow-hidden">
            <img
              src={`${IMAGE_BASE_URL}/${product.image}`}
              alt={productName}
              onError={(e) => (e.target.src = "https://cdn-icons-png.flaticon.com/512/679/679922.png")}
              className="img-fluid"
              style={{ minHeight: "400px", objectFit: "contain", background: "#f8f9fa" }}
            />
          </div>
        </div>

        <div className="col-md-6">
          <div className="ps-md-4">
            <h1 className="fw-bold mb-3">{productName}</h1>

            <div className="mb-4">
              {productSpecialPrice ? (
                <div className="d-flex align-items-center">
                  <span className="h2 text-danger fw-bold mb-0">
                    {productSpecialPrice.toLocaleString()} ‚Ç´
                  </span>
                  <span className="ms-3 text-muted text-decoration-line-through">
                    {productPrice.toLocaleString()} ‚Ç´
                  </span>
                  <span className="badge bg-danger ms-3">-{productDiscount}%</span>
                </div>
              ) : (
                <span className="h2 text-danger fw-bold">{productPrice?.toLocaleString()} ‚Ç´</span>
              )}
            </div>

            <div className="mb-4">
              <span className={`badge ${productQuantity === 0 ? 'bg-secondary' : 'bg-success'} p-2 px-3`}>
                {productQuantity === 0 ? 'T·∫°m h·∫øt h√†ng' : `Trong kho: ${productQuantity}`}
              </span>
            </div>

            <div className="card bg-light border-0 p-3 mb-4">
              <h6 className="fw-bold"><i className="bi bi-info-circle me-2"></i>M√¥ t·∫£ s·∫£n ph·∫©m:</h6>
              <p className="small text-muted mb-0" style={{ whiteSpace: 'pre-line' }}>
                {product.description || "Ch∆∞a c√≥ m√¥ t·∫£ cho s·∫£n ph·∫©m n√†y."}
              </p>
            </div>

            <div className="d-grid gap-3 d-md-flex">
              <button 
                className="btn btn-primary btn-lg px-5 shadow-sm"
                onClick={handleAddToCart}
                disabled={productQuantity === 0 || addingToCart}
              >
                {addingToCart ? 'ƒêang x·ª≠ l√Ω...' : 'üõí Th√™m v√†o gi·ªè h√†ng'}
              </button>
              <button className="btn btn-outline-secondary btn-lg">
                ‚ù§Ô∏è Y√™u th√≠ch
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProductDetail;
