import React, { useEffect, useState, useCallback } from "react";
import { Link, useNavigate } from "react-router-dom";
import userService from "../services/userService";
import categoryService from "../services/categoryService";
import cartService from "../services/cartService";
import Swal from "sweetalert2";

const Header = () => {
  const [user, setUser] = useState(null);
  const [userInfo, setUserInfo] = useState(null);
  const [loadingCategories, setLoadingCategories] = useState(false);
  const [categories, setCategories] = useState([]);
  const [cartCount, setCartCount] = useState(0);

  // MỚI: State cho thanh tìm kiếm
  const [searchTerm, setSearchTerm] = useState("");

  const navigate = useNavigate();

  const initUser = useCallback(async () => {
    const storedUser = localStorage.getItem("user");
    if (storedUser) {
      const parsedUser = JSON.parse(storedUser);
      setUser(parsedUser);
      if (parsedUser.userId) {
        try {
          const data = await userService.getUserById(parsedUser.userId);
          setUserInfo(data);
        } catch (err) { console.error(err); }
      }
    } else {
      setUser(null);
      setUserInfo(null);
    }
  }, []);

  useEffect(() => {
    initUser();
    fetchCategories();
    window.addEventListener("authUpdated", initUser);
    return () => window.removeEventListener("authUpdated", initUser);
  }, [initUser]);

  useEffect(() => {
    loadCartCount();
    window.addEventListener('cartUpdated', loadCartCount);
    return () => window.removeEventListener('cartUpdated', loadCartCount);
  }, [user]);

  const loadCartCount = async () => {
    const currentUser = JSON.parse(localStorage.getItem('user') || '{}');
    if (currentUser?.email) {
      const count = await cartService.getCartCount(currentUser.email);
      setCartCount(count);
    }
  };

  const fetchCategories = async () => {
    setLoadingCategories(true);
    try {
      const data = await categoryService.getAllCategories();
      setCategories(data || []);
    } finally { setLoadingCategories(false); }
  };

  // MỚI: Xử lý tìm kiếm khi nhấn Enter hoặc Click nút search
  const handleSearchSubmit = (e) => {
    e.preventDefault();
    if (searchTerm.trim()) {
      navigate("/product", { 
        state: { searchKeyword: searchTerm.trim() } 
      });
      // Tùy chọn: Xóa ô search sau khi tìm
      // setSearchTerm(""); 
    }
  };

  const handleCategoryClick = (category) => {
    navigate(`/product`, { 
      state: { 
        selectedCategory: category.categoryId || category.id, 
        categoryName: category.categoryName || category.name 
      } 
    });
  };

  const handleLogout = () => {
    Swal.fire({
      title: 'Bạn muốn đăng xuất?',
      confirmButtonColor: '#ff6a00',
      confirmButtonText: 'Đăng xuất',
      showCancelButton: true
    }).then((result) => {
      if (result.isConfirmed) {
        localStorage.clear();
        setUser(null);
        setCartCount(0);
        window.dispatchEvent(new Event("authUpdated"));
        navigate("/login");
      }
    });
  };

  return (
    <header className="section-header shadow-sm sticky-top bg-white">
      <div className="top-banner-ads py-1 text-center bg-dark text-white small">
        GIAO SIÊU TỐC 2H - FREESHIP 0Đ CHO ĐƠN TỪ 199K
      </div>

      <section className="header-main-orange py-3" style={{backgroundColor: '#ff6a00'}}>
        <div className="container">
          <div className="row align-items-center">
            <div className="col-lg-2 col-6">
              <Link to="/" className="text-decoration-none">
                <h3 className="text-white fw-bold mb-0">ShopBeAnh</h3>
              </Link>
            </div>

            <div className="col-lg-6 d-none d-md-block">
              {/* FIX: Thêm onSubmit và bind value */}
              <form className="search-bar-wrap" onSubmit={handleSearchSubmit}>
                <div className="input-group overflow-hidden rounded-pill bg-white px-3">
                  <input 
                    type="text" 
                    placeholder="Bạn cần tìm mỹ phẩm gì..." 
                    className="form-control border-0 shadow-none py-2"
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                  />
                  <button className="btn p-0 text-warning" type="submit">
                    <i className="fa fa-search fa-lg"></i>
                  </button>
                </div>
              </form>
            </div>

            <div className="col-lg-4 col-6 d-flex justify-content-end align-items-center gap-4">
              <div className="user-dropdown-container position-relative">
                <div className="text-white text-center dropdown-toggle" data-bs-toggle="dropdown" style={{cursor: 'pointer'}}>
                  <i className="fa fa-user-circle fa-lg d-block mb-1"></i>
                  <small>{userInfo?.firstName || "Tài khoản"}</small>
                </div>
                <ul className="dropdown-menu dropdown-menu-end shadow border-0 mt-2">
                  {!user ? (
                    <>
                      <li><Link className="dropdown-item" to="/login">Đăng nhập</Link></li>
                      <li><Link className="dropdown-item" to="/register">Đăng ký</Link></li>
                    </>
                  ) : (
                    <>
                      <li><Link className="dropdown-item" to="/profile">Cá nhân</Link></li>
                      <li><button className="dropdown-item text-danger" onClick={handleLogout}>Đăng xuất</button></li>
                    </>
                  )}
                </ul>
              </div>

              <Link to="/cart" className="text-white text-center position-relative text-decoration-none">
                <i className="fa fa-shopping-basket fa-lg d-block mb-1"></i>
                <span className="badge rounded-pill bg-danger position-absolute top-0 start-100 translate-middle" style={{fontSize: '10px'}}>
                  {cartCount}
                </span>
                <small>Giỏ hàng</small>
              </Link>
            </div>
          </div>
        </div>
      </section>

      <nav className="nav-categories border-bottom d-none d-lg-block">
        <div className="container">
          <ul className="nav d-flex align-items-center">
            <li className="nav-item dropdown dropdown-hover">
              <a className="nav-link fw-bold text-dark py-3 dropdown-toggle" href="#">
                <i className="fa fa-bars me-2 text-warning"></i>DANH MỤC SẢN PHẨM
              </a>
              <ul className="dropdown-menu shadow-lg border-0">
                {categories.map((cat, idx) => (
                  <li key={idx}>
                    <button className="dropdown-item py-2" onClick={() => handleCategoryClick(cat)}>
                      {cat.categoryName || cat.name}
                    </button>
                  </li>
                ))}
              </ul>
            </li>
            <li className="nav-item"><Link to="/brands" className="nav-link text-dark">Thương hiệu hot</Link></li>
            <li className="nav-item"><Link to="/flash-sale" className="nav-link text-danger fw-bold">Flash Sale</Link></li>
          </ul>
        </div>
      </nav>
      <style>{`.dropdown-hover:hover > .dropdown-menu { display: block; }`}</style>
    </header>
  );
};

export default Header;
