// services/cartService.js
import api from './api';

const cartService = {
  // Tạo giỏ hàng mới
  createCart: async (email) => {
    try {
      const response = await api.post(`/public/users/${email}/carts`);
      return response.data;
    } catch (error) {
      console.error('❌ Lỗi khi tạo giỏ hàng:', error);
      throw error;
    }
  },

  // Lấy giỏ hàng active
  getActiveCart: async (email) => {
    try {
      const response = await api.get(`/public/users/${email}/carts/active`);
      return response.data;
    } catch (error) {
      console.error('❌ Lỗi khi lấy giỏ hàng:', error);
      throw error;
    }
  },

  // Thêm sản phẩm vào giỏ hàng
  addToCart: async (cartId, productId, quantity) => {
    try {
      const response = await api.post(`/public/carts/${cartId}/products/${productId}/quantity/${quantity}`);
      return response.data;
    } catch (error) {
      console.error('❌ Lỗi khi thêm vào giỏ hàng:', error);
      throw error;
    }
  },

  // Cập nhật số lượng
  updateQuantity: async (cartId, productId, quantity) => {
    try {
      const response = await api.put(`/public/carts/${cartId}/products/${productId}/quantity/${quantity}`);
      return response.data;
    } catch (error) {
      console.error('❌ Lỗi khi cập nhật số lượng:', error);
      throw error;
    }
  },

  // Xóa sản phẩm
  removeFromCart: async (cartId, productId) => {
    try {
      const response = await api.delete(`/public/carts/${cartId}/products/${productId}`);
      return response.data;
    } catch (error) {
      console.error('❌ Lỗi khi xóa sản phẩm:', error);
      throw error;
    }
  },

  // Tạo đơn hàng - FIXED
    createOrder: async (email, cartId, paymentMethod, shippingAddress) => {
    try {
      // ✅ SỬA: Thêm shippingAddress vào URL params
      const response = await api.post(
        `/public/users/${email}/carts/${cartId}/payments/${paymentMethod}/order?shippingAddress=${encodeURIComponent(shippingAddress)}`
      );
      return response.data;
    } catch (error) {
      throw error;
    }
  },

  // Hoặc dùng endpoint alternative
  createOrderAlt: async (email, cartId, paymentMethod, shippingAddress) => {
    try {
      const response = await api.post(
        `/public/orders/place?email=${email}&cartId=${cartId}&paymentMethod=${paymentMethod}&shippingAddress=${encodeURIComponent(shippingAddress)}`
      );
      return response.data;
    } catch (error) {
      throw error;
    }
  },
  // Lấy số lượng sản phẩm trong giỏ hàng
  getCartCount: async (email) => {
    try {
      const cart = await cartService.getActiveCart(email);
      if (!cart || !cart.products) return 0;
      return cart.products.reduce((total, item) => total + (item.quantity || 1), 0);
    } catch (error) {
      console.error('❌ Lỗi khi lấy số lượng giỏ hàng:', error);
      return 0;
    }
  },

  // Hoặc nếu bạn muốn tính từ localStorage cho nhanh
  getCartCountFromStorage: () => {
    try {
      const cart = JSON.parse(localStorage.getItem('cart') || '{}');
      if (!cart.products) return 0;
      return cart.products.reduce((total, item) => total + (item.quantity || 1), 0);
    } catch (error) {
      return 0;
    }
  },

  // ✅ THÊM: Hàm làm rỗng giỏ hàng
  clearCart: async (email, cartId) => {
    try {
      const response = await api.delete(`/public/users/${email}/carts/${cartId}/clear`);
      return response.data;
    } catch (error) {
      console.error('Error clearing cart:', error);
      throw error;
    }
  },

  // ✅ THÊM: Hoặc hàm xóa toàn bộ sản phẩm trong giỏ hàng
  removeAllFromCart: async (email, cartId) => {
    try {
      // Lấy giỏ hàng hiện tại
      const cart = await cartService.getActiveCart(email);

      // Xóa từng sản phẩm
      if (cart && cart.products) {
        for (const product of cart.products) {
          await cartService.removeFromCart(cartId, product.productId);
        }
      }

      return { success: true, message: 'Cart cleared successfully' };
    } catch (error) {
      console.error('Error removing all from cart:', error);
      throw error;
    }
  },

  // Tính tổng tiền - FIXED
  calculateTotal: (cartItems) => {
    if (!cartItems || cartItems.length === 0) return 0;
    return cartItems.reduce((total, item) => {
      // Ưu tiên specialPrice nếu có, nếu không thì dùng price
      const price = item.specialPrice !== undefined && item.specialPrice !== null 
        ? item.specialPrice 
        : item.price || 0;
      const quantity = item.quantity || 1;
      return total + (price * quantity);
    }, 0);
  },

  // ✅ THÊM: Hàm refresh giỏ hàng (tải lại giỏ hàng từ server)
  refreshCart: async (email) => {
    try {
      const cart = await cartService.getActiveCart(email);

      // Lưu vào localStorage để đồng bộ
      if (cart) {
        localStorage.setItem('cart', JSON.stringify(cart));
      }

      return cart;
    } catch (error) {
      console.error('Error refreshing cart:', error);
      throw error;
    }
  },

  // ✅ THÊM: Hàm lấy giỏ hàng từ localStorage
  getCartFromStorage: () => {
    try {
      return JSON.parse(localStorage.getItem('cart') || 'null');
    } catch (error) {
      return null;
    }
  },

  // ✅ THÊM: Hàm cập nhật giỏ hàng trong localStorage
  updateCartInStorage: (cart) => {
    try {
      localStorage.setItem('cart', JSON.stringify(cart));
    } catch (error) {
      console.error('Error updating cart in storage:', error);
    }
  },

  // ✅ THÊM: Hàm xóa giỏ hàng khỏi localStorage
  clearCartFromStorage: () => {
    try {
      localStorage.removeItem('cart');
    } catch (error) {
      console.error('Error clearing cart from storage:', error);
    }
  }
};

export default cartService;
