import React, { useState, useEffect, useCallback } from "react";
import api from "../../../services/api";
import { Eye, X, Package, Calendar, User, MapPin } from "lucide-react";

const OrderListAD = () => {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedOrder, setSelectedOrder] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [pageConfig, setPageConfig] = useState({ pageNumber: 0, totalPages: 0, totalElements: 0 });

  const fetchOrders = useCallback(async (page = 0) => {
    try {
      setLoading(true);
      const token = localStorage.getItem("admin-token") || localStorage.getItem("token");
      const res = await api.get(`/admin/orders?pageNumber=${page}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      // Đảm bảo lấy đúng mảng content từ OrderResponse
      setOrders(res.data.content || []);
      setPageConfig({
        pageNumber: res.data.pageNumber || 0,
        totalPages: res.data.totalPages || 0,
        totalElements: res.data.totalElements || 0
      });
    } catch (err) {
      console.error("❌ Lỗi lấy đơn hàng:", err);
    } finally {
      setLoading(false);
    }
  }, []);

  useEffect(() => {
    fetchOrders(0);
  }, [fetchOrders]);

  const handleViewOrder = async (orderId) => {
    try {
      const token = localStorage.getItem("admin-token") || localStorage.getItem("token");
      // Sử dụng đúng endpoint debug mày đã gửi
      const res = await api.get(`/admin/debug/orders/${orderId}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setSelectedOrder(res.data);
      setShowModal(true);
    } catch (err) {
      alert("Không thể lấy chi tiết đơn hàng!");
    }
  };

  const getStatusClass = (status) => {
    switch (status?.toUpperCase()) {
      case 'DELIVERED': return 'bg-success text-white';
      case 'CANCELLED': return 'bg-danger text-white';
      case 'SHIPPED': return 'bg-info text-white';
      default: return 'bg-warning text-dark';
    }
  };

  return (
    <div className="card border-0 shadow-sm text-start">
      <div className="card-header bg-white py-3 d-flex align-items-center gap-2">
        <Package size={20} className="text-primary" />
        <h5 className="m-0 fw-bold text-dark text-uppercase">Danh sách đơn hàng</h5>
      </div>

      <div className="table-responsive">
        <table className="table table-hover align-middle mb-0">
          <thead className="table-light text-secondary small text-uppercase">
            <tr>
              <th className="ps-4">Mã đơn</th>
              <th>Khách hàng</th>
              <th>Ngày đặt</th>
              <th>Tổng tiền</th>
              <th>Trạng thái</th>
              <th className="text-center">Thao tác</th>
            </tr>
          </thead>
          <tbody>
            {loading ? (
              <tr><td colSpan="6" className="text-center py-5">Đang tải dữ liệu...</td></tr>
            ) : orders.length === 0 ? (
              <tr><td colSpan="6" className="text-center py-5 text-muted">Chưa có đơn hàng nào</td></tr>
            ) : orders.map((o) => (
              <tr key={o.orderId}>
                <td className="ps-4 fw-bold text-primary">#{o.orderId}</td>
                <td>
                  <div className="fw-semibold">{o.email}</div>
                </td>
                <td className="text-secondary small">{o.orderDate}</td>
                <td className="fw-bold">{o.totalAmount?.toLocaleString()}đ</td>
                <td>
                  <span className={`badge rounded-pill ${getStatusClass(o.orderStatus)}`}>
                    {o.orderStatus}
                  </span>
                </td>
                <td className="text-center">
                  <button className="btn btn-sm btn-outline-primary border-0 bg-light" onClick={() => handleViewOrder(o.orderId)}>
                    <Eye size={18} />
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* Phân trang */}
      {pageConfig.totalPages > 1 && (
        <div className="card-footer bg-white d-flex justify-content-between align-items-center py-3">
          <div className="small text-muted">Hiển thị trang {pageConfig.pageNumber + 1} / {pageConfig.totalPages}</div>
          <nav>
            <ul className="pagination pagination-sm mb-0">
              {[...Array(pageConfig.totalPages)].map((_, i) => (
                <li key={i} className={`page-item ${pageConfig.pageNumber === i ? 'active' : ''}`}>
                  <button className="page-link" onClick={() => fetchOrders(i)}>{i + 1}</button>
                </li>
              ))}
            </ul>
          </nav>
        </div>
      )}

      {/* MODAL CHI TIẾT ĐƠN HÀNG */}
      {showModal && selectedOrder && (
        <div className="modal d-block shadow" style={{ backgroundColor: "rgba(0,0,0,0.6)", zIndex: 1050 }}>
          <div className="modal-dialog modal-lg modal-dialog-centered">
            <div className="modal-content border-0 shadow-lg">
              <div className="modal-header border-bottom-0 pb-0">
                <h5 className="modal-title fw-bold d-flex align-items-center gap-2">
                  <Package className="text-primary" /> Đơn hàng #{selectedOrder.orderId}
                </h5>
                <button type="button" className="btn-close" onClick={() => setShowModal(false)}></button>
              </div>
              <div className="modal-body px-4">
                {/* Thông tin khách hàng & vận chuyển */}
                <div className="row g-3 mb-4 p-3 bg-light rounded-3 small">
                  <div className="col-md-4 d-flex align-items-center gap-2 text-truncate">
                    <User size={16} className="text-secondary" /> <strong>Khách:</strong> {selectedOrder.email}
                  </div>
                  <div className="col-md-4 d-flex align-items-center gap-2 text-truncate">
                    <Calendar size={16} className="text-secondary" /> <strong>Ngày:</strong> {selectedOrder.orderDate}
                  </div>
                  <div className="col-md-4 d-flex align-items-center gap-2 text-truncate">
                    <MapPin size={16} className="text-secondary" /> <strong>Địa chỉ:</strong> {selectedOrder.shippingAddress || "N/A"}
                  </div>
                </div>
                
                <h6 className="fw-bold mb-3 small text-uppercase text-secondary">Danh sách sản phẩm</h6>
                <div className="table-responsive rounded border">
                  <table className="table table-sm mb-0 align-middle">
                    <thead className="table-light small text-uppercase">
                      <tr>
                        <th className="ps-3 py-2">Sản phẩm</th>
                        <th className="text-center py-2">SL</th>
                        <th className="text-end py-2">Đơn giá</th>
                        <th className="text-end pe-3 py-2">Thành tiền</th>
                      </tr>
                    </thead>
                    <tbody>
                      {selectedOrder.orderItems?.map((item, idx) => (
                        <tr key={idx}>
                          <td className="ps-3 py-3">
                            <div className="fw-semibold text-dark">{item.product?.productName || "Sản phẩm không tên"}</div>
                            <div className="text-muted small" style={{fontSize: '10px'}}>Giảm giá: {item.discount || 0}%</div>
                          </td>
                          <td className="text-center">x{item.quantity}</td>
                          <td className="text-end">{item.orderedProductPrice?.toLocaleString()}đ</td>
                          <td className="text-end pe-3 fw-bold text-dark">
                            {(item.quantity * item.orderedProductPrice)?.toLocaleString()}đ
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>

                <div className="d-flex justify-content-end mt-4">
                  <div className="text-end border-top pt-3" style={{ minWidth: "200px" }}>
                    <div className="d-flex justify-content-between mb-2">
                      <span className="text-muted small">Tổng giá trị:</span>
                      <span className="fw-bold">{selectedOrder.totalAmount?.toLocaleString()}đ</span>
                    </div>
                    <div className="d-flex justify-content-between align-items-center">
                      <span className="text-dark fw-bold">THANH TOÁN:</span>
                      <span className="text-danger fw-bolder fs-4">{selectedOrder.totalAmount?.toLocaleString()}đ</span>
                    </div>
                  </div>
                </div>
              </div>
              <div className="modal-footer border-top-0 pt-0 pb-4 pe-4">
                <button className="btn btn-secondary px-4 fw-semibold shadow-sm" onClick={() => setShowModal(false)}>Đóng lại</button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default OrderListAD;
