import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import productService from '../../services/productService';

const IMAGE_BASE_URL = "http://localhost:8080/api/public/products/image";

const ProductNew = () => {
  const [newProducts, setNewProducts] = useState([]);
  const [topProducts, setTopProducts] = useState([]);
  const [allProducts, setAllProducts] = useState([]);
  const [loading, setLoading] = useState(false);
  const [page, setPage] = useState(0);
  const [expanded, setExpanded] = useState(false);

  useEffect(() => {
    fetchInitialData();
  }, []);

  const fetchInitialData = async () => {
    setLoading(true);
    const [topRes, allRes] = await Promise.all([
      productService.getTopSellingProducts(0, 8),
      productService.getAllProducts(0, 8)
    ]);

    setTopProducts(topRes.content || []);
    setAllProducts(allRes.content || []);

    const sortedNew = [...(allRes.content || [])]
      .sort((a, b) => b.productId - a.productId)
      .slice(0, 4);

    setNewProducts(sortedNew);
    setLoading(false);
  };

  const loadMoreProducts = async () => {
    setLoading(true);
    const nextPage = page + 1;
    const res = await productService.getAllProducts(nextPage, 8);
    setAllProducts([...allProducts, ...(res.content || [])]);
    setPage(nextPage);
    setExpanded(true);
    setLoading(false);
  };

  const collapseProducts = () => {
    fetchInitialData();
    setPage(0);
    setExpanded(false);
  };

  const ProductItem = ({ p }) => (
    <div className="col-xl-3 col-lg-4 col-md-6 col-6 mb-4">
      <div className="card h-100 border-0 shadow-sm">
        <Link to={`/product/${p.productId}`}>
          <img src={`${IMAGE_BASE_URL}/${p.image}`} className="img-fluid p-3" style={{height:200,objectFit:"contain"}} />
        </Link>
        <div className="card-body">
          <h6 className="fw-bold text-truncate">{p.productName}</h6>
          <p className="text-danger fw-bold mb-0">{(p.specialPrice || p.price).toLocaleString()} ₫</p>
        </div>
      </div>
    </div>
  );

  return (
    <div className="bg-light pb-5">

      {/* NEW PRODUCTS */}
      <section className="container pt-5">
        <h3 className="fw-bold mb-4">Sản phẩm mới</h3>
        <div className="row">{newProducts.map(p => <ProductItem key={p.productId} p={p} />)}</div>
      </section>

      {/* TOP SELLING */}
      <section className="container mt-5">
        <h3 className="fw-bold mb-4">Bán chạy nhất</h3>
        <div className="row">{topProducts.map(p => <ProductItem key={p.productId} p={p} />)}</div>
      </section>

      {/* ALL PRODUCTS SLIDER */}
      <section className="container mt-5">
        <h3 className="fw-bold mb-4">Dành cho bạn</h3>

        {!expanded ? (
          <div className="d-flex overflow-auto gap-3 pb-3">
            {allProducts.map(p => (
              <div style={{minWidth:220}} key={p.productId}>
                <ProductItem p={p} />
              </div>
            ))}
          </div>
        ) : (
          <div className="row">
            {allProducts.map(p => <ProductItem key={p.productId} p={p} />)}
          </div>
        )}

        <div className="text-center mt-4">
          {!expanded ? (
            <button className="btn btn-outline-primary px-5 rounded-pill" onClick={loadMoreProducts} disabled={loading}>
              {loading ? "ĐANG TẢI..." : "XEM TẤT CẢ"}
            </button>
          ) : (
            <button className="btn btn-outline-secondary px-5 rounded-pill" onClick={collapseProducts}>
              THU GỌN
            </button>
          )}
        </div>
      </section>
    </div>
  );
};

export default ProductNew;
