import React, { useState, useEffect } from 'react';
import bannerService from '../services/bannerService';

const BannerThree = () => {
  const [leftBanner, setLeftBanner] = useState(null);
  const [rightBanner, setRightBanner] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchBanners();
  }, []);

  const fetchBanners = async () => {
    try {
      // Lấy banner ID 7 và 8 từ bannerService
      const [banner7Data, banner8Data] = await Promise.all([
        bannerService.getBannerById(7),
        bannerService.getBannerById(8)
      ]);

      setLeftBanner(banner7Data);
      setRightBanner(banner8Data);
    } catch (err) {
      console.error('Lỗi lấy banners:', err);
      // Nếu ID 7,8 không tồn tại, lấy 2 banner đầu tiên
      try {
        const allBanners = await bannerService.getAllBanners();
        if (allBanners.length > 0) {
          setLeftBanner(allBanners[0]);
          if (allBanners.length > 1) {
            setRightBanner(allBanners[1]);
          }
        }
      } catch (fallbackErr) {
        console.error('Lỗi lấy banner fallback:', fallbackErr);
      }
    } finally {
      setLoading(false);
    }
  };

  const getImageUrl = (banner) => {
    if (!banner?.imageUrl) return 'https://via.placeholder.com/600x400/2d3748/ffffff?text=Banner+Image';
    return `http://localhost:8080/api/public/banners/image/${banner.imageUrl}`;
  };

  if (loading) {
    return (
      <section className="banner-three-section">
        <div className="container">
          <div className="row">
            <div className="col-md-6">
              <div className="placeholder-glow">
                <div className="placeholder rounded-3" style={{ height: '400px' }}></div>
              </div>
            </div>
            <div className="col-md-6">
              <div className="placeholder-glow">
                <div className="placeholder rounded-3" style={{ height: '400px' }}></div>
              </div>
            </div>
          </div>
        </div>
      </section>
    );
  }

  if (!leftBanner && !rightBanner) {
    return null;
  }

  return (
    <section className="banner-three-section">
      <div className="container">
        <div className="row g-4">
          {/* Banner trái - ID 7 */}
          {leftBanner && (
            <div className="col-md-6">
              <div className="banner-full-width">
                <img
                  src={getImageUrl(leftBanner)}
                  alt={leftBanner.title || 'Banner'}
                  className="img-fluid w-100 rounded-3"
                  style={{ 
                    height: '400px',
                    objectFit: 'cover',
                    objectPosition: 'center'
                  }}
                />
              </div>
            </div>
          )}

          {/* Banner phải - ID 8 */}
          {rightBanner && (
            <div className="col-md-6">
              <div className="banner-full-width">
                <img
                  src={getImageUrl(rightBanner)}
                  alt={rightBanner.title || 'Banner'}
                  className="img-fluid w-100 rounded-3"
                  style={{ 
                    height: '400px',
                    objectFit: 'cover',
                    objectPosition: 'center'
                  }}
                />
              </div>
            </div>
          )}
        </div>
      </div>
    </section>
  );
};

export default BannerThree;
