import api from "./api";

const productService = {
  async getAllProducts() {
    try {
      const { data } = await api.get("/public/products");
      return data;
    } catch (error) {
      console.error("❌ Lỗi khi lấy danh sách sản phẩm:", error);
      throw error;
    }
  },

  async getProductById(productId) {
    try {
      const { data } = await api.get(`/public/products/${productId}`);
      return data;
    } catch (error) {
      console.error("❌ Lỗi khi lấy sản phẩm:", error);
      throw error;
    }
  },

  async getProductsByCategory(categoryId) {
    try {
      const { data } = await api.get(`/public/categories/${categoryId}/products`);
      return data;
    } catch (error) {
      console.error("❌ Lỗi khi lấy sản phẩm theo danh mục:", error);
      throw error;
    }
  },

  async searchProductsByKeyword(keyword) {
    try {
      const { data } = await api.get(`/public/products/keyword/${keyword}`);
      return data;
    } catch (error) {
      console.error("❌ Lỗi khi tìm kiếm sản phẩm:", error);
      throw error;
    }
  },
};

export default productService;
