import React, { useState, useEffect, useCallback } from "react";
import { useNavigate } from "react-router-dom";
import categoryService from "../../../services/categoryService";
import Swal from "sweetalert2";

const CategoryListAD = () => {
  const [categories, setCategories] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showModal, setShowModal] = useState(false);
  const [isEdit, setIsEdit] = useState(false);
  const [formData, setFormData] = useState({ categoryId: "", categoryName: "" });
  const navigate = useNavigate();

  const [pageConfig, setPageConfig] = useState({
    pageNumber: 0,
    pageSize: 10,
    totalPages: 0,
    totalElements: 0,
  });

  // Kiểm tra quyền Admin trước khi load data
  const checkAdminAuth = useCallback(() => {
    const token = localStorage.getItem("token");
    const role = localStorage.getItem("role"); // Giả sử mày lưu role khi login

    if (!token || role !== "ADMIN") {
      Swal.fire("Truy cập bị chặn", "Mày cần quyền ADMIN để vào đây!", "error");
      navigate("/login");
      return false;
    }
    return true;
  }, [navigate]);

  const fetchCategories = useCallback(async (page = 0) => {
    try {
      setLoading(true);
      const data = await categoryService.getAllCategories(page, pageConfig.pageSize);
      
      // Xử lý đúng cấu trúc CategoryResponse
      setCategories(data.content || []);
      setPageConfig({
        pageNumber: data.pageNumber || 0,
        pageSize: data.pageSize || 10,
        totalPages: data.totalPages || 0,
        totalElements: data.totalElements || 0,
      });
    } catch (err) {
      console.error("Lỗi:", err);
    } finally {
      setLoading(false);
    }
  }, [pageConfig.pageSize]);

  useEffect(() => {
    if (checkAdminAuth()) {
      fetchCategories(0);
    }
  }, [fetchCategories, checkAdminAuth]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (formData.categoryName.trim().length < 5) {
      return Swal.fire("Lỗi", "Tên danh mục ít nhất 5 ký tự", "warning");
    }

    try {
      const payload = { categoryName: formData.categoryName };
      if (isEdit) {
        await categoryService.updateCategory(formData.categoryId, payload);
      } else {
        await categoryService.createCategory(payload);
      }
      setShowModal(false);
      Swal.fire("Thành công", "Đã lưu thay đổi", "success");
      fetchCategories(pageConfig.pageNumber);
    } catch (err) {
      Swal.fire("Lỗi", "Thao tác thất bại. Check Token/Role!", "error");
    }
  };

  const handleDelete = async (id) => {
    const result = await Swal.fire({
      title: "Xóa danh mục?",
      icon: "warning",
      showCancelButton: true,
      confirmButtonText: "Xóa",
      confirmButtonColor: "#d33"
    });

    if (result.isConfirmed) {
      try {
        await categoryService.deleteCategory(id);
        Swal.fire("Đã xóa!", "", "success");
        fetchCategories(0);
      } catch (err) {
        Swal.fire("Lỗi", "Không thể xóa", "error");
      }
    }
  };

  return (
    <div className="p-4">
      <div className="card shadow-sm border-0">
        <div className="card-header bg-white d-flex justify-content-between align-items-center py-3">
          <h5 className="mb-0 fw-bold">QUẢN LÝ DANH MỤC (ADMIN)</h5>
          <button className="btn btn-primary btn-sm" onClick={() => { setIsEdit(false); setFormData({categoryId: "", categoryName: ""}); setShowModal(true); }}>
            + Thêm mới
          </button>
        </div>
        <div className="table-responsive">
          <table className="table table-hover align-middle mb-0">
            <thead className="table-light">
              <tr>
                <th width="10%">ID</th>
                <th>Tên danh mục</th>
                <th width="20%" className="text-center">Thao tác</th>
              </tr>
            </thead>
            <tbody>
              {loading ? (
                <tr><td colSpan="3" className="text-center">Đang tải dữ liệu...</td></tr>
              ) : (
                categories.map((c) => (
                  <tr key={c.categoryId}>
                    <td>#{c.categoryId}</td>
                    <td className="fw-semibold">{c.categoryName}</td>
                    <td className="text-center">
                      <button className="btn btn-sm btn-info me-2" onClick={() => { setIsEdit(true); setFormData(c); setShowModal(true); }}>Sửa</button>
                      <button className="btn btn-sm btn-danger" onClick={() => handleDelete(c.categoryId)}>Xóa</button>
                    </td>
                  </tr>
                ))
              )}
            </tbody>
          </table>
        </div>
        
        {/* Pagination UI */}
        <div className="card-footer bg-white d-flex justify-content-end">
          <nav>
            <ul className="pagination pagination-sm mb-0">
              <li className={`page-item ${pageConfig.pageNumber === 0 ? 'disabled' : ''}`}>
                <button className="page-link" onClick={() => fetchCategories(pageConfig.pageNumber - 1)}>Trước</button>
              </li>
              {[...Array(pageConfig.totalPages)].map((_, i) => (
                <li key={i} className={`page-item ${pageConfig.pageNumber === i ? 'active' : ''}`}>
                  <button className="page-link" onClick={() => fetchCategories(i)}>{i + 1}</button>
                </li>
              ))}
              <li className={`page-item ${pageConfig.pageNumber >= pageConfig.totalPages - 1 ? 'disabled' : ''}`}>
                <button className="page-link" onClick={() => fetchCategories(pageConfig.pageNumber + 1)}>Sau</button>
              </li>
            </ul>
          </nav>
        </div>
      </div>

      {/* Modal */}
      {showModal && (
        <div className="modal d-block" style={{backgroundColor: "rgba(0,0,0,0.5)"}}>
          <div className="modal-dialog modal-dialog-centered">
            <div className="modal-content">
              <form onSubmit={handleSubmit}>
                <div className="modal-header">
                  <h5 className="fw-bold">{isEdit ? "Cập nhật" : "Thêm mới"}</h5>
                  <button type="button" className="btn-close" onClick={() => setShowModal(false)}></button>
                </div>
                <div className="modal-body p-4">
                  <label className="form-label small fw-bold">Tên danh mục</label>
                  <input type="text" className="form-control" value={formData.categoryName} onChange={e => setFormData({...formData, categoryName: e.target.value})} required />
                </div>
                <div className="modal-footer">
                  <button type="submit" className="btn btn-primary w-100">Xác nhận</button>
                </div>
              </form>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default CategoryListAD;
