import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import authService from "../../services/authService";
import userService from "../../services/userService";

const Login = () => {
  const navigate = useNavigate();
  const [form, setForm] = useState({
    email: "",
    password: "",
  });

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

const handleSubmit = async (e) => {
  e.preventDefault();
  try {
    console.log("üîÑ Attempting login for:", form.email);

    const res = await authService.login(form);
    console.log("‚úÖ LOGIN SUCCESS:", res.data);

    const loginData = res.data;

    // ‚úÖ T·∫†O USER OBJECT T·ª™ RESPONSE
    const userToStore = {
      userId: loginData.user_id || 8,
      user_id: loginData.user_id || 8,
      email: loginData.email || form.email,
      first_name: loginData.first_name || "Thuy",
      last_name: loginData.last_name || "Nguyen"
    };

    console.log("üíæ Saving user to localStorage:", userToStore);

    // ‚úÖ L∆ØU V√ÄO LOCALSTORAGE
    localStorage.setItem("user", JSON.stringify(userToStore));

    // ‚úÖ L∆ØU TOKEN
    if (loginData['jwt-token']) {
      localStorage.setItem("token", loginData['jwt-token']);
      console.log("üîê Token saved");
    }

    // ‚úÖ KI·ªÇM TRA
    console.log("üì¶ Final localStorage user:", JSON.parse(localStorage.getItem("user")));

    alert("‚úÖ ƒêƒÉng nh·∫≠p th√†nh c√¥ng!");
    window.location.href = "/";

  } catch (err) {
    console.error("‚ùå LOGIN ERROR DETAILS:", {
      message: err.message,
      response: err.response?.data,
      status: err.response?.status
    });

    // ‚úÖ HI·ªÇN TH·ªä L·ªñI CHI TI·∫æT
    const errorMessage = err.response?.data?.message || err.response?.data?.error || "ƒêƒÉng nh·∫≠p th·∫•t b·∫°i!";
    alert("‚ùå " + errorMessage);
  }
};
  return (
    <div className="card mx-auto" style={{ maxWidth: "380px", marginTop: "100px" }}>
      <div className="card-body">
        <h4 className="card-title mb-4">Sign in</h4>

        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <input
              name="email"
              className="form-control"
              placeholder="Email"
              type="email"
              value={form.email}
              onChange={handleChange}
              required
            />
          </div>

          <div className="form-group">
            <input
              name="password"
              className="form-control"
              placeholder="Password"
              type="password"
              value={form.password}
              onChange={handleChange}
              required
            />
          </div>

          <button type="submit" className="btn btn-primary btn-block">
            Login
          </button>
        </form>
      </div>
    </div>
  );
};

export default Login;
