import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import authService from "../../services/authService";

const Login = () => {
  const navigate = useNavigate();
  const [form, setForm] = useState({
    email: "",
    password: "",
  });

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await authService.login(form);
      console.log("‚úÖ LOGIN RESULT:", res.data);

      // ‚úÖ L∆∞u th√¥ng tin user ƒê√öNG C√ÅCH ƒë·ªÉ Header c√≥ th·ªÉ s·ª≠ d·ª•ng
      const userData = res.data;

      localStorage.setItem("user", JSON.stringify({
        userId: userData.user_id || userData.userId, // ‚úÖ Quan tr·ªçng: ph·∫£i c√≥ userId
        email: userData.email,
        first_name: userData.first_name, // ‚úÖ L∆∞u first_name n·∫øu c√≥
        last_name: userData.last_name,   // ‚úÖ L∆∞u last_name n·∫øu c√≥
        // C√≥ th·ªÉ th√™m c√°c field kh√°c n·∫øu API tr·∫£ v·ªÅ
      }));

      // ‚úÖ L∆∞u token n·∫øu c√≥
      if (userData.token) {
        localStorage.setItem("token", userData.token);
      }

      console.log("üíæ User saved to localStorage:", JSON.parse(localStorage.getItem("user")));

      alert("‚úÖ ƒêƒÉng nh·∫≠p th√†nh c√¥ng!");
      navigate("/");

      // ‚úÖ Reload page ƒë·ªÉ Header c·∫≠p nh·∫≠t ngay l·∫≠p t·ª©c
      window.location.reload();

    } catch (err) {
      console.error("‚ùå LOGIN ERROR:", err);
      alert("‚ùå ƒêƒÉng nh·∫≠p th·∫•t b·∫°i! Vui l√≤ng ki·ªÉm tra email v√† m·∫≠t kh·∫©u.");
    }
  };

  return (
    <div className="card mx-auto" style={{ maxWidth: "380px", marginTop: "100px" }}>
      <div className="card-body">
        <h4 className="card-title mb-4">Sign in</h4>

        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <input
              name="email"
              className="form-control"
              placeholder="Email"
              type="email"
              value={form.email}
              onChange={handleChange}
              required
            />
          </div>

          <div className="form-group">
            <input
              name="password"
              className="form-control"
              placeholder="Password"
              type="password"
              value={form.password}
              onChange={handleChange}
              required
            />
          </div>

          <button type="submit" className="btn btn-primary btn-block">
            Login
          </button>
        </form>
      </div>
    </div>
  );
};

export default Login;
