import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import authService from "../../services/authService";
import userService from "../../services/userService";

const Login = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    email: "",
    password: "",
    remember: true,
  });
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState("");
  const [messageType, setMessageType] = useState(""); // "success" or "error"

  const showMessage = (text, type) => {
    setMessage(text);
    setMessageType(type);
    setTimeout(() => {
      setMessage("");
      setMessageType("");
    }, 3000);
  };

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: type === "checkbox" ? checked : value,
    }));
  };

  const validate = () => {
    const { email, password } = formData;
    const errors = [];

    if (!email || !password) {
      errors.push("Vui l√≤ng nh·∫≠p ƒë·∫ßy ƒë·ªß th√¥ng tin.");
    }

    if (email && !/\S+@\S+\.\S+/.test(email)) {
      errors.push("Email kh√¥ng h·ª£p l·ªá.");
    }

    return errors.length > 0 ? errors : null;
  };

 // Trong component Login, s·ª≠a ph·∫ßn x·ª≠ l√Ω sau khi login th√†nh c√¥ng:

const handleSubmit = async (e) => {
  e.preventDefault();
  setLoading(true);

  try {
    console.log("üîê Attempting login with:", { email: formData.email });

    const res = await authService.login({
      email: formData.email,
      password: formData.password
    });

    console.log("‚úÖ LOGIN SUCCESS:", res.data);
    const loginData = res.data;

    // L·∫•y token t·ª´ response
    const token = loginData.token || loginData['jwt-token'] || loginData.jwt;

    if (!token) {
      throw new Error("Server kh√¥ng tr·∫£ v·ªÅ token");
    }

    // L∆∞u token v√†o localStorage
    localStorage.setItem("token", token);

    // ‚úÖ QUAN TR·ªåNG: L·∫•y user ID t·ª´ API sau khi login
    let userInfo = null;
    let realUserId = null;

    try {
      // Th·ª≠ l·∫•y user info b·∫±ng email
      userInfo = await userService.getUserByEmail(formData.email);
      console.log("‚úÖ User info from email lookup:", userInfo);
      realUserId = userInfo.user_id || userInfo.userId;
    } catch (emailError) {
      console.warn("‚ö†Ô∏è Could not get user by email:", emailError);
    }

    // ‚úÖ N·∫øu kh√¥ng l·∫•y ƒë∆∞·ª£c user ID, decode t·ª´ token
    if (!realUserId) {
      try {
        const payload = JSON.parse(atob(token.split('.')[1]));
        realUserId = payload.userId || payload.sub || payload.user_id;
        console.log("‚úÖ User ID from token:", realUserId);
      } catch (decodeError) {
        console.warn("‚ö†Ô∏è Could not decode token:", decodeError);
      }
    }

    // ‚úÖ N·∫øu v·∫´n kh√¥ng c√≥ user ID, t·∫°o temp (nh∆∞ng ∆∞u ti√™n t√¨m user ID th·∫≠t)
    if (!realUserId) {
      console.warn("‚ö†Ô∏è No real user ID found, need to find correct user ID");
      // Kh√¥ng t·∫°o temp ID n·ªØa, m√† b√°o l·ªói
      throw new Error("Kh√¥ng th·ªÉ x√°c ƒë·ªãnh user ID. Vui l√≤ng th·ª≠ l·∫°i.");
    }

    // ‚úÖ L∆ØU USER DATA V·ªöI USER ID TH·∫¨T
    const userToStore = {
      userId: realUserId, // ‚úÖ ƒê√¢y ph·∫£i l√† s·ªë (1, 2, 3,...) t·ª´ database
      email: formData.email,
      first_name: userInfo?.first_name || userInfo?.firstName || "",
      last_name: userInfo?.last_name || userInfo?.lastName || "",
      mobile_number: userInfo?.mobile_number || userInfo?.mobileNumber || ""
    };

    localStorage.setItem("user", JSON.stringify(userToStore));
    console.log("üíæ User data saved to localStorage:", userToStore);

    alert("‚úÖ ƒêƒÉng nh·∫≠p th√†nh c√¥ng!");

    setTimeout(() => {
      window.location.href = "/";
    }, 500);

  } catch (err) {
    console.error("‚ùå LOGIN ERROR:", err);
    localStorage.clear();
    alert("‚ùå ƒêƒÉng nh·∫≠p th·∫•t b·∫°i: " + (err.response?.data?.message || err.message));
  } finally {
    setLoading(false);
  }
};

  return (
    <div className="p-4">
      <div className="card mx-auto" style={{ maxWidth: "400px", marginTop: "50px" }}>
        <div className="card-body">
          <h4 className="card-title mb-4 text-center text-primary">Sign in</h4>

          {/* Message Alert */}
          {message && (
            <div 
              className={`alert ${messageType === "success" ? "alert-success" : "alert-danger"}`} 
              role="alert"
            >
              {messageType === "success" ? "‚úÖ " : "‚ùå "}
              {message}
            </div>
          )}

          <form onSubmit={handleSubmit}>
            <div className="form-group mb-3">
              <label className="form-label">Email</label>
              <input
                name="email"
                className="form-control"
                placeholder="Enter your email"
                type="email"
                value={formData.email}
                onChange={handleChange}
                required
                disabled={loading}
              />
            </div>

            <div className="form-group mb-3">
              <label className="form-label">Password</label>
              <input
                name="password"
                className="form-control"
                placeholder="Enter your password"
                type="password"
                value={formData.password}
                onChange={handleChange}
                required
                disabled={loading}
              />
            </div>

            <div className="form-group mb-3">
              <div className="form-check">
                <input
                  name="remember"
                  className="form-check-input"
                  type="checkbox"
                  checked={formData.remember}
                  onChange={handleChange}
                  disabled={loading}
                />
                <label className="form-check-label">
                  Remember me
                </label>
              </div>
            </div>

            <button 
              type="submit" 
              className="btn btn-primary w-100"
              disabled={loading}
            >
              {loading ? (
                <>
                  <span className="spinner-border spinner-border-sm me-2" role="status"></span>
                  ƒêang ƒëƒÉng nh·∫≠p...
                </>
              ) : (
                'Login'
              )}
            </button>
          </form>

          <div className="text-center mt-3">
            <a href="/forgot-password" className="text-decoration-none">
              Forgot password?
            </a>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Login;
