// services/cartService.js
import api from './api';

const cartService = {
  // Tạo giỏ hàng mới
  createCart: async (email) => {
    try {
      const response = await api.post(`/api/public/users/${email}/carts`);
      return response.data;
    } catch (error) {
      console.error('❌ Lỗi khi tạo giỏ hàng:', error);
      throw error;
    }
  },

  // Lấy giỏ hàng active
  getActiveCart: async (email) => {
    try {
      const response = await api.get(`/api/public/users/${email}/carts/active`);
      return response.data;
    } catch (error) {
      console.error('❌ Lỗi khi lấy giỏ hàng:', error);
      throw error;
    }
  },

  // Thêm sản phẩm vào giỏ hàng
  addToCart: async (cartId, productId, quantity) => {
    try {
      const response = await api.post(`/api/public/carts/${cartId}/products/${productId}/quantity/${quantity}`);
      return response.data;
    } catch (error) {
      console.error('❌ Lỗi khi thêm vào giỏ hàng:', error);
      throw error;
    }
  },

  // Cập nhật số lượng
  updateQuantity: async (cartId, productId, quantity) => {
    try {
      const response = await api.put(`/api/public/carts/${cartId}/products/${productId}/quantity/${quantity}`);
      return response.data;
    } catch (error) {
      console.error('❌ Lỗi khi cập nhật số lượng:', error);
      throw error;
    }
  },

  // Xóa sản phẩm
  removeFromCart: async (cartId, productId) => {
    try {
      const response = await api.delete(`/api/public/carts/${cartId}/products/${productId}`);
      return response.data;
    } catch (error) {
      console.error('❌ Lỗi khi xóa sản phẩm:', error);
      throw error;
    }
  },

  // Tạo đơn hàng
  createOrder: async (email, cartId, paymentMethod) => {
    try {
      const response = await api.post(`/api/public/users/${email}/carts/${cartId}/payments/${paymentMethod}/order`);
      return response.data;
    } catch (error) {
      console.error('❌ Lỗi khi tạo đơn hàng:', error);
      throw error;
    }
  },

  // Tính tổng tiền
  calculateTotal: (cartItems) => {
    if (!cartItems || cartItems.length === 0) return 0;
    return cartItems.reduce((total, item) => {
      const price = item.price || 0;
      const quantity = item.quantity || 1;
      return total + (price * quantity);
    }, 0);
  }
};

export default cartService;
