import React, { useState, useEffect, useCallback } from "react";
import { useNavigate } from "react-router-dom";
import categoryService from "../../../services/categoryService";
import Swal from "sweetalert2";

const CategoryListAD = () => {
  const [categories, setCategories] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showModal, setShowModal] = useState(false);
  const [isEdit, setIsEdit] = useState(false);
  const [formData, setFormData] = useState({ categoryId: "", categoryName: "" });
  const navigate = useNavigate();

  // ================= 1. KI·ªÇM TRA QUY·ªÄN (CONSOLE LOG) =================
  const checkAdminRole = useCallback(() => {
    const token = localStorage.getItem("token");
    const role = localStorage.getItem("role"); // Ho·∫∑c l·∫•y t·ª´ userInfo t√πy m√†y l∆∞u

    console.log("üõ°Ô∏è [AUTH] Token hi·ªán t·∫°i:", token ? "‚úÖ ƒê√£ c√≥" : "‚ùå TR·ªêNG");
    console.log("üõ°Ô∏è [AUTH] Role hi·ªán t·∫°i:", role);

    if (!token || role !== "ADMIN") {
      console.warn("üö´ [AUTH] T·ª´ ch·ªëi: M√†y kh√¥ng ph·∫£i ADMIN ho·∫∑c ch∆∞a ƒëƒÉng nh·∫≠p!");
      return false;
    }
    return true;
  }, []);

  // ================= 2. L·∫§Y D·ªÆ LI·ªÜU (GET) =================
  const fetchCategories = useCallback(async () => {
    try {
      setLoading(true);
      console.log("üì° [FETCH] ƒêang g·ªçi service l·∫•y danh m·ª•c...");
      const data = await categoryService.getAllCategories();
      
      console.log("üì• [FETCH] D·ªØ li·ªáu hi·ªÉn th·ªã:", data);
      setCategories(data);
    } catch (err) {
      console.error("‚ùå [FETCH] L·ªói load danh m·ª•c:", err);
    } finally {
      setLoading(false);
    }
  }, []);

  useEffect(() => {
    if (checkAdminRole()) {
      fetchCategories();
    } else {
      Swal.fire("L·ªói", "M√†y kh√¥ng c√≥ quy·ªÅn ADMIN ƒë·ªÉ v√†o ƒë√¢y!", "error");
      navigate("/login");
    }
  }, [fetchCategories, checkAdminRole, navigate]);

  // ================= 3. TH√äM / S·ª¨A (POST / PUT) =================
  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log("üì§ [SUBMIT] D·ªØ li·ªáu g·ª≠i ƒëi:", formData);

    try {
      if (isEdit) {
        await categoryService.updateCategory(formData.categoryId, { categoryName: formData.categoryName });
        Swal.fire("Th√†nh c√¥ng", "ƒê√£ c·∫≠p nh·∫≠t danh m·ª•c!", "success");
      } else {
        await categoryService.createCategory({ categoryName: formData.categoryName });
        Swal.fire("Th√†nh c√¥ng", "ƒê√£ th√™m danh m·ª•c m·ªõi!", "success");
      }
      setShowModal(false);
      fetchCategories();
    } catch (err) {
      console.error("‚ùå [SUBMIT] Chi ti·∫øt l·ªói Admin:", err.response?.data || err.message);
      const msg = err.response?.data?.message || "L·ªói quy·ªÅn ADMIN ho·∫∑c d·ªØ li·ªáu (T√™n > 5 k√Ω t·ª±)";
      Swal.fire("Th·∫•t b·∫°i", msg, "error");
    }
  };

  // ================= 4. X√ìA (DELETE) =================
  const handleDelete = async (id) => {
    const result = await Swal.fire({
      title: "X√≥a danh m·ª•c n√†y?",
      text: "C√°c s·∫£n ph·∫©m thu·ªôc danh m·ª•c n√†y s·∫Ω b·ªã ·∫£nh h∆∞·ªüng!",
      icon: "warning",
      showCancelButton: true,
      confirmButtonColor: "#d33",
      confirmButtonText: "X√≥a lu√¥n",
    });

    if (result.isConfirmed) {
      try {
        console.log(`üóëÔ∏è [DELETE] ƒêang y√™u c·∫ßu x√≥a ID: ${id}`);
        await categoryService.deleteCategory(id);
        Swal.fire("ƒê√£ x√≥a!", "Danh m·ª•c ƒë√£ bay m√†u.", "success");
        fetchCategories();
      } catch (err) {
        console.error("‚ùå [DELETE] L·ªói x√≥a:", err.response?.data || err.message);
        Swal.fire("L·ªói", "Kh√¥ng th·ªÉ x√≥a. Check l·∫°i quy·ªÅn Admin!", "error");
      }
    }
  };

  // M·ªü modal
  const openModal = (cat = null) => {
    if (cat) {
      setIsEdit(true);
      setFormData({ categoryId: cat.categoryId, categoryName: cat.categoryName });
    } else {
      setIsEdit(false);
      setFormData({ categoryId: "", categoryName: "" });
    }
    setShowModal(true);
  };

  return (
    <div className="container-fluid p-4">
      <div className="card shadow border-0">
        <div className="card-header bg-dark text-white d-flex justify-content-between align-items-center py-3">
          <h5 className="mb-0 fw-bold"><i className="bi bi-grid-3x3-gap me-2"></i>QU·∫¢N L√ù CATEGORY</h5>
          <button className="btn btn-success btn-sm px-4 fw-bold" onClick={() => openModal()}>
            + TH√äM M·ªöI
          </button>
        </div>

        <div className="table-responsive">
          <table className="table table-hover align-middle mb-0">
            <thead className="table-light">
              <tr>
                <th className="ps-4" width="10%">M√£ ID</th>
                <th width="65%">T√™n danh m·ª•c</th>
                <th className="text-center" width="25%">Thao t√°c</th>
              </tr>
            </thead>
            <tbody>
              {loading ? (
                <tr><td colSpan="3" className="text-center py-5">ƒêang x·ª≠ l√Ω d·ªØ li·ªáu...</td></tr>
              ) : categories.length > 0 ? (
                categories.map((c) => (
                  <tr key={c.categoryId}>
                    <td className="ps-4 text-secondary fw-bold">#{c.categoryId}</td>
                    <td className="fw-semibold text-dark">{c.categoryName}</td>
                    <td className="text-center">
                      <button className="btn btn-sm btn-outline-primary me-2 shadow-sm" onClick={() => openModal(c)}>
                        <i className="bi bi-pencil-square"></i> S·ª≠a
                      </button>
                      <button className="btn btn-sm btn-outline-danger shadow-sm" onClick={() => handleDelete(c.categoryId)}>
                        <i className="bi bi-trash"></i> X√≥a
                      </button>
                    </td>
                  </tr>
                ))
              ) : (
