import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import authService from "../../services/authService";

const Register = () => {
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);

  // 1. Khai b√°o state ƒë·ªÉ l∆∞u l·ªói t·ª´ Backend
  const [backendErrors, setBackendErrors] = useState({});

  const [form, setForm] = useState({
    first_name: "",
    last_name: "",
    email: "",
    mobile_number: "",
    password: "",
    confirmPassword: "",
    street: "Default Street",
    buildingName: "Building A",
    city: "Hanoi",
    state: "HN",
    country: "VN",
    pincode: "100000",
  });

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
    // Khi ng∆∞·ªùi d√πng g√µ v√†o, x√≥a l·ªói c·ªßa √¥ ƒë√≥ ƒëi
    if (backendErrors[e.target.name]) {
      setBackendErrors({ ...backendErrors, [e.target.name]: "" });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setBackendErrors({}); // Reset l·ªói c≈©

    if (form.password !== form.confirmPassword) {
      setBackendErrors({ confirmPassword: "M·∫≠t kh·∫©u nh·∫≠p l·∫°i kh√¥ng kh·ªõp!" });
      return;
    }

    setLoading(true);

    try {
      const payload = {
        firstName: form.first_name,
        lastName: form.last_name,
        mobileNumber: form.mobile_number,
        email: form.email,
        password: form.password,
        address: {
          street: form.street,
          buildingName: form.buildingName,
          city: form.city,
          state: form.state,
          country: form.country,
          pincode: form.pincode,
        },
        role: [{ roleId: 102, roleName: "USER" }],
      };

      await authService.register(payload);
      alert("üéâ ƒêƒÉng k√Ω th√†nh c√¥ng!");
      navigate("/login");

    } catch (error) {
      const errorData = error.response?.data;
      console.error("‚ùå Register Error Object:", errorData);

      if (typeof errorData === "object") {
        // 2. L∆∞u object l·ªói v√†o state (v√≠ d·ª•: {firstName: '...', lastName: '...'})
        // Ch√∫ √Ω: Map t√™n field t·ª´ Backend (firstName) sang t√™n field c·ªßa Form (first_name)
        setBackendErrors({
          first_name: errorData.firstName,
          last_name: errorData.lastName,
          email: errorData.email,
          mobile_number: errorData.mobileNumber,
          password: errorData.password
        });
      } else {
        alert("‚ùå L·ªói h·ªá th·ªëng: " + (errorData || "Vui l√≤ng th·ª≠ l·∫°i"));
      }
    } finally {
      setLoading(false);
    }
  };

  return (
    <section className="section-content padding-y bg-light">
      <div className="card mx-auto shadow-sm" style={{ maxWidth: "520px", marginTop: "40px" }}>
        <article className="card-body p-4">
          <header className="mb-4 text-center">
            <h4 className="card-title fw-bold">ƒêƒÇNG K√ù</h4>
          </header>

          <form onSubmit={handleSubmit}>
            <div className="row">
              <div className="col-md-6 mb-3">
                <label className="form-label small fw-bold">H·ªç</label>
                <input
                  name="first_name"
                  className={`form-control ${backendErrors.first_name ? 'is-invalid' : ''}`}
                  value={form.first_name}
                  onChange={handleChange}
                  required
                />
                {/* 3. Hi·ªÉn th·ªã th√¥ng b√°o l·ªói ngay d∆∞·ªõi Input */}
                {backendErrors.first_name && <div className="invalid-feedback">{backendErrors.first_name}</div>}
              </div>

              <div className="col-md-6 mb-3">
                <label className="form-label small fw-bold">T√™n</label>
                <input
                  name="last_name"
                  className={`form-control ${backendErrors.last_name ? 'is-invalid' : ''}`}
                  value={form.last_name}
                  onChange={handleChange}
                  required
                />
                {backendErrors.last_name && <div className="invalid-feedback">{backendErrors.last_name}</div>}
              </div>
            </div>

            <div className="mb-3">
              <label className="form-label small fw-bold">Email</label>
              <input
                name="email"
                type="email"
                className={`form-control ${backendErrors.email ? 'is-invalid' : ''}`}
                value={form.email}
                onChange={handleChange}
                required
              />
              {backendErrors.email && <div className="invalid-feedback">{backendErrors.email}</div>}
            </div>

            <div className="mb-3">
              <label className="form-label small fw-bold">S·ªë ƒëi·ªán tho·∫°i</label>
              <input
                name="mobile_number"
                className={`form-control ${backendErrors.mobile_number ? 'is-invalid' : ''}`}
                value={form.mobile_number}
                onChange={handleChange}
                required
              />
              {backendErrors.mobile_number && <div className="invalid-feedback">{backendErrors.mobile_number}</div>}
            </div>

            <div className="row mb-4">
              <div className="col-md-6">
                <label className="form-label small fw-bold">M·∫≠t kh·∫©u</label>
                <input
                  name="password"
                  type="password"
                  className={`form-control ${backendErrors.password ? 'is-invalid' : ''}`}
                  value={form.password}
                  onChange={handleChange}
                  required
                />
                {backendErrors.password && <div className="invalid-feedback">{backendErrors.password}</div>}
              </div>
              <div className="col-md-6">
                <label className="form-label small fw-bold">Nh·∫≠p l·∫°i m·∫≠t kh·∫©u</label>
                <input
                  name="confirmPassword"
                  type="password"
                  className={`form-control ${backendErrors.confirmPassword ? 'is-invalid' : ''}`}
                  value={form.confirmPassword}
                  onChange={handleChange}
                  required
                />
                {backendErrors.confirmPassword && <div className="invalid-feedback">{backendErrors.confirmPassword}</div>}
              </div>
            </div>

            <button type="submit" className="btn btn-primary w-100 py-2" disabled={loading}>
              {loading ? <span className="spinner-border spinner-border-sm"></span> : "ƒêƒÇNG K√ù"}
            </button>
          </form>
        </article>
      </div>
    </section>
  );
};

export default Register;
